INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (1, 'ABEL SALVADOR MESQUITA JUNIOR', 'Abel Mesquita Jr', 'DEM', 'RR', 'Sim', 'M', 'Roraima, verás que o filho teu não foge à luta! O povo brasileiro merece respeito! Por um Brasil com justiça, igualdade social e sem corrupção, por uma Roraima desacorrentada, para que possamos exercer o direito constitucional de ir e vir e por todas as famílias roraimenses, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (2, 'ADALBERTO CAVALCANTI RODRIGUES', 'Adalberto Cavalcanti', 'PTB', 'PE', 'Não', 'M', 'Sr. Presidente, em nome do meu querido Estado de Pernambuco, em nome dos 100 mil eleitores de quem eu recebi os votos, não poderia ser ingrato com a Presidente Dilma nem com o Presidente Lula. Então, mais do que justo: sou contra o impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (3, 'ADALBERTO SOUZA GALVÃO', 'Bebeto', 'PSB', 'BA', 'Não', 'M', 'Sr. Presidente, em momentos de dificuldades é que podemos afirmar o nosso caráter e a nossa trajetória. A minha trajetória reclama ter uma posição nesta sessão distinta da facilidade que muitos têm tido de encaminhar o voto sim. A minha trajetória reclama respeito à classe trabalhadora, respeito à democracia, respeito à valorização social do trabalho. É nessa circunstância, respeitando a democracia, respeitando a classe trabalhadora, que quero aqui encaminhar o voto não neste processo.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (4, 'ADELMO CARNEIRO LEAO', 'Adelmo Carneiro Leão', 'PT', 'MG', 'Não', 'M', 'Votar em impeachment só é possível com crime de responsabilidade. Não havendo crime de responsabilidade, é golpe. E golpe, nós não podemos votar por ele. Eu sou contra o golpe, contra o impeachment, em favor da democracia.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (5, 'ADELSON BARRETO DOS SANTOS', 'Adelson Barreto', 'PR', 'SE', 'SIm', 'M', 'Sr. Presidente, Srs. Deputados, Sras. Deputadas, povo brasileiro, em sintonia e harmonia com o povo de Aracaju, com o povo de Sergipe, meu estimado e amado Estado, em sintonia com a maioria do povo brasileiro, na esperança do extirpar da estagnação a que fora submetido este País, na esperança de que o País possa progredir, nós votamos sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (6, 'ADILTON DOMINGOS SACHETTI', 'Adilton Sachetti', 'PSB', 'MT', 'Sim', 'M', 'Pelo Brasil, pelo nosso Estado de Mato Grosso, pela minha querida Rondonópolis, pela minha família e, em especial, pela minha mulher, que neste momento luta pela vida, o voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (7, 'ADRIANO ELI CORRÊA', 'Eli Corrêa Filho', 'DEM', 'SP', 'Sim', 'M', 'Contra a corrupção deste País, pelo futuro das minhas filhas Sophia e Luna e de todos os brasileiros, por Guarulhos, por São Paulo e pelo Brasil, sim ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (8, 'AELTON JOSÉ DE FREITAS', 'Aelton Freitas', 'PR', 'MG', 'Não', 'M', 'Sr. Presidente, Srs. Deputados, eu quero terminar a justificativa da ausência da Deputada Clarissa Garotinho, que, por não ter tido autorização médica, não pôde vir do Rio até aqui. Justifico a ausência da Deputada. Sr. Presidente, todos são considerados inocentes até que alguém apresente uma prova de crime. A Presidente Dilma é uma mulher honesta. Eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (9, 'AFONSO ANTUNES DA MOTTA', 'Afonso Motta', 'PDT', 'RS', 'Não', 'M', 'Pelo legado de Getúlio, Jango e Brizola, pela democracia e o Estado Democrático de Direito, pelo Brasil, o PDT vota não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (10, 'AFONSO BANDEIRA FLORENCE', 'Afonso Florence', 'PT', 'BA', 'Não', 'M', 'Considerando que esse processo de impeachment está maculado na sua origem, porque foi acatado num processo de esforço do Deputado Eduardo Cunha para impedir a admissibilidade da investigação no Conselho de Ética, quando o PT se negou a votar; considerando que não há crime de responsabilidade e considerando que a pauta traz a derrota de uma agenda conservadora, que agora é vitoriosa, com a luta da reforma agrária, que está na rua, do Bolsa Família, do Minha Casa, Minha Vida, das contas das universidades, do povo negro, com a luta pelos direitos das mulheres, que estão na rua com o movimento estudantil e a juventude lutando pela legalidade ');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (11, 'AGUINALDO VELLOSO BORGES RIBEIRO', 'Aguinaldo Ribeiro', 'PP', 'PB', 'Sim', 'M', 'Sr. Presidente, hoje não é um dia de homenagem, não é um dia de celebração, é um dia de lamento. E todos nós, todas as forças políticas que aqui estão, devemos ter responsabilidade, a partir de amanhã, com o futuro do nosso País. Eu respeitei democraticamente, como Líder do meu partido, a maioria absoluta que fechou a questão. Eu sou líder da maioria, não sou líder de minoria. Por isso, sigo o meu partido pela admissibilidade desse processo.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (12, 'ALAN RICK MIRANDA', 'Alan Rick', 'PRB', 'AC', 'Sim', 'M', 'Sr. Presidente, quando fui eleito no meu Estado, eu jurei, acima de tudo, respeitar e honrar a vontade do meu eleitor. E é em respeito a essa vontade que, hoje, eu voto sim ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (13, 'ALCEU MOREIRA DA SILVA', 'Alceu Moreira', 'PMDB', 'RS', 'Sim', 'M', 'Pelo fim do populismo irresponsável e corrupto, pelo fim da vagabundização remunerada, pela valorização do trabalho, da produção, da pesquisa, tecnologia e inovação, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (14, 'PAULO PEREIRA DA SILVA', 'Paulo Pereira da Silva', 'SD', 'SP', 'Sim', 'M', 'Pelos trabalhadores do Brasil, pelos aposentados, contra os 10 milhões de pessoas que perderam emprego no Governo Dilma, do PT, pelo crescimento do Brasil, por mais emprego e contra a boquinha do PT, pelo fim da boquinha do PT e do PCdoB, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (15, 'BENEDITA SOUZA DA SILVA SAMPAIO', 'Benedita da Silva', 'PT', 'RJ', 'Não', 'F', 'Sr. Presidente, em nome da Constituição a que eu me comprometi em cumprir, em nome do respeito a ela, em nome dos pobres e necessitados deste País, em respeito aos 54 milhões de votos concedidos pelo povo brasileiro à Presidenta Dilma, em nome dos que ainda têm fome e sede de justiça, entendendo que o reino dividido não prospera, eu quero dizer não ao golpe.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (16, 'ALESSANDRO LUCCIOLA MOLON', 'Alessandro Molon', 'Rede', 'RJ', 'Não', 'M', 'Porque prometi defender a Constituição quando cheguei a esta Casa, porque não há crime de responsabilidade no parecer do Relator, porque respeito o voto do eleitor brasileiro e porque luto pela democracia no meu País, meu voto convicto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (17, 'ALEX CANZIANI SILVEIRA', 'Alex Canziani', 'PTB', 'PR', 'Sim', 'M', 'O futuro não é o mero encontro dos astros, o futuro se constrói, segundo Dom Ivo Lorscheiter. Sr. Presidente, nobres pares, voto com o Relator Jovair Arantes. Voto pela reconstrução do Brasil. Voto sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (18, 'ALEX SPINELLI MANENTE', 'Alex Manente', 'PPS', 'SP', 'Sim', 'M', 'Sr. Presidente, tenho a honra de iniciar a votação do Estado mais forte do nosso País. Em respeito à minha família, para honrar o futuro das minhas filhas, para ter fé no nosso País, trago aqui a representação de uma das maiores regiões do nosso País, que é o grande ABC paulista. Trago também a bandeira da minha cidade, São Bernardo do Campo. É pelo Brasil e por São Bernardo que eu voto sim, pelo impeachment da Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (19, 'ALEXANDRE AUGUSTUS SERFIOTIS', 'Alexandre Serfiotis', 'PMDB', 'RJ', 'Sim', 'M', 'Sr. Presidente, por Deus, em homenagem ao meu pai, Jorge Serfiotis, à minha família e aos meus amigos, por uma saúde digna, pela minha cidade de Porto Real, no Estado do Rio de Janeiro, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (20, 'ALEXANDRE BALDY DE SANT''ANNA BRAGA', 'Alexandre Baldy', 'PTN', 'GO', 'Sim', 'M', 'Sr. Presidente, neste momento histórico que vivo, agradeço a Deus por ter a oportunidade de ajudar o meu povo a limpar este País de mazelas, corrupção e malfeitos. Pela minha esposa, pelo meu filho e a minha filha, por toda a minha família, por toda esta Nação, pela cidade que me acolheu, Anápolis, por Goiás e pelo Brasil, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (21, 'ALEXANDRE LEITE DA SILVA', 'Alexandre Leite', 'DEM', 'SP', 'Sim', 'M', 'Sr. Presidente, eu saúdo o Brasil e os brasileiros. Eu saúdo o meu Estado de São Paulo e a minha querida Zona Sul com o voto sim, pelo impedimento da Presidente Dilma Vana Rousseff. Tchau, querida!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (22, 'ALEXANDRE VALLE CARDOSO', 'Alexandre Valle', 'PR', 'RJ', 'Sim', 'M', 'Sr. Presidente, pela minha família, pelos meus filhos, pelo povo do Estado do Rio de Janeiro e pela população de Itaguaí, ordeira e trabalhadora, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (23, 'ALFREDO PEREIRA DO NASCIMENTO', 'Alfredo Nascimento', 'PR', 'AM', 'Sim', 'M', 'Sr. Presidente, como sabe V.Exa., eu presido nacionalmente o Partido da República. Em uma reunião da executiva do nosso partido, realizada de forma democrática, majoritariamente o partido decidiu encaminhar o voto não. Em respeito ao meu partido, em respeito aos meus colegas Parlamentares, quero comunicar a esta Casa que renuncio ao meu mandato de Presidente Nacional do Partido da República, porque entendo o meu voto de forma diferente. O meu voto, Sr. Presidente, entendo que não pertence ao Governo, não pertence à Oposição, não pertence ao meu partido e sequer pertence a mim. O meu voto pertence ao povo do Amazonas, que me colocou na vida pública hámais de 30 anos, vida pública da qual eu me orgulho muito, e majoritariamente o povo do meu Estado do Amazonas vota pelo impedimento. Eu voto sim, Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (25, 'ALIEL MACHADO BARK', 'Aliel Machado', 'Rede', 'PR', 'Não', 'M', 'Acredito que a única forma de termos legitimidade a partir desta crise é uma nova eleição presidencial, que é isso que eu defendo. Neste momento, não posso aceitar nem Dilma, nem Temer, muito menos o Sr. Eduardo Cunha, que é acusado de ser ladrão. Por isso, o meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (26, 'ALTINEU CÔRTES FREITAS COUTINHO', 'Altineu Côrtes', 'PMDB', 'RJ', 'Sim', 'M', 'Respeitando o sentimento de cada brasileiro, pelos brasileiros mais humildes, que estão sofrendo, pelas nossas cidades de Itaboraí, de São Gonçalo e de Niterói, pelo desejo da maioria dos eleitores, pelo Brasil e para derrubar esse muro, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (27, 'ALUISIO GUIMARAES MENDES FILHO', 'Aluisio Mendes', 'PTN', 'MA', 'Não', 'M', 'Sr. Presidente, pelo direito e certeza de poder olhar no espelho e ficar em paz com a minha consciência, eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (28, 'ANA LUCIA LIPPAUS PERUGINI', 'Ana Perugini', 'PT', 'SP', 'Não', 'F', 'Deputadas e Deputados, trabalhadoras e trabalhadores que estão nos ouvindo, nossa juventude democrática, nossa democracia jovem não pode ser ferida de morte neste Parlamento, que deveria defendê-la. Pela democracia do nosso País, pela soberania do voto popular, não, Presidente, não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (29, 'ANDERSON FERREIRA RODRIGUES', 'Anderson Ferreira', 'PR', 'PE', 'Sim', 'M', 'Nada, nada justifica um voto que faça o Brasil sofrer. Hoje, nesta Casa, somos chamados para fazer parte da história. Hoje lembrei fortemente do meu pai, que me ensinou os valores da vida, os valores éticos e a política feita em compromisso com o povo. Por Jaboatão de Guararapes, por Pernambuco, pela família brasileira, pela minha esposa e pelos meus filhos, digo sim ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (30, 'ANDRÉ CARLOS ALVES DE PAULA FILHO', 'André de Paula', 'PSD', 'PE', 'Sim', 'M', 'Sr. Presidente, pelos pernambucanos e, de forma especial, pelos 100.785 cidadãos que me honraram com o seu voto para representá-los nesta Casa, fazendo aqui a merecida homenagem a três grandes Parlamentares pernambucanos, Raul Jungmann, Cadoca e Fernando Monteiro, que, se estivem na condição de titular, votariam como eu vou votar, pela ética na política, pela decência, por Pernambuco e pelo Brasil, sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (31, 'ANDRE DOS SANTOS ABDON', 'André Abdon', 'PP', 'AP', 'Sim', 'M', 'Sr. Presidente, o caráter e o valor de um homem não se faz com ouro, nem prata, se faz pela sua posição. Pelo Brasil, pelo meu Estado do Amapá, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (32, 'ANDRE LUIS CARVALHO RIBEIRO', 'André Fufuca', 'PP', 'MA', 'Sim', 'M', 'Em nome da unidade partidária do Partido Progressista, dos milhares de pessoas que foram enganadas pela Refinaria Premium, dos milhares de pessoas que choraram a morte dos seus entes queridos na BR-75, em nome desse Estado que carrego nas costas e no coração, olhando para você e para o meu querido Alto Alegre, digo ao povo maranhense e ao povo do Brasil que voto sim, a favor do impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (33, 'ANDRÉ LUIS DANTAS FERREIRA', 'André Moura', 'PSC', 'SE', 'Sim', 'M', 'Sr. Presidente, inicialmente, eu gostaria de registrar o quão orgulhoso eu sou em liderar uma bancada tão valorosa como a do PSC. Eu quero fazer o registro de um Parlamentar que orgulha muito este Parlamento e o nosso partido e que esteve conosco até a semana passada, mas, na condição de suplente, não está aqui hoje. É o Deputado Marcondes Gadelha, que é um Parlamentar exemplar para nós. Sr. Presidente, quero agradecer a toda a nossa bancada e dizer que nenhum povo é realmente grande, senão pela liberdade que tem ou conquista. Neste momento histórico, nós somos o povo brasileiro, nós somos a Pátria. Pelo Brasil, pelo meu amado Estado de Sergipe, de um grande homem, mestre da Filosofia e do Direito, Tobias Barreto; em nome da família e dos meus filhos, Yandra e Yago, eu voto sim, Sr. Presidente, pelo impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (34, 'ANDRES NAVARRO SANCHEZ', 'Andres Sanchez', 'PT', 'SP', 'Não', 'M', 'Sr. Presidente, este é o meu primeiro mandato. Meu filhos nunca quiseram que eu entrasse na política, e a maioria dos corintianos também não; mas, infelizmente, entrei. Que decepção! Meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (35, 'ANÍBAL FERREIRA GOMES', 'Aníbal Gomes', 'PMDB', 'CE', 'Ausente', 'M', NULL);
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (36, 'ANTONIO CARLOS MARTINS DE BULHÕES', 'Antonio Bulhões', 'PRB', 'SP', 'Sim', 'M', 'Sr. Presidente, eu jamais aceitaria fazer parte de um golpe. Com a consciência de que este processo seguiu rigorosamente todos os princípios constitucionais, eu voto sim. Que Deus abençoe o Brasil!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (37, 'ANTÔNIO CARLOS VALADARES FILHO', 'Valadares Filho', 'PSB', 'SE', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, pensando no melhor para o povo brasileiro, respeitando a nossa Constituição, seguindo a orientação do meu partido, pelos aracajuanos, pelo meu querido povo de Sergipe e pelo povo do Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (38, 'ANTONIO DA CRUZ FILGUEIRA JUNIOR', 'Junior Marreca', 'PEN', 'MA', 'Não', 'M', 'Eu gostaria de dizer ao nobre Deputado Beto Mansur que nós aqui não estamos fazendo democracia. Nós estaríamos fazendo democracia se déssemos àqueles que estão nas ruas a oportunidade de ir às urnas, votar e escolher o seu Presidente democraticamente. Aí, sim, estaríamos fazendo democracia. Como disse João Castelo, para não perder a minha dignidade nem minha coerência, voto não ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (39, 'ANTONIO DUARTE NOGUEIRA JÚNIOR', 'Duarte Nogueira', 'PSDB', 'SP', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Parlamentares brasileiros, pelo reencontro deste País maravilhoso com a sua esperança, porque não aguenta mais tanta afronta à Constituição, mentiras, impunidade e corrupção; pela minha família, meus filhos e meus pais, que me ensinaram valores e princípios; pelos paulistas, em especial os da minha terra natal; e pelos brasileiros, eu voto sim, pelo impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (40, 'ANTONIO FLORIANO PEREIRA PESARO', 'Floriano Pesaro', 'PSDB', 'SP', 'Sim', 'M', 'Sr. Presidente, em respeito aos meus eleitores, em respeito ao povo paulista, por um País mais justo, humano, solidário e ético, sob a proteção do Grande Arquiteto do Universo, eu, Floriano Pesaro, e meu suplente, Mendes Thame, votamos sim, pelo impedimento da Presidente Dilma.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (41, 'ANTONIO GOULART DOS REIS', 'Goulart', 'PSD', 'SP', 'Sim', 'M', 'Pela liberdade, igualdade e fraternidade; pelos meus eleitores de São Paulo,especialmente os da Zona Sul de São Paulo; pelos meus filhos, Fabinho e Rodrigo Goulart; pelo Deputado Estadual Jorge Caruso; por Vargem Bonita, Grajaú, Parelheiros, Capela do Socorro e Santo Amaro, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (42, 'ANTONIO JACOME DE LIMA JUNIOR', 'Antônio Jácome', 'PTN', 'RN', 'Sim', 'M', 'Sr. Presidente, nunca foi tão atual a palavra bíblica que diz: E se o meu povo, que se chama pelo meu nome, se humilhar, e orar, e buscar a minha face e se converter dos seus maus caminhos, então eu ouvirei dos céus, e perdoarei os seus pecados, e sararei a sua terra. Por uma nova ordem política, pelo fim da corrupção, pelos meus irmãos brasileiros, pelos meus irmãos norte-rio-grandenses, em homenagem à memória do meu pai e da minha saudosa irmã Ozanide, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (43, 'ANTONIO JOSÉ IMBASSAHY DA SILVA', 'Antonio Imbassahy', 'PSDB', 'BA', 'Sim', 'M', 'Sr. Presidente, pedindo a Deus que ilumine o Brasil, eu vim da Bahia para dizer sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (44, 'LEONARDO CUNHA DE BRITO', 'Leo de Brito', 'PT', 'AC', 'Não', 'M', 'Em respeito à democracia; em respeito à soberania do voto de 54 milhões de brasileiros; em respeito à Constituição; na luta por um Brasil melhor, sem recuar, sem cair e sem temer, meu voto é não ao golpe, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (45, 'ANTONIO LUIZ PARANHOS RIBEIRO LEITE DE BRITO', 'Antonio Brito', 'PSD', 'BA', 'Não', 'M', 'Sr. Presidente, meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (46, 'ANTÔNIO PINHEIRO JÚNIOR', 'Toninho Pinheiro', 'PP', 'MG', 'Sim', 'M', 'Com muita humildade e respeito, pela minha amada Ibirité, por toda Minas Gerais e pelo Brasil, nós pedimos, punição exemplar contra aqueles políticos que se juntaram a alguns empreiteiros milionários que estão roubando o Brasil há mais de 20 anos, dos mais diversos partidos e Governos, conforme se confirma na Lava Jato. Eu quero pedir a Deus sabedoria. Não queremos enganar a população e precisamos encontrar um caminho para que ninguém nunca mais roube no Brasil e nunca falte recurso para a saúde. Eu voto sim pelo impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (47, 'ANTONIO ROBERTO RODRIGUES GÓES DA SILVA', 'Roberto Góes', 'PDT', 'AP', 'Não', 'M', 'Pelo Programa Minha Casa, Minha Vida, pelo PRONATEC, pelo desenvolvimento do Brasil, eu voto não, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (48, 'ANTONIO SERGIO ALVES VIDIGAL', 'Sérgio Vidigal', 'PDT', 'ES', 'Sim', 'M', 'Pelo Estado do Espírito Santo, que me deu a oportunidade de ser o mais votado do Estado, com mais de 161 mil votos, especialmente o Município da Serra, eu quero aqui dizer ao meu partido, o PDT, que fechou questão contra o impeachment, que estou aqui no papel de julgador. Cabe ao julgador ser imparcial. Pela liberdade inviolável da consciência, pela democracia e contra a corrupção, quero dizer que, em nome da população capixaba, e peço desculpa ao PDT, vou ficar com o Estado do Espírito Santo. Voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (49, 'ANTONIO WANDSCHEER', 'Toninho Wandscheer', 'PROS', 'PR', 'Sim', 'M', 'Em memória do meu pai, Paulo Wandscheer, que, tenho certeza, estaria mandando — ele nem pediria; ele mandaria — eu votar pela minha cidade, Fazenda Rio Grande, pelo meu Estado do Paraná, pela minha família e pelo meu Brasil querido, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (50, 'ARLINDO CHIGNALIA JUNIOR', 'Arlindo Chinaglia', 'PT', 'SP', 'Não', 'M', 'Sr. Presidente e demais pares, o caminho mais curto para garantir a vontade popular e representar o povo brasileiro é respeitar o resultado das eleições. A segurança deste processo égarantida pela Constituição. Então, em defesa da democracia, em defesa da Constituição, em defesa de homens e mulheres que estão nas ruas hoje e que estiveram sempre nas ruas em defesa da democracia, o meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (51, 'ARNALDO CALIL PEREIRA JARDIM', 'Arnaldo Jardim', 'PPS', 'SP', 'Sim', 'M', 'Sr. Presidente, em homenagem ao PPS, que neste processo todo foi altivo, firme e decidido; em homenagem ao grande brasileiro, Presidente nacional, Deputado Roberto Freire; em homenagem ao setor ativo, inovador e gerador de renda, que é o setor agropecuário; epara que venha um governo de reconstrução nacional e que o Brasil vença hoje, voto sim, Sr. Presidente!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (52, 'GEOVANIA DE SA RODRIGUES', 'Geovania de Sá', 'PSDB', 'SC', 'Sim', 'F', 'Sr. Presidente, pela honra da minha família, pela minha cidade, Criciúma, por Santa Catarina e pela libertação do povo brasileiro, eu digo sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (53, 'ARNALDO FARIA DE SÁ', 'Arnaldo Faria de Sá', 'PTB', 'SP', 'Sim', 'M', 'Pela segunda vez, eu vou votar pelo impeachment e, sem dúvida nenhuma, acompanhando aqueles aposentados, pensionistas e idosos que querem que eu vote dessa maneira. Pela recuperação da economia brasileira, pelo combate à recessão e pela garantia de emprego para 10 milhões de trabalhadores desempregados, meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (54, 'ARNALDO JORDY FIGUEIREDO', 'Arnaldo Jordy', 'PPS', 'PA', 'Sim', 'M', 'Sr. Presidente, pelos trabalhadores brasileiros, pelo povo do Pará, pelos meus filhos, pela Constituição brasileira, contra Dilma e contra Cunha, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (55, 'ARTHUR CÉSAR PEREIRA DE LIRA', 'Arthur Lira', 'PP', 'AL', 'Sim', 'M', 'Sr. Presidente, tenho o prazer de abrir a votação do último Estado, o Estado de Alagoas. Antes do meu voto, queria parabenizar ao belíssimo trabalho do Relator, meu amigo, Deputado Jovair Arantes. Não acredito que o resultado de hoje, já consagrado por esta Casa, vá permitir que a Casa não tenha diálogo para encontrar um caminho para o desenvolvimento do Brasil. Em nome desse diálogo, em nome do desenvolvimento, chego a este meu segundo mandato com a certeza de que o desenvolvimento e a mudança da vida das pessoas é o grande trabalho deste Congresso. O Brasil está parado. E nós temos que encontrar essa saída. Em nome dessa saída, do progresso dos alagoanos, dos meus filhos, de quem vive em Alagoas, eu, encaminhando junto com meu partido, que fechou questão a favor do impeachment, dou meu voto sim ao prosseguimento.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (56, 'AROLDE DE OLIVEIRA', 'Arolde de Oliveira', 'PSC', 'RJ', 'Sim', 'M', 'Sr. Presidente, com esperança, com amor e com muita fé na restauração do Brasil, eu voto como o povo quer que eu vote, como o povo do Rio de Janeiro, que eu amo, quer eu vote e como minha família me orientou a votar. Eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (57, 'ARTHUR DE OLIVEIRA MAIA DA SILVA', 'Arthur Oliveira Maia', 'PPS', 'BA', 'Sim', 'M', 'Sr. Presidente, eu voto com a maioria do povo brasileiro. Eu voto, Sr. Presidente, na esperança de construirmos um País em que caiba a grandeza da felicidade, do futuro de todos os brasileiros. Pela Bahia e pelo Brasil! Meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (58, 'ARTHUR VIRGILIO DO CARMO RIBEIRO BISNETO', 'Arthur Virgílio Bisneto', 'PSDB', 'AM', 'Sim', 'M', 'Sr. Presidente, pela libertação do povo brasileiro, pela responsabilidade que tenho com o meu mandato, pelo Estado do Amazonas, por você, Manaus, minha razão e meu amor, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (59, 'ASSIS MIGUEL DO COUTO', 'Assis do Couto', 'PDT', 'PR', 'Não', 'M', 'Pela democracia, em respeito à Constituição, que juramos cumprir nesta Casa, pela cidadania dos mais pobres deste País, o meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (60, 'ÁTILA FREITAS LIRA', 'Átila Lira', 'PSB', 'PI', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Parlamentares, este éum momento, creio, difícil para todos nós. A minha decisão é pela renovação de esperanças para o nosso País, para todos nós. Pelo meu Estado do Piauí, pelo meu País, não vamos desistir do Brasil, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (61, 'ÁTILA SIDNEY LINS ALBUQUERQUE', 'Átila Lins', 'PSD', 'AM', 'Sim', 'M', 'Sr. Presidente, pela minha família, pelo povo do meu Estado, o Amazonas, pelo Brasil, para um Brasil melhor e para me reencontrar com a história, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (62, 'EVAIR VIEIRA DE MELO', 'Evair de Melo', 'PV', 'ES', 'Sim', 'M', 'Sras. e Srs. Deputados, os capixabas nunca legitimaram este Governo. Este Governo, para aumentar a maldade, abandonou os capixabas, nossas rodovias, portos e aeroportos. Então, por tudo isso, Srs. Deputados, e pela minha família, em especial, pelo meu filho Arthur e pela minha filha Sarah, pela agricultura e pelos agricultores do meu Espírito Santo, pelo cooperativismo, pela indústria abandonada por este Governo, pela ciência e pela tecnologia, pela maioria esmagadora dos capixabas, Espírito Santo, o meu voto é sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (63, 'AUGUSTO RODRIGUES COUTINHO DE MELO', 'Augusto Coutinho', 'SD', 'PE', 'Sim', 'M', 'Sr. Presidente, com toda a minha convicção, pelo Deputado Felipe Carreras, pela minha família, pelo meu Pernambuco que tanto amo e para que o maior símbolo do Brasil volte a ser a bandeira verde-amarela, eu voto sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (64, 'AUGUSTO SILVEIRA DE CARVALHO', 'Augusto Carvalho', 'SD', 'DF', 'Sim', 'M', 'Sr. Presidente, em respeito à Constituição Brasileira que ajudei a escrever em 1988, em respeito à opinião do povo do Distrito Federal e do País, voto sim, contra a corrupção, contra a dilapidação das empresas estatais e dos fundos de pensão e a favor de um novo tempo.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (65, 'AUREO LIDIO MOREIRA RIBEIRO', 'Aureo', 'SD', 'RJ', 'Sim', 'M', 'Feliz a nação cujo Deus é Senhor! Sr. Presidente, eu acredito nisso. Como Parlamentar do Estado do Rio de Janeiro, representando aqui não só o Estado, mas a minha cidade de Duque de Caxias, com a responsabilidade de chegar a casa e olhar meus filhos e a minha família, eu voto sim. Sr. Presidente, quero, ao olhar para os meus filhos, Gabriel e Alice, construir um futuro melhor e encher de esperança este Brasil. Eu voto sim. Fora, Dilma! Fora PT!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (66, 'BENEDITO ROBERTO ALVES FERREIRA', 'Roberto Alves', 'PRB', 'SP', 'Sim', 'M', 'Sr. Presidente, o povo brasileiro e o povo paulista clamam por justiça. Pela minha família, pela minha esposa, pelos meus filhos, pelo meu neto Gabriel, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (68, 'BENITO DA GAMA SANTOS', 'Benito Gama', 'PTB', 'BA', 'Sim', 'M', 'Sr. Presidente, como representante do povo da Bahia e pelo Brasil, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (69, 'BENJAMIN GOMES MARANHÃO NETO', 'Benjamin Maranhão', 'SD', 'PB', 'Sim', 'M', 'O meu voto está fundamentado no relatório que foi aprovado na Comissão Especial, o relatório que aponta crime de responsabilidade da Presidente Dilma Rousseff. Ela feriu o art. 85 da Constituição. Ela atentou contra a Constituição e contra a Lei Orçamentária. Ela editou créditos, sem autorização, de 90 bilhões de reais. Isso é crime! Ela fez empréstimos em bancos públicos, o que é proibido pela Lei de Responsabilidade Fiscal. Isso também é crime de responsabilidade e improbidade administrativa. Ela participou da compra fraudulenta de Pasadena. Ela é ladra! O dinheiro de Pasadena foi parar na conta de João Santana! Ela responderá por esses atos! Hoje o julgamento é político — do crime de responsabilidade —, mas depois ela vai responder criminalmente. Ela vai responder, sim, à Justiça. E pela Paraíba, pela tradição de luta e coragem do nosso povo, pelo meu partido, o Solidariedade, que lutou incansavelmente até este dia de hoje, eu vou votar sim, pela grandeza do nosso povo e pelo Brasil.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (70, 'BONIFÁCIO JOSÉ TAMM DE ANDRADA', 'Bonifácio de Andrada', 'PSDB', 'MG', 'Sim', 'M', 'Sr. Presidente, pela democracia, pelo Brasil, por Minas Gerais, em busca de melhores dias, votamos sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (71, 'BRUNA DIAS FURLAN', 'Bruna Furlan', 'PSDB', 'SP', 'Sim', 'F', 'Saquearam a nossa República, mas hoje resgataremos a esperança do povo brasileiro. Pela minha querida cidade Barueri, pela minha família, o meu voto é sim. Sim ao processo de impeachment da Presidente Dilma.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (72, 'BRUNIELE FERREIRA GOMES', 'Brunny', 'PR', 'MG', 'Não', 'F', 'Sr. Presidente, votar sim pelo impeachment é muito mais fácil. Mas eu nunca corri atrás de alguma coisa que fosse fácil na minha vida. Por isso eu voto contra. Eu sou PR e não sou covarde.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (73, 'BRUNO CAVALCANTI DE ARAÚJO', 'Bruno Araújo', 'PSDB', 'PE', 'Sim', 'M', 'Sr. Presidente, quanta honra o destino me reservou de poder da minha voz sair o grito de esperança de milhões de brasileiros. Senhoras e senhores, Pernambuco nunca faltou ao Brasil. Carrego comigo nossas histórias de luta pela liberdade e pela democracia. Por isso, eu digo ao Brasil sim pelo futuro!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (74, 'BRUNO COVAS LOPES', 'Bruno Covas', 'PSDB', 'SP', 'Sim', 'M', 'Sr. Presidente, eu aprendi que, diante das adversidades, só há três atitudes possíveis: enfrentar, combater e vencer. E nós vamos vencer esta. Eu venho aqui hoje dar voz a todos os paulistas que, anônimos, foram para as ruas dizer basta. Eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (75, 'CAIO NARCIO RODRIGUES DA SILVEIRA', 'Caio Narcio', 'PSDB', 'MG', 'Sim', 'M', 'Por um Brasil onde meu pai e meu avô diziam que decência e honestidade não eram possibilidade, eram obrigação; por um Brasil onde os brasileiros tenham decência e honestidade (pausa); por Minas, pelo Brasil, pelos jovens que estão lá fora, sim! Brasil, verás que um filho teu não foge à luta!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (76, 'CARLOS ALBERTO DE SOUSA ROSADO SEGUNDO', 'Beto Rosado', 'PP', 'RN', 'Sim', 'M', 'Agradeço a todo o Partido Progressista, que entendeu o sentimento das ruas e de toda a sua bancada e votou majoritariamente, fez questão de fechar o voto em favor do impeachment. Quero dizer que nós agora temos a oportunidade de sonhar, a oportunidade de ter esperança em um futuro melhor. Quero saudar minha cidade, Mossoró; todo o povo potiguar; minha família; meu pai, Betinho Rosado, que foi Parlamentar por cinco mandatos aqui nesta Casa. O meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (77, 'CARLOS ALBERTO ROLIM ZARATTINI', 'Carlos Zarattini', 'PT', 'SP', 'Não', 'M', 'Contra o golpe, em defesa da democracia, em defesa dos direitos dos trabalhadores, em defesa do direito dos aposentados, em defesa do petróleo do pré-sal, em homenagem à juventude paulista, que ocupou dezenas de escolas, e aos trabalhadores que estão na rua em defesa da democracia, meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (78, 'CARLOS AUGUSTO ANDRADE SILVA', 'Carlos Andrade', 'PHS', 'RR', 'Sim', 'M', 'Sr. Presidente, esta não é uma história de ricos contra pobres nem da direita contra a esquerda, mas é da Nação contra a corrupção. Eu voto sim, Sr. Presidente!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (79, 'CARLOS CESAR CORREIA DE MESSIAS', 'César Messias', 'PSB', 'AC', 'Não', 'M', 'Olhem aonde nós chegamos! Tira Dilma, entra Temer! Tira Temer, entra Cunha! Tira Cunha, entra Renan! Que Brasil é esse? Voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (80, 'CARLOS DO CARMO ANDRADE MELLES', 'Carlos Melles', 'DEM', 'MG', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, eu voto aqui hoje a favor das nossas crianças, da nossa juventude, das nossas famílias, da minha Paraíso, do meu sul de Minas. Voto a favor do agricultor e do café, voto a favor dos mineiros e do Brasil. Mas voto também a favor da Constituição. Voto sim ao impeachment da Presidente Dilma Rousseff!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (81, 'ANTONIO PEDRO DE SIQUEIRA INDIO DA COSTA', 'Indio da Costa', 'PSD', 'RJ', 'Sim', 'M', 'Sr. Presidente, eu disputei a eleição de 2010 como Vice de José Serra, contra Dilma Rousseff e contra o PT. Na época denunciei a corrupção. Hoje, falta saúde, educação, segurança pública, e falta emprego. Fui Relator da Lei da Ficha Limpa e trabalhei muito para aprová-la nesta Casa. Não poderia votar diferente. Votarei em favor dos cariocas e dos brasileiros. Votarei sim pelo impeachment da Presidente Dilma!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (82, 'EDUARDO HENRIQUE DA FONTE DE ALBUQUERQUE SILVA', 'Eduardo da Fonte', 'PP', 'PE', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, ao lado do meu filho Luiz Eduardo, que aqui está, em nome da minha família, eu gostaria de, primeiro, pedir a Deus que abençoe o Brasil. Eu vou passar a palavra ao meu filho, para que ele possa dizer ao Brasil o meu voto. Sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (83, 'CARLOS EDUARDO XAVIER MARUN', 'Carlos Marun', 'PMDB', 'MS', 'Sim', 'M', 'Sr. Presidente, porque é constitucional, porque é necessário —meu País precisa de um novo rumo —, pela minha família, pelo meu Mato Grosso do Sul, eu voto sim. Impeachment já! Viva o Brasil!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (84, 'CHRISTIANE DE SOUZA YARED', 'Christiane de Souza Yared', 'PR', 'PR', 'Sim', 'F', 'Senhores, cabe a nós a tarefa de unirmos este País. E,se as leis são para todos, se as pedaladas condenam Dilma, devem condenar também Governadores como o do meu Estado. Sr. Beto Richa, a sua hora está chegando! Meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (85, 'CARLOS FELIPE VAZQUEZ DE SOUZA LEÃO', 'Cacá Leão', 'PP', 'BA', 'Abstenção', 'M', 'Todos os amigos da Bahia conhecem a nossa posição no Estado. Jamais poderia faltar coerência na minha posição neste momento. Não posso permitir que a traição marque a minha vida e a da minha família, mas não posso desrespeitar a orientação do meu partido, que fechou questão a favor do impeachment. Então, eu me abstenho desta votação.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (86, 'ANTONIO CARLOS GOMES DA SILVA', 'Carlos Gomes', 'PRB', 'RS', 'Sim', 'M', 'Sr. Presidente, em nome das famílias gaúchas, em nome das pessoas que, com trabalho, constroem este País, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (87, 'CARLOS GOMES BEZERRA', 'Carlos Bezerra', 'PMDB', 'MT', 'Sim', 'M', 'Sr. Presidente, eu saí da cadeia da ditadura para, há50 anos, fundar o MDB. Sou membro da Direção Nacional do PMDB. E o meu partido, praticamente por unanimidade, está apoiando o voto sim. Portanto, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (88, 'CARLOS HENRIQUE AMORIM', 'Carlos Henrique Gaguim', 'PTN', 'TO', 'Sim', 'M', 'Pela minha família, pelos meus amigos, pelo melhor Estado, o Tocantins, e por Palmas, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (89, 'CARLOS HENRIQUE FOCESI SAMPAIO', 'Carlos Sampaio', 'PSDB', 'SP', 'Sim', 'M', 'Brasileiros e brasileiras, para que a decência se sobreponha a este Governo moralmente desonesto, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (90, 'CARLOS HUMBERTO MANNATO', 'Carlos Manato', 'SD', 'ES', 'Sim', 'M', 'Sr. Presidente, em homenagem à minha querida Alegre, na Região do Caparaó, aos 3,5 milhões de capixabas em 78 Municípios, às mais de 50 mil pessoas do movimento Vem Pra Rua que estão na Praça do Papa, e para que nossa ex-Presidenta Dilma tenha férias eternas, eu digo sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (91, 'CARLOS VICTOR GUTERRES MENDES', 'Victor Mendes', 'PSD', 'MA', 'Sim', 'M', 'Acreditando, Sras. e Srs. Deputados, que o meu voto ajudará o nosso País, ajudará a melhorar o nosso País, com a coragem do maranhense de norte a sul, de leste a oeste, com o carinho e a força da minha cidade natal, a cidade de Pinheiro, em homenagem às minhas únicas e verdadeiras riquezas, minhas filhas, Amanda e Isabela, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (92, 'CARMEN EMÍLIA BONFÁ ZANOTTO', 'Carmen Zanotto', 'PPS', 'SC', 'Sim', 'F', 'Com serenidade e convicção, pelo meu Estado de Santa Catarina, pelo fim da corrupção em todos os espaços de poder, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (93, 'CELIO ANTONIO DA SILVEIRA', 'Célio Silveira', 'PSDB', 'GO', 'Sim', 'M', 'Sr. Presidente, pela minha querida Luziânia, pelo meu Entorno de Brasília, que tanto precisa de ação governamental, pelos médicos brasileiros, tão perseguidos por este desgoverno, pelo bravo e honrado povo de Goiás, por Daianne, Mateus e Adrianne, muda Brasil!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (94, 'CELSO MALDANER', 'Celso Maldaner', 'PMDB', 'SC', 'Sim', 'M', 'Eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (95, 'CELSO PANSERA', 'Celso Pansera', 'PMDB', 'RJ', 'Não', 'M', 'É necessário que se mantenha a coerência com aquilo que nós acreditamos e aquilo que nós defendemos por toda uma vida. É necessário manter a esperança daqueles que ainda esperam ter uma casa própria com o Minha Casa, Minha Vida, de milhares de famílias na Baixada Fluminense que dependem do Bolsa Família. Em homenagem às centenas de amigos, professores, pesquisadores, que têm enviado mensagens para mim nos últimos dias, pedalada fiscal não é crime, eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (96, 'CELSO UBIRAJARA RUSSOMANNO', 'Celso Russomano', 'PRB', 'SP', 'Sim', 'M', 'Sr. Presidente, o meu Estado, o Estado de São Paulo, me deu a honra de ser o Deputado mais votado do Brasil, com 1.524.286 votos. Não poderia, de forma nenhuma, de forma nenhuma, fazer com que o povo do meu Estado se decepcionasse comigo. Pelo meu Estado, pela família brasileira, pela minha família, meus filhos, a geração dos meus filhos e a geração dos meus netos, eu voto sim ao impeachment, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (97, 'CÉSAR ANTONIO DE SOUZA', 'Cesar Souza', 'PSD', 'SC', 'Sim', 'M', 'Sr. Presidente, em 1992, eu aqui estava e votei a favor do impeachment. Agora, o País está clamando por mudança. Estou aqui, em nome do povo de Florianópolis, em nome do povo de Santa Catarina e do Brasil, pela segunda vez neste plenário, e o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (98, 'CESAR HANNA HALUM', 'César Halum', 'PRB', 'TO', 'Sim', 'M', 'Para devolver a esperança ao povo brasileiro, ao povo do meu Estado de Tocantins; especialmente pela minha cidade de Araguarina; em memória do meu irmão João Halum, que me ensinou a combater a corrupção, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (99, 'CLARISSA BARROS ASSED MATHEUS DE OLIVEIRA', 'Clarissa Garotinho', 'PR', 'RJ', 'Ausente', 'F', NULL);
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (100, 'CLÁUDIO CAJADO SAMPAIO', 'Claudio Cajado', 'DEM', 'BA', 'Sim', 'M', 'Pelas baianas e baianos que querem um País decente, em nome da minha cidade, Dias d''Ávila, que me projetou para a vida pública, digo que o povo brasileiro não é refém deste momento, mas passageiro rumo ao futuro e ao seu melhor destino. Para que os governantes brasileiros possam cumprir a Constituição da República do Brasil e as leis brasileiras que ao tomarem posse juraram defender e cumprir, voto sim, pelo impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (101, 'CLEBER VERDE CORDEIRO MENDES', 'Cleber Verde', 'PRB', 'MA', 'Sim', 'M', 'Sr. Presidente, Deputados, brasileiros e brasileiras, esperamos que amanhã possamos estar todos unidos em favor do Brasil. Sr. Presidente, embora ninguém possa voltar atrás para fazer um novo começo, qualquer um pode começar de novo e fazer um novo fim. O PRB segue junto e unido. Que Deus nos abençoe. Sim ao impeachment!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (102, 'CRISTIANE BRASIL FRANCISCO', 'Cristiane Brasil', 'PTB', 'RJ', 'Sim', 'F', 'Sr. Presidente, obrigada por permitir a todo o povo brasileiro esta data que estamos vivendo hoje. Há 11 anos, meu pai perdeu seu mandato porque disse a verdade, quando muitos aqui disseram que o que ele estava falando era mentira. Portanto, hoje, em homenagem ao meu pai, Roberto Jefferson, à verdade, à democracia, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (103, 'DAGOBERTO NOGUEIRA FILHO', 'Dagoberto', 'PDT', 'MS', 'Não', 'M', 'Em homenagem a um grande brasileiro, o Leonel Brizola, e à sua história de luta, pela legalidade, pela democracia e pela liberdade, eu voto não, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (104, 'DAMIÃO FELICIANO DA SILVA', 'Damião Feliciano', 'PDT', 'PB', 'Não', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, primeiro, eu rogo a Deus que ilumine os caminhos da Paraíba e os caminhos do Brasil. Pelo Estado Democrático de Direito, seguindo o meu partido, o PDT, eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (105, 'DAMINA DE CARVALHO PEREIRA', 'Dâmina Pereira', 'PSL', 'MG', 'Sim', 'F', 'Pelo futuro dos meus netos, pela minha cidade Lavras, pelo meu querido povo mineiro e pela retomada do crescimento do Brasil, meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (106, 'DANIEL ELIAS CARVALHO VILELA', 'Daniel Vilela', 'PMDB', 'GO', 'Sim', 'M', 'Sr. Presidente, falo aqui em nome da maioria dos goianos. Ressaltando o papel que cabe a esta Casa de julgar a admissibilidade, entendo que há elementos suficientes para o prosseguimento da ação. Portanto, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (107, 'RAIMUNDO GOMES DE MATOS', 'Raimundo Gomes de Matos', 'PSDB', 'CE', 'Sim', 'M', 'Sr. Presidente, em defesa do povo cearense, em apoio aos milhares de fortalezenses que estão, neste momento, na Praça Portugal, pelo fortalecimento dos Municípios brasileiros a partir de Maranguape, Maracanaú, na região metropolitana, e acima de tudo pela esperança do povo brasileiro, por um desenvolvimento socioeconômico justo para o nosso País, nós votamos sim pelo impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (109, 'DANIEL PIRES COELHO', 'Daniel Coelho', 'PSDB', 'PE', 'Sim', 'M', 'Num momento da história, este Plenário cassou um Presidente que cometeu crime, o ex-Presidente Collor. Esse fato se repete hoje no Brasil. A Presidente Dilma — ex-Presidente Dilma — cometeu crime de responsabilidade ao usar dinheiro público sem autorização desta Casa. A lei é para todos. Eu voto sim com o Brasil. Fora, Dilma! Fora, Dilma!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (110, 'DANILO JORGE DE BARROS CABRAL', 'Danilo Cabral', 'PSB', 'PE', 'Sim', 'M', 'Sr. Presidente, reassumi o mandato — eu estava ocupando o cargo de Secretário de Estado em Pernambuco — por entender que, neste momento tão importante para a vida do nosso País, não cabia fazermos a delegação deste momento. Em nome da confiança que o povo de Pernambuco nos deu de estar aqui representando um Estado que tem a marca das lutas libertárias, quero aqui, neste momento tão importante para o Brasil de virada de página, prestar uma homenagem a um grande pernambucano que deu o que temos de mais valor, a vida. Em nome de um Brasil diferente, em nome de Eduardo Henrique Accioly Campos, que foi nosso Governador, Presidente do nosso partido, em nome de sonhos de um Brasil mais igual, de um Brasil mais equilibrado, com mais educação e saúde, de um Estado que faça as entregas que a população deseja, em nome daquilo que ele nos pediu, Coragem pra mudar o Brasil, sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (111, 'DANRLEI DE DEUS HINTERHOLZ', 'Danrlei de Deus Hinterholz', 'PSD', 'RS', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, foi-me dada a honra, pelo povo do Rio Grande do Sul, de estar nesta Casa. E é carregando a esperança de todos os gaúchos que eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (112, 'DARCI POMPEO DE MATTOS', 'Pompeo de Mattos', 'PDT', 'RS', 'Abstenção', 'M', 'Presidente, nem Dilma, nem Temer, nem Cunha. Eu quero eleições limpas e honestas para limpar mais que a sujeira, limpar a alma do País. Cumpro decisão do meu partido: não posso votar a favor, mas não voto contra. Eu voto pela abstenção, contra a corrupção.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (113, 'DARCÍSIO PAULO PERONDI', 'Darcísio Perondi', 'PSD', 'RS', 'Sim', 'M', 'Pelos direitos das crianças, dos jovens, das mulheres, de todos os brasileiros; por um Governo decente e, acima de tudo, por mais esperança para os brasileiros, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (115, 'DÉCIO NERY DE LIMA', 'Décio Lima', 'PT', 'SC', 'Não', 'M', 'Sr. Presidente, pelos trabalhadores do campo e da cidade; por todos aqueles que lutaram pela democracia e tombaram nesta luta; pelo Deputado Paulo Wright, do meu Estado, que foi assassinado pela ditadura; contra o golpe, voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (116, 'DIEGO ALEXSANDER GONCALO PAULA GARCIA', 'Diego Garcia', 'PHS', 'PR', 'Sim', 'M', 'Sr. Presidente, fui eleito por paranaenses que acreditam no resgate da moralidade na política. Fui eleito pelo projeto de fé e política da renovação carismática católica no meu Estado do Paraná. Pela minha família, pela minha filha, pela minha esposa, pelo povo do Paraná, tenho orgulho em dizer: terra da Lava-Jato, avante! Polícia Federal! Sim ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (117, 'DIEGO LEONARDO DE ANDRADE CARVALHO', 'Diego Andrade', 'PSD', 'MG', 'Sim', 'M', 'Por Minas Gerais; pelos meus filhos, Isabel e Léo, que me acompanham de Belo Horizonte; pelos transportadores; pelos agricultores; por minha querida Três Pontas; pelo meu avô, exemplo de trabalho em Juatuba; por toda Minas Gerais; pelo homem do campo; pelo transportador; pelos profissionais da saúde; o meu voto é sim ao impeachment!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (118, 'DILCEU JOÃO SPERAFICO', 'Dilceu Sperafico', 'PP', 'PR', 'Sim', 'M', 'Pela minha família, pela minha querida cidade de Toledo, do meu Estado do Paraná, pelos agricultores do nosso Brasil, que estão fazendo com que este País ainda possa andar, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (119, 'DIMAS FABIANO TOLEDO JÚNIOR', 'Dimas Fabiano', 'PP', 'MG', 'Sim', 'M', 'Hoje é o dia certo de fazer a coisa certa da maneira certa. Pela minha esposa, Juliana, pelos meus filhos, Leonardo e Lucas, por Varginha, por Bocaina de Minas, por Itajubá, pelo Brasil e por Minas Gerais, meu voto é sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (120, 'DIONILSO MATEUS MARCON', 'Marcon', 'PT', 'RS', 'Não', 'M', 'Sou brasileiro e, em defesa dos pequenos, dos pobres, dos trabalhadores e em defesa dos movimentos sociais, da reforma agrária e da reforma urbana, pela democracia, e para que todos os réus da Lava-Jato estejam na prisão, voto contra o golpe. O meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (121, 'DOMINGOS GOMES DE AGUIAR NETO', 'Domingos Neto', 'PSD', 'CE', 'Não', 'M', 'Solução para o nosso País só pode existir se for através da democracia. Apresentei nesta Casa, com apoio da maioria, projeto que convoca plebiscito popular para que a população diretamente decida o nosso futuro. Qualquer solução fora disso não pode ser respeitada por um País que tanto lutou pela democracia. Sou contra eleições indiretas. Sou a favor da democracia e voto não ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (122, 'DOMINGOS SÁVIO CAMPOS RESENDE', 'Domingos Sávio', 'PSDB', 'MG', 'Sim', 'M', 'Pelos valores da família; pelos homens livres e de bons costumes; em respeito ao meu povo, de Divinópolis e de toda Minas Gerais; em respeito ao povo brasileiro, que não suporta mais este desgoverno de tanta corrupção; em respeito à Constituição, não vai haver golpe! Vai haver impeachment! Meu voto é sim, a favor do Brasil!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (123, 'DULCE FERREIRA PAGANI MIRANDA', 'Dulce Miranda', 'PMDB', 'TO', 'Sim', 'F', 'Sr. Presidente, com toda a convicção, por conhecer os 139 Municípios do Estado do Tocantins, voto por um Brasil melhor, para que todos tenham dignidade. O meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (124, 'EDER MAURO CARDOSO BARRA', 'Delegado Éder Mauro', 'PSD', 'PA', 'Sim', 'M', 'Sr. Presidente, em nome do meu filho Éder Mauro Filho, de 4 anos, e do Rogério, que, junto com a minha esposa, formamos uma família no Brasil, que tanto esses bandidos querem destruir com propostas de que criança troque de sexo e aprenda sexo nas escolas, com 6 anos de idade, em nome de todo o povo do Estado do Pará, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (125, 'ÉDIO VIEIRA LOPES', 'Édio Lopes', 'PR', 'RR', 'Não', 'M', 'Sr. Presidente, o meu voto é contra o prosseguimento do processo de impedimento da Sra. Presidente da República.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (126, 'EDMILSON BRITO RODRIGUES', 'Edmilson Rodrigues', 'PSOL', 'PA', 'Não', 'M', 'Sr. Presidente, o meu voto é um rotundo não, com a moral de ser de um partido, o PSOL, que faz oposição, mas oposição de esquerda, programática, socialista ao Governo, e que não é conivente com golpe à democracia.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (127, 'EDSON EDINHO COELHO ARAÚJO', 'Edinho Araújo', 'PMDB', 'SP', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Parlamentares, celebrando a Constituição Cidadã de Ulysses Guimarães, as liberdades individuais com o povo nas ruas, o fortalecimento das instituições brasileiras, pelo Brasil, por São Paulo, por São José do Rio Preto e região, por esta e pelas futuras gerações, para renovar as esperanças do povo brasileiro e pedindo a Deus que abençoe este País, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (128, 'EDSON MOREIRA DA SILVA', 'Delegado Edson Moreira', 'PSL', 'MG', 'SIm', 'M', 'Sr. Presidente, o Brasil espera que cada um cumpra o seu dever — Almirante Barroso. Pela libertação do povo brasileiro, pela minha família, pelo saudoso Celso Daniel e por Minas Gerais, São Paulo e todo o Brasil, eu voto sim, Sr. Presidente!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (129, 'EDUARDO COSENTINO DA CUNHA', 'Eduardo Cunha', 'PMDB', 'RJ', 'Sim', 'M', 'Que Deus tenha misericórdia desta Nação. Voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (130, 'EDUARDO LUIZ BARROS BARBOSA', 'Eduardo Barbosa', 'PSDB', 'MG', 'Sim', 'M', 'Sr. Presidente, ao longo desses meus 6 mandatos, tenho presenciado aqui uma incoerência de um discurso humanista com uma prática inconsequente, rasteira e mentirosa. Por isso tudo e também pelo crime de responsabilidade cometido, pela honra do nosso povo mineiro e de todos aqueles que eu represento, meu voto é sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (131, 'EDUARDO NANTES BOLSONARO', 'Eduardo Bolsonaro', 'PSC', 'SP', 'Sim', 'M', 'Pelo povo de São Paulo nas ruas, com o espírito dos revolucionários de 1932; em respeito aos 59 milhões de votos contra o Estatuto do Desarmamento, em 2005; pelos militares de 1964, hoje e sempre; pelas polícias e, em nome de Deus e da família brasileira, é sim. E Lula e Dilma na cadeia.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (132, 'EDUARDO PEDROSA CURY', 'Eduardo Cury', 'PSDB', 'SP', 'Sim', 'M', 'Sob a proteção de Deus, representando o Vale do Paraíba, em apoio ao Juiz Sergio Moro e aos garotos da Lava-Jato, em defesa dos valores da liberdade e do respeito aos valores individuais, o meu voto só pode ser sim, a favor do impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (133, 'EFRAIM DE ARAÚJO MORAIS FILHO', 'Efraim Filho', 'DEM', 'PB', 'Sim', 'M', 'Sr. Presidente, voto com a coerência de quem sempre fez oposição ao PT,apontando os seus erros, os seus equívocos e as suas mentiras, apontando os crimes de responsabilidade que causaram um rombo no Orçamento e essa farra com o dinheiro público, cuja conta quem paga é o cidadão. As empresas estão fechando, os pais e mães de família estão desempregados, roubaram o dinheiro dos aposentados nos fundos de pensão. O remédio para um Governo irresponsável está previsto na Constituição e é o impeachment, realizado pelo Congresso e fiscalizado pelo Supremo Federal. Por isso, pelo amor à minha Paraíba e à minha família, meu voto é sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (134, 'ELCIONE THEREZINHA ZAHLUTH BARBALHO', 'Elcione Barbalho', 'PMDB', 'PA', 'Não', 'F', 'Em respeito à nossa Carta Magna, à nossa Constituição, eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (135, 'ELIZEU DIONIZIO SOUZA DA SILVA', 'Elizeu Dionizio', 'PSDB', 'MS', 'Sim', 'M', 'Sr. Presidente, na minha curta estrada da política, é a segunda vez que eu deparo com uma situação dessas. É a segunda vez que tenho que votar contra um gestor que cometeu improbidade administrativa. Como na primeira vez, eu voto pelo meu Mato Grosso do Sul. Pela juventude do meu Brasil, eu voto sim. Tchau, querida!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (136, 'ELIZIANE PEREIRA GAMA FERREIRA', 'Eliziane Gama', 'PPS', 'MA', 'Sim', 'F', 'Sr. Presidente, pelo combate à corrupção, que ficou cristalizada neste Governo, em nome da força do meu Maranhão, em nome da força da minha São Luís, em nome da força da minha gente nordestina, eu voto sim ao impeachment!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (137, 'ELMAR JOSE VIEIRA NASCIMENTO', 'Elmar Nascimento', 'DEM', 'BA', 'Sim', 'M', 'Para extirpar da vida nacional essa organização criminosa que sequestrou a Bahia e o Brasil, pelo Brasil e pelos brasileiros, pela Bahia e pelos baianos, pelo futuro das minhas Mariana e Juliana, encho o peito de orgulho, e este campo-formosense vota sim. Viva o Brasil!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (138, 'ELVINO JOSÉ BOHN GASS', 'Bohn Gass', 'PT', 'RS', 'Não', 'M', 'Eu tenho nojo e horror do golpismo. O poder édo povo, e o povo elegeu a Presidenta. Ela é honesta e não pode ser afastada por um processo ilegítimo, conduzido por um réu da Lava-Jato. Pela democracia e pelo País, é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (139, 'ENIO JOSÉ VERRI', 'Enio Verri', 'PT', 'PR', 'Não', 'M', 'Sr. Presidente, em respeito aos 200 milhões de habitantes deste País, que lutaram e morreram pela democracia — neste momento, querem transformar esta Casa num colégio eleitoral aplicando um golpe em nossa Constituição —, em respeito aos trabalhadores, eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (140, 'ÉRIKA JUCÁ KOKAY', 'Erika Kokay', 'PT', 'DF', 'Não', 'F', 'Por não haver saída fora da democracia; em homenagem ao povo brasileiro, que carrega as marcas de ausência de democracia na pele e na alma; contra a corrupção, que está sentada na Presidência desta sessão; contra o golpe e a traição; em defesa dos direitos e em defesa da democracia, eu voto contra o golpe, eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (141, 'ERIVELTON LIMA SANTANA', 'Erivelton Santana', 'PEN', 'BA', 'Sim', 'M', 'Presidente, pelo resgate da esperança do povo brasileiro, pela reconstrução do nosso País, mas, sobretudo, em defesa da vida, da família e da fé, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (142, 'ERONILDES VASCONCELOS CARVALHO', 'Tia Eron', 'PRB', 'BA', 'Sim', 'F', 'Sr. Presidente, Sras. e Srs. Deputados, povo brasileiro, estou muito honrada nesta noite, porque eu sou a voz da mulher negra e da mulher nordestina, que não quer mais a migalha do Governo Federal, pois tem dignidade para trabalhar e para vencer. Eu sou a voz do Presidente Nacional, Marcos Pereira, que traz aqui unidade ao Partido Republicano Brasileiro. Eu sou a voz dos jovens, das crianças da minha Bahia, da minha Salvador, terra mãe deste País, que não se curvará a esta farsa. Vários Deputados chegaram aqui para dizer que não existe crime, e estamos convencidos de que existe, sim, uma configuração do crime de responsabilidade. Portanto, o meu voto é sim, sim, sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (143, 'EROS FERREIRA BIONDINI', 'Eros Biondini', 'PROS', 'MG', 'Sim', 'M', 'Sr. Presidente, pelos valores que herdei dos meus pais, e que procuro repassar aos meus filhos; pela gratidão que tenho à Renovação Carismática Católica, à Canção Nova e aos demais movimentos que me ajudaram a me livrar das drogas; pelos milhões de brasileiros que vivem hoje nas drogas e não têm ajuda; para honrar os belorizontinos, os mineiros, os brasileiros nesta Terra de Santa Cruz, o meu voto é sim. E o voto do meu suplente, Euclides, também seria sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (144, 'ESPERIDIÃO AMIN HELOU FILHO', 'Esperidião Amin', 'PP', 'SC', 'Sim', 'M', 'Para honrar a ampla maioria da vontade do povo catarinense e para dar uma chance ao Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (145, 'EVANDRO HERRERA BERTONE GUSSI', 'Evandro Gussi', 'PV', 'SP', 'Sim', 'M', 'Sr. Presidente, este é o momento de silenciarmos a nossa voz e o nosso coração e de ouvirmos a voz que vem das urnas inundadas de verde-amarelo no Brasil. A favor do Brasil, por São Paulo, especialmente, do seu oeste, Sr. Presidente, eu voto sim e registro aqui que o Deputado Mendes Thame, suplente nesta ocasião, se aqui estivesse, também votaria sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (146, 'EVANDRO ROGERIO ROMAN', 'Evandro Roman', 'PSD', 'PR', 'Sim', 'M', 'Pelo meu Paraná, pelo meu Estado, que foi tão maltratado por este Governo, pela minha querida Céu Azul, pela minha região Oeste do Estado do Paraná, por Cascavel, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (147, 'EXPEDITO GONCALVES FERREIRA NETTO', 'Expedito Netto', 'PSD', 'RO', 'Sim', 'M', 'Sr. Presidente, gostaria de cumprimentar todos os nobres companheiros que aqui se encontram e todos que nos assistem pelos meios de comunicação de nosso País. Quero dizer que, hoje, estamos votando o impeachment da Presidente Dilma e, amanhã, estaremos votando o seu, Sr. Presidente. E pode ter certeza de que votarei da mesma forma como voto hoje. Respeitando o povo rondoniense e respeitando o povo brasileiro, eu voto sim, contra a corrupção, venha ela de que partido vier.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (148, 'EZEQUIEL CORTAZ TEIXEIRA', 'Ezequiel Teixeira', 'PTN', 'RJ', 'Sim', 'M', 'Sr. Presidente, parafraseando o pastor Martin Luther King, nós não somos ainda aquilo que gostaríamos de ser. Não sabemos em quanto tempo seremos aquilo que gostaríamos de ser. Mas uma coisa é certa: nunca mais seremos os mesmos. Eu quero homenagear os mais de 50 milhões de evangélicos do Brasil, a Nação Cara de Leão, a minha querida família, a minha esposa Márcia Teixeira, os meus filhos, Diego e Tati Teixeira, por um tempo novo contra essa corrupção. Voto sim ao impeachment!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (149, 'FABIO ABREU COSTA', 'Capitão Fábio Abreu', 'PTB', 'PI', 'Não', 'M', 'De acordo com as minhas convicções, em defesa das leis deste País, eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (150, 'FÁBIO AUGUSTO RAMALHO DOS SANTOS', 'Fábio Ramalho', 'PMDB', 'MG', 'Sim', 'M', 'Eu pedi a Deus que me desse sabedoria para votar com dignidade. Eu pedi a Deus que me iluminasse. E, neste momento, em nome de um Estado cujo outro nome éliberdade, Minas Gerais; em nome de milhares de mineiros que me pediram para votar a favor do impeachment; eu estou aqui para declarar o meu voto, em gratidão ao povo mineiro, à família mineira e, sobretudo, aos milhões de desempregados deste País. Eu voto sim por Minas Gerais e pelo Brasil!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (151, 'FABIO CRUZ MITIDIERI', 'Fábio Mitidieri', 'PSD', 'SE', 'Não', 'M', 'Quis o destino e o povo de Sergipe que eu estivesse aqui hoje vivendo este momento. Eu não sou ladrão, não sou corrupto, não sou vendido, sequer sou PT. Também amo a minha família e o povo sergipano, e a democracia nos ensina a respeitar as posições divergentes. Exercendo essa democracia, em consideração ao Estado Democrático de Direito e à vontade das urnas, voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (152, 'FABIO DE ALMEIDA REIS', 'Fábio Reis', 'PMDB', 'SE', 'Sim', 'M', 'Sr. Presidente, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (153, 'FABIO FERNANDES DE SOUSA', 'Fábio Souza', 'PSDB', 'GO', 'Sim', 'M', 'Sr. Presidente, pela minha família, por meus filhos, Estêvão e Amanda, pela minha esposa, pelos meus pais, pelo meu Estado de Goiás, pelo futuro do Brasil, eu digo sim. Viva o Brasil!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (154, 'FABIO PAULINO GARCIA', 'Fabio Garcia', 'PSB', 'MT', 'Sim', 'M', 'Sr. Presidente, por um Brasil mais justo, pela mudança, pela retomada da esperança, por um novo caminho, pelo meu Mato Grosso, que tanto amo, pelos milhões de brasileiros que foram às ruas, pelos meus mato-grossenses, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (155, 'FÁBIO SALUSTINO MESQUITA DE FARIA', 'Fábio Faria', 'PSD', 'RN', 'Sim', 'M', 'Pela união do Brasil, com fé nas nossas instituições,e pela retomada do crescimento, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (156, 'FAUSTO RUY PINATO', 'Fausto Pinato', 'PP', 'SP', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, em um momento este País escolheu a bandeira vermelha, mas viu que errou e quer novamente o verde-amarelo, a ordem e o progresso. Esse povo que está aí fora não veio da Venezuela, não veio da Coreia do Norte. Eu queria aqui, em nome da minha família, em nome da minha região noroeste do Estado de São Paulo, da minha cidade natal, votar sim e registrar que William Woo, que é suplente, se estivesse aqui, também votaria sim, por um Brasil mais forte, um Brasil independente, um Brasil sem corrupção.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (157, 'FELIPE CATALÃO MAIA', 'Felipe Maia', 'DEM', 'RN', 'Sim', 'M', 'Sr. Presidente, pelo respeito à Constituição Federal, pelo respeito às outras leis do ordenamento jurídico do nosso País que regulamentam ocrime de responsabilidade civil, pelo povo do Brasil, pelo povo do Rio Grande do Norte, por todos aqueles que ocupam as ruas do nosso Brasil, para pedir mudanças para este País, pelas famílias que estão desempregadas — já se somam 10 milhões de famílias — e para que o Brasil possa, Sr. Presidente, ter uma luz no fim do túnel, eu voto sim ao impeachment da Presidente Dilma.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (158, 'FELIPE LEONE BORNIER DE OLIVEIRA', 'Felipe Bornier', 'PROS', 'RJ', 'Sim', 'M', 'Pelo futuro do meu filho e do meu País, pelo meu Estado do Rio de Janeiro; que tenho muito orgulho de representar aqui na Câmara dos Deputados; pelos meus eleitores do noroeste fluminense; pela minha querida Baixada Fluminense; pela minha maravilhosa cidade de Nova Iguaçu, que eu muito amo e tenho orgulho de representar nesta Casa, e pelos 10 milhões de brasileiros hoje desempregados, eu voto pelo impedimento da Presidente da República. Eu voto sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (159, 'FELIX DE ALMEIDA MENDONÇA JÚNIOR', 'Félix Mendonça Júnior', 'PDT', 'BA', 'Não', 'M', 'Sr. Presidente, já com a clareza do resultado, vou acompanhar meu partido votando não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (160, 'FERNANDO ANTÔNIO CECILIANO JORDÃO', 'Fernando Jordão', 'PMDB', 'RJ', 'Sim', 'M', 'Sr. Presidente, pelo Brasil, por todas as cidades do Rio de Janeiro, pelo eleitor que me colocou aqui, pelo trabalhador desempregado, pela minha família, eu digo: O verde de teu mar, oh, Angra dos Reis! A luz de teu luar, oh, Angra dos Reis! O brilho do teu sol, oh, Angra dos Reis! Sim pelo impeachment da Dilma!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (161, 'FERNANDO BEZERRA DE SOUZA COELHO FILHO', 'Fernando Coelho Filho', 'PSB', 'PE', 'Sim', 'M', 'Sr. Presidente, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (162, 'FERNANDO DANTAS TORRES', 'Fernando Torres', 'PSD', 'BA', 'Não', 'M', 'Sr. Presidente, o senhor talvez seja o Deputado que mais conhece o Regimento, mas não era para estar sentado nesta cadeira. O senhor, Presidente, não era para estar aí. Estão me vaiando! Mas eu vou esperar, Presidente, que o Conselho de Ética mande sua cassação para o Plenário e vou votar sim à sua cassação. Eu voto, Sr. Presidente, não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (163, 'FERNANDO DESTITO FRANCISCHINI', 'Fernando Francischini', 'SD', 'PR', 'Sim', 'F', 'Sr. Presidente, como Delegado da Polícia Federal, meu voto vai pelo fim da facção criminosa lulopetista, fim da pelegagem da CUT, fim da CUT e seus marginais. Viva a Lava-Jato, a República de Curitiba! E a minha bandeira nunca será vermelha! Sim, Presidente!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (164, 'FERNANDO LÚCIO GIACOBO', 'Giacobo', 'PR', 'PR', 'Sim', 'M', 'Sr. Presidente, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (165, 'FLÁVIA CARREIRO ALBUQUERQUE MORAIS', 'Flávia Morais', 'PDT', 'GO', 'Sim', 'F', 'Que Deus abençoe o nosso País neste momento tão difícil. Com todo o respeito ao posicionamento do meu partido, mas atendendo ao apelo do povo de Goiás, do povo de Trindade, do Brasil, eu voto sim, Sr. Presidente, pelo impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (166, 'FLAVIANO FLÁVIO BAPTISTA DE MELO', 'Flaviano Melo', 'PMDB', 'AC', 'Sim', 'M', 'Pelo meu querido Estado do Acre; pelos peemedebistas,que sempre me acompanharam; pela democracia; pelo futuro deste País para nossos filhos e nossos netos, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (167, 'FLAVIO ALVES SABINO', 'Cabo Sabino', 'PR', 'CE', 'Sim', 'M', 'Presidenta Dilma, V.Exa. está sentindo o que 10 milhões de brasileiros sentiram quando receberam o aviso prévio de perda dos seus empregos. V.Exa. também está perdendo o seu emprego. Tchau, querida, não precisa voltar! Eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (168, 'FLAVIO AUGUSTO DA SILVA', 'Flavinho', 'PSB', 'SP', 'Sim', 'M', 'Cumprindo a minha obrigação constitucional de legislador e de fiscalizador, eu acolho a denúncia dos juristas que foi apresentada contra a Presidente Dilma Rousseff. Também, fazendo um julgamento político, creio que não é o melhor Governo para este País. Mas, Sr. Presidente, com todo respeito institucional que tenho por V.Exa., espero que também esta Casa tenha a coerência de fazer com que o processo contra V.Exa. progrida, para que esta Casa também seja passada a limpo. Pelo Brasil, pelo meu Estado e pela honra da minha família, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (169, 'FRANCISCO ALVES DE AGUIAR', 'Francisco Chapadinha', 'PTN', 'PA', 'Sim', 'M', 'Pelo Pará e pelo Brasil, sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (170, 'FRANCISCO ARIOSTO HOLANDA', 'Ariosto Holanda', 'PDT', 'CE', 'Não', 'M', 'Sr. Presidente, em defesa da democracia, em defesa do Estado de Direito, pelo crescimento das nossas universidades federais, dos institutos federais e pela população mais pobre deste País, o meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (171, 'FRANCISCO DANILO BASTOS FORTE', 'Danilo Forte', 'PSB', 'CE', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, o Ceará é feito por homens e mulheres de bem. Em respeito ao meu povo, que me mandou para cá não para ser achincalhado, não para ser chamado de covarde, de picareta, de vendilhão do voto, mas para ter a honradez de um povo que precisa reconstruir a esperança, de um povo que foi enganado no castelo de mentiras que elegeu a Presidenta Dilma, no segundo mandato de um Governo que cometeu erros que o Advogado-Geral da União não conseguiu defender, pois são indefensáveis, e para garantir o respeito à Constituição brasileira e a responsabilidade fiscal, eu voto sim ao afastamento da Presidenta Dilma.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (172, 'FRANCISCO DE ASSIS CARVALHO GONÇALVES', 'Assis Carvalho', 'PT', 'PI', 'Não', 'M', 'Em defesa da minha Nação, do Nordeste, do Piauí, da minha cidade de Oeiras, mas, principalmente, pelo combate à corrupção representada por Eduardo Cunha e Michel Temer, eu digo não a esta corrupção ridícula que envergonha o meu País. Logo, logo, Eduardo Cunha, V.Exa. estará pagando por isto, detrás das grades, pelo mal que faz ao Brasil e ao mundo.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (173, 'FRANCISCO EURICO DA SILVA', 'Pastor Eurico', 'PHS', 'PE', 'Sim', 'M', 'Feliz é a nação cujo Deus é o Senhor! Em defesa da vida, da família, da moral, dos bons costumes, contra a corrupção e não desistindo do Brasil, meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (174, 'FRANCISCO EVERARDO OLIVEIRA SILVA', 'Tiririca', 'PR', 'SP', 'Sim', 'M', 'Sr. Presidente, pelo meu país, meu voto é sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (175, 'FRANCISCO FLORIANO DE SOUSA SILVA', 'Francisco Floriano', 'DEM', 'RJ', 'Sim', 'M', 'Muito obrigado, Sr. Presidente, Eduardo Cunha. Parabéns ao meu Relator, parabéns à Mesa que está presente, a todo povo brasileiro, parabéns a todo povo que está na Av. Atlântica, na cidade mais linda do mundo, Rio de Janeiro. Um abraço, Av. Atlântica! Eu quero dizer a todos que chega o momento de se valer da Constituição, de se valer do Regimento da Casa. De dar uma demonstração nesta tarde e ouvir a sintonia das vozes das ruas. Então, pelo meu Estado Democrático, pelo meu filho Matheus Floriano, minha filha Nathália e meu neto Lucca, que é criança, e também, Sr. Presidente, desde o meu Estado, de Varre-Sai a Paraty, o meu País, do Oiapoque ao Chuí, sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (176, 'FRANCISCO JOSÉ D''ANGELO PINTO', 'Chico D''Angelo', 'PT', 'RJ', 'Não', 'M', 'Em defesa da democracia, da Constituição e do mandato da Presidenta Dilma, uma mulher honesta e correta, que foi legitimamente eleita e que não cometeu nenhum crime, eu voto contra o golpe.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (178, 'FRANCISCO RODRIGUES DE ALENCAR FILHO', 'Chico Alencar', 'PSOL', 'RJ', 'Não', 'M', 'Contra a hipocrisia que faz corruptos se tornarem arautos da moralidade pública (palmas), contra o condutor ilegítimo dessa farsa, que está ali sentado à Presidência da Mesa da Câmara, por uma reforma política radical, com participação popular, que tire o poder da grana do sistema degenerado, pelos direitos da população, do povo que luta por terra, trabalho e dignidade. Contra esse processo de farsa. Quero falar! Não à demagogia, à mentira e à escalada reacionária. O nosso voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (179, 'FRANCISCO TADEU BARBOSA DE ALENCAR', 'Tadeu Alencar', 'PSB', 'PE', 'Sim', 'M', 'Sr. Presidente, o afastamento de um Presidente da República é sempre algo muito traumático na vida de qualquer país, mas essa é uma noite necessária. Estão presentes os pressupostos políticos e jurídicos que caracterizam crime de responsabilidade da Presidente. Essa grande manifestação do Parlamento brasileiro é o sentimento da maioria da sociedade brasileira. Por isso, hoje, nós começamos, aqui, a reconstruir a vida pública em nosso País, a tentar reconstruí-la. Precisamos seguir mudando. Inclusive, é um imperativo cívico a este Parlamento que a Presidência desta Casa se submeta ao julgamento deste Parlamento, porque a sociedade brasileira não entende que a Presidente da República esteja sendo afastada e que esta Presidência continue sob o repúdio da sociedade. Por Pernambuco, pelo meu líder Eduardo Campos, pelo Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (180, 'ROBERTO ALVES DE LUCENA', 'Roberto de Lucena', 'PV', 'SP', 'Sim', 'M', 'Sr. Presidente, o Brasil merece uma chance, o Brasil merece uma oportunidade. Énessa direção, com respeito ao povo de São Paulo e por amor à Nação brasileira, que eu voto sim. Que Deus abençoe o Brasil, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (198, 'HENRIQUE FONTANA JÚNIOR', 'Henrique Fontana', 'PT', 'RS', 'Não', 'M', 'Contra a conspiração e a corrupção representadas por Eduardo Cunha e Temer; contra o golpe; em defesa da democracia e do respeito ao voto do cidadão brasileiro, eu voto com toda convicção não a esse golpe, não a esse impeachment!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (181, 'FRANKLIN ROBERTO DE LIMA SOUZA', 'Franklin Lima', 'PP', 'MG', 'Sim', 'M', 'Srs. Deputados, Sr. Presidente, povo brasileiro, em primeiro lugar, eu quero agradecer a Deus a oportunidade de ser eleito por um Estado tão amável, tão maravilhoso como Minas Gerais. Neste Estado, nasceu uma pessoa que admiro muito, que é o apóstolo Valdemiro Santiago, e aquela Igreja maravilhosa me ajudou neste trabalho. Quero agradecer também à minha esposa, à minha filha, que vêm me dando muita força; à minha mãe; à minha tia Eurides, que cuidou de mim quando pequeno; à minha tia Geo, que me ensinou a educação. Eu quero agradecer ao povo brasileiro e dizer, neste instante, contra a corrupção, contra a roubalheira, contra a safadeza, eu sempre lutei por novas eleições. Neste momento, para que o brasileiro tenha uma nova esperança de dias melhores, de prosperidade, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (182, 'GABRIEL GUIMARÃES DE ANDRADE', 'Gabriel Guimarães', 'PT', 'MG', 'Não', 'M', 'Com a certeza de que a Presidenta Dilma não cometeu nenhum dos crimes pelos quais foi denunciada, o que será confirmado adiante, voto não, pelo não prosseguimento da denúncia pela qual responde.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (183, 'GENECIAS MATEUS NORONHA', 'Genecias Noronha', 'SD', 'CE', 'Sim', 'M', 'Sr. Presidente, em nome dos 221 mil que em mim votaram e acreditaram, em nome do meu Parambu, do meu Estado do Ceará e do povo brasileiro, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (184, 'GEORGE HILTON DOS SANTOS CECÍLIO', 'George Hilton', 'PROS', 'MG', 'Não', 'M', 'Sr. Presidente, eu quero fazer, hoje, no meu voto, uma homenagem a todos os mineiros e brasileiros que um dia lutaram pelo voto popular. Muitos deles pagaram com suas vidas. E esta Casa não tem o direito, hoje, já que as ruas estão divididas — e eram eles que nós tínhamos que consultar hoje, o que realmente a população quer. Trabalhei com a Presidente Dilma, sei o quanto ela é uma mulher honrada, e sei que vou passar para a história sem manchar as minhas mãos e a minha biografia de querer tirar do poder uma mulher eleita legitimamente, e uma mulher honrada. Pela democracia, é não!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (185, 'GERALDO RESENDE PEREIRA', 'Geraldo Resende', 'PSDB', 'MS', 'Sim', 'M', 'Em respeito ao meu querido povo de Dourados, em respeito ao povo do Mato Grosso do Sul, em respeito ao povo brasileiro, em nome da minha família e por um novo Brasil, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (186, 'GILBERTO JOSÉ SPIER VARGAS', 'Pepe Vargas', 'PT', 'RS', 'Não', 'M', 'Contra o acordão de Eduardo Cunha, Michel Temer e Aécio Neves, que querem abafar o combate à corrupção, contra os golpistas, que não prezam a democracia e os direitos dos trabalhadores, pela Constituição, que jurei cumprir, pela democracia e pela legalidade, meu voto é não ao golpe.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (187, 'GILBERTO NASCIMENTO SILVA', 'Gilberto Nascimento', 'PSC', 'SP', 'Sim', 'M', 'Sr. Presidente, eu, primeiro, agradeço a Deus pelo fato de estarneste momento nesta Casa. Em nome da minha família e pensando no futuro dos meus netos, Guilherme, Elisa e Gabriel; pensando também nos 10 milhões de brasileiros que estão desempregados; pensando nos mais de 60 milhões de pessoas que estão com seu nome no SPC; pensando ainda no povo evangélico e nos meus eleitores de São Paulo; na esperança de que os brasileiros voltem a sonhar, voltem a sorrir, o meu voto é sim ao impeachment. Muito obrigado.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (188, 'GIOVANI CHERINI', 'Giovani Cherini', 'PDT', 'RS', 'Sim', 'M', 'Gente de bom coração, pelo amor ao Brasil, pelo amor a esta bandeira, pelo amor à vida, pelo amor aos 115.294 eleitores, pelo fim da corrupção, Sr. Presidente, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (189, 'GIUSEPPE VECCI', 'Giuseppe Vecci', 'PSDB', 'GO', 'Sim', 'M', 'Com esperança na retomada do desenvolvimento do País, por Goiás e pelo Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (190, 'GIVALDO DE SÁ GOUVEIA', 'Givaldo Carimbão', 'PHS', 'AL', 'Não', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, Nação brasileira, seria muito fácil, depois de 20 votos a mais, querer aparecer e, de repente, fazer média com a sociedade, mas esse não é meu perfil, Sr. Presidente. Prefiro perder em pé, mas com dignidade, a fazer aquilo que muitos fizeram aqui, sem ter a coragem de enfrentar a sua posição. Aqui há homens de bem. Tenho 20 anos nesta Casa. Companheiros e companheiras Deputadas e Deputados, o Supremo Tribunal Federal julgou uma causa antes de ontem, e o resultado foi cinco a cinco. Ali estão os maiores juristas do Brasil julgando a Constituição, e o resultado foi uma divisão no Supremo: cinco a cinco. Quanto mais o Parlamento, que é uma Casa política! O Parlamentar tem toda a liberdade de divergir! Portanto, Sr. Presidente, fiz o meu registro na Casa por três vezes... e continuo com a minha posição: eu não vou lavar as minhas mãos, como Pôncio Pilatos. Eu voto não, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (191, 'GIVALDO VIEIRA DA SILVA', 'Givaldo Vieira', 'PT', 'ES', 'Não', 'M', 'A coragem daqueles que resistiram ao Golpe de 1964, com dor, sangue e a própria vida,foi o que devolveu à minha geração a tão sonhada liberdade. Em homenagem a esses lutadores da democracia, eu me recuso a entregar o meu País nas mãos dos golpistas, nas mãos dos traidores, nas mãos dos corruptos que armaram esta farsa e aqui a comandam! Em homenagem aos milhões de lutadores que hoje lutam e não se cansarão de lutar pela democracia, por amor à Pátria, o meu voto é não!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (192, 'GLAUBER DE MEDEIROS BRAGA', 'Glauber Braga', 'PSOL', 'RJ', 'Não', 'M', 'Eduardo Cunha, você é um gângster. O que dá sustentação à sua cadeira cheira enxofre. Eu voto por aqueles que nunca escolheram o lado fácil da história. Eu voto por Marighella, eu voto por Plínio de Arruda Sampaio, eu voto por Evandro Lins e Silva, eu voto por Arraes, eu voto por Luís Carlos Prestes. Eu voto por Olga Benário. Eu voto por Brizola e Darcy Ribeiro. Eu voto por Zumbi dos Palmares. Eu voto não!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (193, 'GUILHERME MUSSI FERREIRA', 'Guilherme Mussi', 'PP', 'SP', 'Sim', 'M', 'Sr. Presidente, senhoras e senhores, pela legalidade, com muita responsabilidade e serenidade, em respeito à minha família, aos meus amigos, a todos os paulistas e aos brasileiros, meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (194, 'HEBERTE LAMARCK GOMES DA SILVA', 'Betinho Gomes', 'PSDB', 'PE', 'Sim', 'M', 'Povo brasileiro, nós estamos aqui para autorizar a abertura de um processo contra a Presidente da República. É uma Presidente que cometeu as pedaladas fiscais e gerou um rombo de 60 bilhões, uma Presidente que publicou decretos sem autorização desta Casa, a Casa do Povo, causando um prejuízo de 90 bilhões, uma Presidente que infringiu a Constituição brasileira e que, por isso, vai ser julgada pelo Senado, e, certamente, vai responder pelos crimes de responsabilidade que cometeu. Por isso, é sim ao impeachment!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (195, 'HEITOR JOSE SCHUCH', 'Heitor Schuch', 'PSB', 'RS', 'Sim', 'M', 'Pela retomada do crescimento econômico e social do Brasil, pelo bem do Brasil, meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (196, 'HELDER IGNACIO SALOMAO', 'Helder Salomão', 'PT', 'ES', 'Não', 'M', 'Não posso concordar que o Deputado Eduardo Cunha presida esta sessão. Isso é um absurdo! Ele não tem moral política nem ética para estar aqui. Mas, no dia em que tomei posse, eu fiz um juramento. Jurei defender e respeitar a Constituição. Jurei defender a democracia. Por isso o meu voto é não ao golpe!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (197, 'HELIO LEITE DA SILVA', 'Hélio Leite', 'DEM', 'PA', 'Sim', 'M', 'Com a proteção de Deus e em respeito à minha família, em respeito aos meus amigos, em respeito à minha Castanhal, ao Estado do Pará e ao Brasil, meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (252, 'JOSÉ ALBERTO FOGAÇA DE MEDEIROS', 'José Fogaça', 'PMDB', 'RS', 'Sim', 'M', 'Que o Brasil tenha responsabilidade e sabedoria coletiva paraenfrentar os momentos que virão; o voto é sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (199, 'HERÁCLITO DE SOUSA FORTES', 'Heráclito Fortes', 'PSB', 'PI', 'Sim', 'M', 'Sr. Presidente, quero deixar aqui o meu abraço à minha mulher Mariana; às minhas filhas Marianinha, Heloísa e Camila; aos meus netos Antônio e João; à minha neta que está por vir, Olímpia; à minha irmã Zélia; e à minha Tia Elzamir, com 96 anos. Esse pessoal sabe o que eu sofri nas mãos do PT de 2010 até agora.O voto que eu vou dar não é um voto de rancor, é o voto da lógica, é o voto do futuro do Brasil. O voto que eu vou dar é o voto que o Brasil está exigindo, é o voto em nome das ruas. Portanto, ele é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (200, 'HERCULANO CASTILHO PASSOS JUNIOR', 'Herculano Passos', 'PSD', 'SP', 'Sim', 'M', 'Por São Paulo, pela minha querida cidade de Itu e toda a região, pela minha família, por todos aqueles que confiaram o voto em mim, eu voto sim, Sr. Presidente. Viva o Brasil!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (201, 'HERMES PARCIANELLO', 'Hermes Parcianello', 'PMDB', 'PR', 'Sim', 'M', 'Sr. Presidente, sinto cheiro das mesmas aves de rapina de 54, que levaram Getúlio ao suicídio, mas a força do voto de Cascavel, do Oeste do Paraná, do Noroeste, dos Campos Gerais, dos meus eleitores, dos mais de 150 mil eleitores, do povo do Paraná e do Brasil, meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (202, 'HEULER ABREU CRUVINEL', 'Heuler Cruvinel', 'PSD', 'GO', 'Sim', 'M', 'Por um Brasil melhor e mais decente, pelo futuro de nossos filhos, pelo fim da corrupção e dos fichas-sujas, por minha querida Rio Verde, pelo meu Estado de Goiás, eu voto sim ao impeachment, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (203, 'HIDEKAZU TAKAYAMA', 'Takayama', 'PSC', 'PR', 'Sim', 'M', 'Contra a ladroeira, contra a imposição desse partido de esquerda, que quer transformar este Brasil numa ditadura de esquerda, o meu voto é "sim". Pelo impeachment, pelo Sérgio Moro, pelos evangélicos, pelo meu Brasil, pela minha família, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (204, 'HILDO AUGUSTO DA ROCHA NETO', 'Hildo Rocha', 'PMDB', 'MA', 'Sim', 'M', 'Em respeito aos homens e mulheres do Maranhão que me fizeram Deputado Federal e pelo desenvolvimento do nosso País, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (205, 'HIRAN MANUEL GONCALVES DA SILVA', 'Hiran Gonçalves', 'PP', 'RR', 'Sim', 'M', 'Sr. Presidente, meu querido Brasil, pela minha família; pelos que me fizeram chegar até aqui; pelos médicos do Brasil, para que sejam respeitados pelo próximo governo; pelos maçons do Brasil e pelo bem do povo brasileiro, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (206, 'HISSA NAGIB ABRAHAO FILHO', 'Hissa Abrahão', 'PDT', 'AM', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, houve uma tentativa muito clara no meu Estado e na cidade de Manaus, por parte dos meus adversários, sem eu nunca ter dito o meu voto, de me jogar contra o povo. Eu fui um dos Deputados Federais mais votados da Capital do Amazonas. O meu lado é o do povo. Eu sou homem de coragem e não aceito pressão de nenhum dos lados. Portanto, eu voto com Manaus, voto com o povo. O meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (207, 'HUGO LEAL MELO DA SILVA', 'Hugo Leal', 'PSB', 'RJ', 'Sim', 'M', 'Sr. Presidente, eu gostaria de lembrar a todos os brasileiros e brasileiras que nós temos ainda um País de responsabilidades que espera por nós. Nós não podemos esquecer o Brasil. Eu, nesta sessão especial, quero fazer também uma homenagem póstuma ao saudoso Eduardo Campos, do partido ao qual estou filiado. Nós não vamos desistir do Brasil! Eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (208, 'HUGO MOTTA WANDERLEY DA NÓBREGA', 'Hugo Motta', 'PMDB', 'PB', 'Sim', 'M', 'Sr. Presidente, com o orgulho de representar nesta Casa o povo do meu Estado, a Paraíba, convicto ainda mais da necessidade de uma união nacional depois deste processo, para que o Brasil retome o seu crescimento e o seu desenvolvimento, eu voto sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (209, 'IOLANDA KEIKO MIASHIRO OTA', 'Keiko Ota', 'PSB', 'SP', 'Sim', 'F', 'Sr. Presidente, em nome do meu querido filho, Ives Ota; em nome de todas as mulheres brasileiras; em nome de milhares de mães que perderam os seus filhos em virtude da violência, que clamam por paz, justiça e direitos humanos para todos, declaro o meu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (210, 'IRACEMA MARIA PORTELLA NUNES NOGUEIRA LIMA', 'Iracema Portella', 'PP', 'PI', 'Sim', 'F', 'Exclusivamente por orientação partidária, mas com um sentimento de tristeza, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (211, 'IRAJÁ SILVESTRE FILHO', 'Irajá Abreu', 'PSD', 'TO', 'Não', 'M', 'Pelo Estado de Tocantins; em respeito à democracia; em respeito aos mais de 54 milhões de brasileiros que elegeram democraticamente a Presidente Dilma Rousseff, meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (212, 'IVAN VALENTE', 'Ivan Valente', 'PSOL', 'SP', 'Não', 'M', 'A todo o povo brasileiro, quero dizer que não aceito o corrupto Eduardo Cunha presidindo qualquer processo de impeachment. Ele deveria ser o primeiro impedido. (Manifestação no plenário.) A eleição de Temer, se vingar o impeachment, é uma fraude que deve ser evitada, porque Temer é uma armação para sacrificar os trabalhadores e para abafar a Lava-Jato. Pela democracia e contra o golpe, o PSOL é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (213, 'IZALCI LUCAS FERREIRA', 'Izalci', 'PSDB', 'DF', 'Sim', 'M', 'Em respeito à Constituição; em homenagem à minha família e aos meus eleitores; em homenagem ao aniversário da minha cidade — 56 anos da Capital da República; em homenagem ao povo brasileiro, voto sim. Fora, Dilma! Impeachment já!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (214, 'JACOB ALFREDO STOFFELS KAEFER', 'Alfredo Kaefer', 'PSL', 'PR', 'Sim', 'M', 'Pela liberdade, pela democracia, por um futuro melhor, em honra do povo do Paraná e das pessoas de bem que querem tirar uma oligarquia instalada neste poder, eu voto sim pelo impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (215, 'JAIME MARTINS FILHO', 'Jaime Martins', 'PSD', 'MG', 'Sim', 'M', 'Sr. Presidente, pelos milhares de mineiros que me confiaram a sua representação aqui nesta Casa, mineiros da minha querida Divinópolis; mineiros da minha terra natal, Nova Serrana; de Formiga; de Arcos; pelo povo de Belo Horizonte; na expectativa, Sr. Presidente, de que este seja o início de uma pauta ética, que traga para a vida pública a decência e a moralidade de volta; pela minha família, pelos meus filhos, pela minha esposa, pela minha neta, pelo meu pai, hoje ausente, mas sempre presente na minha vida, pela minha mãe, Dona Maria, os quais me ensinaram os valores que norteiam a minha vida pública, pelos meus irmãos, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (216, 'JAIR MESSIAS BOLSONARO', 'Jair Bolsonaro', 'PSC', 'RJ', 'Sim', 'M', 'Neste dia de glória para o povo brasileiro, um nome entrará para a história nesta data pela forma como conduziu os trabalhos desta Casa: Parabéns, Presidente Eduardo Cunha! Perderam em 1964. Perderam agora em 2016. Pela família e pela inocência das crianças em sala de aula, que o PT nunca teve... Contra o comunismo, pela nossa liberdade, contra a Folha de S.Paulo, pela memória do Cel. Carlos Alberto Brilhante Ustra, o pavor de Dilma Rousseff! Pelo Exército de Caxias, pelas nossas Forças Armadas, por um Brasil acima de tudo, e por Deus acima de todos, o meu voto é sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (253, 'JOSÉ ALBERTO OLIVEIRA VELOSO FILHO', 'Alberto Filho', 'PMDB', 'MA', 'Sim', 'M', 'Sr. Presidente, pela moralidade, pela democracia do nosso País, em nome do povo do meu Estado do Maranhão, em especial da minha cidade de Bacabal, que hoje completa 96 anos, eu voto sim ao prosseguimento do impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (254, 'JOSÉ ALFONSO EBERT HAMM', 'Afonso Hamm', 'PP', 'RS', 'Sim', 'M', 'Em nome do povo gaúcho, povo do meu Estado, em nome do povo brasileiro, para votarmos a favor da mudança, a favor da esperança, sim ao impeachment!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (255, 'JOSÉ ALVES ROCHA', 'José Rocha', 'PR', 'BA', 'Não', 'M', 'Sr. Presidente, pela Constituição que eu jurei cumprir, pela democracia, pelo Estado de Direito e pela minha história política de dez mandatos, voto não ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (218, 'JANETE MARIA GÓES CAPIBERIBE', 'Janete Capiberibe', 'PSB', 'AP', 'Não', 'F', 'Em defesa do voto popular e das regras democráticas, eu voto não a esse impeachment, porque não existe crime. Fora, Cunha!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (219, 'Jarbas de Andrade Vasconcelos', 'Jarbas Vasconcelos', 'PMDB', 'PE', 'Sim', 'M', 'Sras. Deputadas, Srs. Deputados, é evidente que a Presidente da República não só cometeu crimes como as pedaladas, mas sobretudo mentiu ao País. Mentiu ao País quando prometeu uma coisa na campanha e fez outra, exatamente ao contrário. O País está no fundo do poço, quebrado, arrebentado, sem crédito e desmoralizado. Eu voto sim pelo impeachment. Mas quero, Sr. Presidente, dizer do meu desconforto de ter aqui uma pessoa como V.Exa. presidindo esta Casa. V.Exa. agrava, cada vez mais, com o seu expediente aqui, o processo político brasileiro. Ele fica profundamente maculado com a sua presença.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (220, 'JEAN WYLLYS DE MATOS SANTOS', 'Jean Wyllys', 'PSOL', 'RJ', 'Não', 'M', 'Em primeiro lugar, eu quero dizer que eu estou constrangido de participar dessa farsa sexista, dessa eleição indireta, conduzida por um ladrão, urdida por um traidor, conspirador, apoiada por torturados, covardes, analfabetos políticos e vendidos. Em nome dos direitos da população LGBT, do povo negro exterminado nas periferias, dos trabalhadores da cultura, dos sem-teto, dos sem-terra, eu voto não ao golpe. E durmam com essa, canalhas!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (221, 'JEFFERSON ALVES DE CAMPOS', 'Jefferson Campos', 'PSD', 'SP', 'Sim', 'M', 'A palavra de Deus diz: Quando um justo governa, o povo se alegra. Quando um ímpio domina, o povo sofre.  Pelo fim desse Governo injusto que está fazendo o povo sofrer, pela Nação Quadrangular no Brasil, por um pai de 78 anos que me ensinou os princípios da palavra de Deus, pelo meu Estado, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (222, 'JERÔNIMO PIZZOLOTTO GOERGEN', 'Jerônimo Goergen', 'PP', 'RS', 'Sim', 'M', 'Para que meu filho ou minha filha que vão chegar vivam num país de futuro, pelo Rio Grande e pelo Brasil, sim ao impeachment!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (223, 'JESSICA ROJAS SALES', 'Jéssica Sales', 'PMDB', 'AC', 'Sim', 'F', 'Sr. Presidente, primeiramente, quero agradecera Deus, agradecer a meu pai, Wagner Sales, e a minha mãe, Antônia Sales, pois me mostraram desde cedo que o bom político anda ao lado do povo. Portanto, o meu voto é sim. Pelo meu Estado do Acre; pela minha Cruzeiro do Sul,Vale do Juruá, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (224, 'JOÃO ALBERTO FRAGA SILVA', 'Alberto Fraga', 'DEM', 'DF', 'Sim', 'M', 'Sr. Presidente, se 342 votos eu tivesse, 342 votos eu daria para salvar o País dessa corrupção, dessa ladroagem que se chama PT. O meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (225, 'JOÃO CAMPOS DE ARAÚJO', 'João Campos', 'PRB', 'GO', 'Sim', 'M', 'Sr. Presidente, que Deus abençoe o nosso País! Que Deus tenha misericórdia de nossa Nação! Na defesa do Estado Democrático de Direito, contra a corrupção e a impunidade, contra a violência e a insegurança, na defesa da vida e da família, por Goiás, pelo Brasil, por minha família, pela dignidade do povo brasileiro, por esperança, por dias melhores, o meu voto e sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (226, 'JOÃO CARLOS PAOLILO BACELAR FILHO', 'Bacelar', 'PTN', 'BA', 'Não', 'M', 'Em respeito à Constituição, à democracia e ao povo, voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (227, 'JOAO CARLOS BACELAR BATISTA', 'João Carlos Bacelar', 'PR', 'BA', 'Não', 'M', 'Sr. Presidente, com a posição já consolidada aqui no plenário, eu poderia ser oportunista, mas não sou em virtude do meu posicionamento partidário. Meu voto é não, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (228, 'JOÃO CARLOS SIQUEIRA', 'Padre João', 'PT', 'MG', 'Não', 'M', 'Pelo respeito ao voto popular, pela Presidenta Dilma, que não cometeu nenhum crime de responsabilidade, pelos movimentos sociais, pela agricultura familiar, pela reforma agrária, pelo povo que está nas ruas exigindo respeito à Constituição, pelo povo latino-americano, eu voto não. E a luta continua, companheiros!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (229, 'JOÃO CASTELO RIBEIRO GONÇALVES', 'João Castelo', 'PSDB', 'MA', 'Sim', 'M', 'Sr. Presidente, estou há 44 anos na política. Eu sempre disse que político não podia perder duas coisas: a coerência e a dignidade. Este é o meu quinto mandato aqui — fora o de Senador, o de Governador e, recentemente, o de Prefeito de São Luís, minha capital. Eu continuo com a mesma coerência e dignidade, graças a Deus. Voto sim, pelo bem do Brasil!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (230, 'JOAO DERLY DE OLIVEIRA NUNES JUNIOR', 'João Derly', 'Rede', 'RS', 'Sim', 'M', 'Com a minha consciência tranquila, pelo Rio Grande, pelo meu Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (231, 'JOAO FERNANDO PONTUAL COUTINHO', 'João Fernando Coutinho', 'PSB', 'PE', 'Sim', 'M', 'Sr. Presidente, Srs. Deputados, Sras. Deputadas, povo brasileiro, meu voto é pela abertura do processo de impedimento da Presidente Dilma.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (232, 'JOAO FERREIRA NETO', 'Dr. João', 'PR', 'RJ', 'Sim', 'M', 'Sr. Presidente, não sou golpista nem sou fascista. Estou votando aqui em nome do Brasil, do meu Estado do Rio de Janeiro, da minha cidade de São João de Meriti. O meu voto é pela minha família, pelos meus eleitores e pelo meu País. Que Deus nos abençoe! Voto sim ao impeachment!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (233, 'JOAO GUALBERTO VASCONCELOS', 'João Gualberto', 'PSDB', 'BA', 'Sim', 'M', 'Com a convicção de que este Governo da Presidenta Dilma, corrupto e mentiroso, cometeu crime de responsabilidade fiscal e sonhando com um Governo sem corrupção, na esperança de que o próximo Presidente governe para o Brasil e não para o seu partido, aproveitando para homenagear o Ministério Público, a Polícia Federal, o Juiz Sérgio Moro e pedir que se prenda o resto dos corruptos, homenageando Hélio Bicudo, Janaina Paschoal e Miguel Reale Júnior, pela minha querida Mata de São João, pela minha Bahia, pelo Brasil verde e amarelo, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (251, 'JOSÉ AIRTON FÉLIX CIRILO DA SILVA', 'José Airton Cirilo', 'PT', 'CE', 'Não', 'M', 'Sr. Presidente, telespectadores do Brasil, eu dei a minha juventude em defesa da liberdade, da justiça, do direito e pela democracia. Em nome da democracia que conquistamos, em respeito à Constituição brasileira, porque eu não vi aqui os argumentos daqueles que propagaram o voto sim, dizendo a causa dessa admissibilidade. Quase ninguém disse que a acusação contra a Presidente Dilma são as pedaladas fiscais. Todos nós sabemos que isso é uma grande hipocrisia porque todos os governos praticaram pedaladas fiscais. Portanto, a Presidente Dilma não cometeu crime nenhum. Por isso, em nome da democracia, em respeito à Constituição brasileira, em nome da dignidade do povo brasileiro e em respeito à Presidente Dilma, que é uma mulher honrada, séria e comprometida com o povo brasileiro, eu voto não a esse golpe parlamentar.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (234, 'JOAO HENRIQUE HOLANDA CALDAS', 'JHC', 'PSB', 'AL', 'Sim', 'M', 'Quis o povo do meu honrado Estado de Alagoas que eu estivesse aqui, no dia de hoje, para fazer história. Eu sabia da minha responsabilidade, por ter sido o Deputado Federal mais votado daquele Estado; sabia da minha responsabilidade, também, por ser o Deputado mais jovem. E, hoje, com essa responsabilidade na minha mão, de representar os jovens que querem uma política séria, transparente, ética — e também representando o povo brasileiro —, venho aqui me manifestar. Ao contrário do que dizem argumentos falaciosos — e como dizia Schumpeter sobre a democracia participativa —, hoje o Governo tudo o que pregou faz diferente. Sr. Presidente, se nós formos analisar por essa interpretação de regimes, em Cuba tem eleição, na Coreia do Norte tem eleição, no Iraque teve eleição. Portanto, Sr. Presidente, não podemos virar as costas para a opinião pública e fingir que o que povo fala e o que as ruas falam não importam neste momento. Sr. Presidente, Sras. e Srs. Deputados, o Deputado Jhc vota sim pelo impeachment. Viva Alagoas! Viva Maceió!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (235, 'JOÃO JOSÉ DE ARRUDA JÚNIOR', 'João Arruda', 'PMDB', 'PR', 'Sim', 'M', 'Sr. Presidente, há um processo jurídico e político. Pesou a vontade dos paranaenses e também do meu partido. Pela admissibilidade do processo, que continua e será julgado pelo Senado Federal, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (236, 'JOÃO MARCELO SANTOS SOUZA', 'João Marcelo Souza', 'PMDB', 'MA', 'Não', 'M', 'Sr. Presidente, em defesa da nossa frágil democracia, da nossa imatura democracia, eu voto contra o impeachment. Voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (237, 'JOAO PAULO KARAM KLEINUBING', 'João Paulo Kleinübing', 'PSD', 'SC', 'Sim', 'M', 'Sr. Presidente, com a esperança de um futuro melhor, pela brava gente de Santa Catarina e da minha Blumenau, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (238, 'JOAO PAULO TAVARES PAPA', 'João Paulo Papa', 'PSDB', 'SP', 'Sim', 'M', 'Representando aqui a vontade majoritária da minha cidade de Santos, da Baixada Santista, que eu represento, do Estado de São Paulo, e pelo bem do Brasil; o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (239, 'JOÃO RODRIGUES', 'João Rodrigues', 'PSD', 'SC', 'Sim', 'M', 'Sr. Presidente, por minha família, pela minha guerreira Chapecó, pelo meu Estado de Santa Catarina e para quebrar a espinha dorsal dessa quadrilha, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (240, 'JOAO SOMARIVA DANIEL', 'João Daniel', 'PT', 'SE', 'Não', 'M', 'Faço uma homenagem aos grandes homens sergipanos que passaram pelo Congresso Nacional, querido e saudoso ex-Senador José Eduardo Dutra, querido e saudoso ex-Deputado Federal e Governador Marcelo Deda. A classe trabalhadora está nas ruas e não vai aceitar esse golpe de graça. Esse é um golpe contra os pobres, esse é um golpe contra a luta do povo brasileiro, esse é um golpe contra os direitos da classe trabalhadora. Nós votamos com a democracia, nós votamos com a honestidade da Presidenta Dilma. Nós votamos contra o golpe. Não ao golpe! Viva o povo sergipano! Viva o povo brasileiro!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (241, 'JOAQUIM PASSARINHO PINTO DE SOUZA PORTO', 'Joaquim Passarinho', 'PSD', 'PA', 'Sim', 'M', 'Pedindo as bênçãos de Nossa Senhora de Nazaré, pelo meu Estado do Pará, pela minha família, pela minha honra, pelas minhas duas filhas, sim, contra a corrupção!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (242, 'JOHNATHAN PEREIRA DE JESUS', 'Jhonatan de Jesus', 'PRB', 'RR', 'Sim', 'M', 'Sr. Presidente, nem a favor do PMDB nem a favor do PT, com a consciência do povo brasileiro eu voto sim. Eu digo, ao meu Estado de Roraima e aos médicos brasileiros, sim, contra a corrupção.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (243, 'JONY MARCOS DE SOUZA ARAUJO', 'Jony Marcos', 'PRB', 'SE', 'Sim', 'M', 'Sras. e Srs. Deputados, povo do meu grandioso Estado de Sergipe —o menor Estado do Brasil, Estado que eu amo, Estado maravilhoso —, povo brasileiro, o meu partido, o Partido Republicano Brasileiro, do qual eu sou fundador e que é presidido pelo nosso Presidente Marcos Pereira, tomou um posicionamento;e todos nós Deputados decidimos acompanhar o posicionamento, a direção dada pelo nosso Presidente. Eu sou o último Deputado do PRB que vota nesta noite e devo dizer a todos que vou honrar osmeus companheiros e acompanhar o voto de todos os 21 Deputados que me antecederam aqui nesta tribuna. Voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (244, 'JORGE CATARINO LEONARDELI BOEIRA', 'Jorge Boeira', 'PP', 'SC', 'Sim', 'M', 'Pelos princípios que ensinei às minhas filhas da ética, da honestidade, da moral, os quais eu quero elas ensinem aos meus netos; pelo povo de Santa Catarina, que vai às ruas protestar contra a corrupção, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (245, 'JORGE JOSE SANTOS PEREIRA SOLLA', 'Jorge Solla', 'PT', 'BA', 'Não', 'M', 'Pelos trabalhadores, pela população brasileira que está nas ruas se manifestando contra o golpe, pelos companheiros da Bahia que estão mobilizados para não aceitar mais um golpe de estado neste País... Ninguém acredita, nem vocês acreditam que é solução para o Brasil tirar a Dilma e deixar o Temer e Eduardo Cunha. Mas vocês não querem solução para o Brasil. Vocês querem solução para as situações particulares de cada um vocês. É por isto que vocês corruptos se alinharam com a Oposição e rasgaram a Constituição: para fazer um golpe de Estado neste País. Meu voto é não ao golpe.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (246, 'JORGE SILVA', 'Dr. Jorge Silva', 'PHS', 'ES', 'Sim', 'M', 'Sr. Presidente, em respeito à Constituição, em respeito àgrande maioria da população da minha querida cidade de São Mateus, em respeito à grande maioria da população do norte do Estado do Espírito Santo, em respeito à grande maioria da população capixaba, e para que nosso País encontre novamente o rumo do desenvolvimento, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (247, 'JORGE TADEU MUDALEN', 'Jorge Tadeu Mudalen', 'DEM', 'SP', 'Sim', 'M', 'Pela minha família — pela Sandra, pela Érica, pelo Vítor, pelo Jorge e pelo meu neto que está chegando agora —, por Guarulhos, por São Paulo, pelo Brasil, o voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (248, 'JORGE WICKS CÔRTE REAL', 'Jorge Côrte Real', 'PTB', 'PE', 'Sim', 'M', 'Sr. Presidente, que esse meu voto signifique o retorno deste País ao desenvolvimento econômico, gerando emprego, gerando mais renda, gerando condições mais dignas ao brasileiro e à família pernambucana. Gostaria de dedicar o meu voto especificamente aos pernambucanos, que me honraram com o mandato, e à minha família, em especial à minha mulher e aos meus netos. O meu voto é sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (249, 'JORGINHO DOS SANTOS MELLO', 'Jorginho Mello', 'PR', 'SC', 'Sim', 'M', 'Sr. Presidente, pelos meus filhos, Bruno e Felipe, pelo privilégio de ser de Santa Catarina, por entender que corrupção não combina com democracia, por Santa Catarina e pelo Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (250, 'JOSE ADAIL CARNEIRO SILVA', 'Adail Carneiro', 'PP', 'CE', 'Sim', 'M', 'Eu gostaria, incialmente, de pedir licença a todos os Parlamentares desta Casa, ao Sr. Presidente, para reconhecer o trabalho belíssimo que o ex-Presidente Lula fez pelo nosso Brasil, dando oportunidade às pessoas mais pobres, que nada tinham durante governos anteriores. Quero pedir desculpas a ele, ao ex-Governador Cid Gomes, que também fez muito pelo nosso povo cearense, à Presidenta Dilma; ao Governador Camilo Santana, mas eu não posso deixar de atender aos pedidos que chegam a mim, pelas redes sociais (palmas), para que nós demos uma nova oportunidade ao povo brasileiro, tão necessária, diante dessa crise política que levou a uma economia desastrada, desenfreada, desandada. Hoje, por fazer parte do PP, o que muito me orgulha, e por este partido ter fechado questão, eu não poderia emitir meu voto de forma diferente. Meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (256, 'JOSÉ ARNON CRUZ BEZERRA DE MENEZES', 'Arnon Bezerra', 'PTB', 'CE', 'Não', 'M', 'Na dificuldade, nós não podemos nos eximir da responsabilidade. Consciente da minha decisão, em nome da minha família, do povo de Juazeiro, do Crato, de Barbalha, da maioria do Cariri e da maioria do Ceará, em homenagem ao povo nordestino e respeitando a decisão do povo brasileiro, o meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (257, 'JOSÉ AUGUSTO DA SILVA CURVO', 'Tampinha', 'PSD', 'MT', 'Sim', 'M', 'Sr. Presidente, em 1992, eu estava nesta Casa e votei sim, junto com o povo brasileiro. Pelo meu povo honrado de Mato Grosso, pelo Governador Pedro Taques, homem sério e honesto, pela minha família Curvo — meu pai completa 100 anos este ano —, pela minha esposa, meu neto, e em memória dos meus dois filhos falecidos Rodolfo e Roland, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (258, 'JOSE AUGUSTO ROSA', 'Capitão Augusto', 'PR', 'SP', 'Sim', 'M', 'Sr. Presidente, pelo futuro do meu filho, Breno, pela minha família, pela minha cidade de Ourinhos e região, pela minha querida Polícia Militar do Estado de São Paulo, pelo Brasil, pela honestidade e pela ética, em homenagem aos policiais militares que deram a sua vida pela sociedade, eu voto sim,pelo impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (259, 'JOSÉ BENITO PRIANTE JÚNIOR', 'José Priante', 'PMDB', 'PA', 'Sim', 'M', 'Sr. Presidente, o sentimento majoritário que preside em cada brasileiro que acompanha esta sessão histórica que se realiza hoje aqui na Câmara dos Deputados é o sentimento da esperança. Portanto, vou acompanhar a opinião majoritária do povo do meu Estado do Pará. Eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (260, 'JOSÉ CARLOS ALELUIA COSTA', 'José Carlos Aleluia', 'DEM', 'BA', 'Sim', 'M', 'Durante 13 anos, o meu partido, o Democratas, fez oposição ao projeto criminoso implantado por Luiz Inácio Lula da Silva. Em 2007, ele disse que iria extirpar o Democratas da política brasileira. Hoje, nós estamos extirpando Lula e Dilma, e ele vai para a cadeia. Eu estou votando sim pelos crimes que Dilma cometeu e não é só por ter passado cheque sem fundo em nome do povo brasileiro. Ela roubou na refinaria, roubou na PETROBRAS e roubou em Belo Monte. Ela não é honrada. Eu voto sim pelos princípios em respeito à vida, à liberdade e à justiça. Eu voto pelo povo baiano, voto pela minha mulher Maria Luísa, pelos meus filhos e pelos meus netos. Viva o Brasil! Viva a Bahia! Fora, Lula! Fora, Dilma!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (261, 'JOSÉ CARLOS LEÃO DE ARAÚJO', 'José Carlos Araújo', 'PR', 'BA', 'Não', 'M', 'Srs. Deputados, eu já ouvi falar aqui de todas as coisas, mas eu ainda não ouvi falar ainda de conta na Suíça, eu não ouvi falar ainda em recebimento de propinas. É preciso falar aqui, Sr. Presidente... V.Exa. hoje está rindo, mas agora a bola da vez é o senhor. O senhor vai pagar por tudo que fez. Eu estou votando aqui pela Bahia, pelo Brasil, por minha família, por meus filhos, por meus netos, pela minha querida Chapada Diamantina. É por isso que eu digo, Sr. Presidente, não à corrupção, não a dinheiro em contas na Suíça, não a ter contas e dizer que não tem. As contas estão aí. Isso está provado, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (262, 'JOSÉ CARLOS BECKER DE OLIVEIRA E SILVA', 'Zeca Dirceu', 'PT', 'PR', 'Não', 'M', 'Meu voto será uma homenagem aos jovens da década de 60, que lutaram contra a ditadura, uma homenagem aos jovens que, há muitos meses, estão nas ruas, lutando contra esse golpe, uma homenagem aos sindicalistas, aos movimentos sociais, a todos aqueles que sabem que o que está em jogo não é apenas um Governo, não é apenas a figura da Presidenta Dilma, é a democracia. Meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (263, 'JOSÉ CARLOS NUNES JÚNIOR', 'Zé Carlos', 'PT', 'MA', 'Não', 'M', 'Por esta Constituição que eu jurei aqui defender, em defesa da democracia, sou contra um processo que, na sua essência, no seu mérito, jamais deveria ter sido aceito nesta Casa, sou contra ter na Presidência a dupla Eduardo Cunha e Temer, sou contra, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (264, 'JOSE CAVALCANTI ALVES JUNIOR', 'Zeca Cavalcanti', 'PTB', 'PE', 'Não', 'M', 'Companheiros, seria facílimo dar um voto aqui, depois de 355 votos favoráveis ao impedimento da Presidenta da República. Mas, venho de Pernambuco, do Sertão de Pernambuco, da cidade de Arcoverde, e lá endossam, assim como alguns companheiros já assinalaram em seus votos. O meu voto é pela democracia, é pelo Brasil, é por Pernambuco, em especial pelo Sertão de Pernambuco. O meu voto é não ao impedimento.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (265, 'JOSÉ CÍCERO SOARES DE ALMEIDA', 'Cícero Almeida', 'PMDB', 'AL', 'Sim', 'M', 'Sr. Presidente, Srs. Deputados, este momento não estava na minha programação. Eu tenho certeza de que a população alagoana que votou em mim durante os últimos 15 anos não tinha como objetivo que eu participasse deste momento. Mas eu tenho uma gratidão e uma dívida para com Deus, para com o povo alagoano e para com uma senhora que está nos assistindo agora: minha mãe, com 83 anos de idade. Agradeço a Deus, pela vida que me devolveu inúmeras vezes; agradeço à população alagoana, por tudo o que fez por mim durante os últimos 15 anos. Portanto, pela lealdade especialmente ao meu povo e à minha capital, meu voto é sim, pelo impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (266, 'JOSÉ FRANCISCO PAES LANDIM', 'Paes Landim', 'PTB', 'PI', 'Não', 'M', 'Sr. Presidente, desculpe-me a grande maioria, mas os meus limitados conhecimentos jurídicos não me convenceram da existência de crime de responsabilidade por parte da Sra. Presidente, razão por que eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (267, 'JOSÉ GERALDO TORRES DA SILVA', 'Zé Geraldo', 'PT', 'PA', 'Não', 'M', 'Povo brasileiro, povo do Pará, haja coração para aguentar tanta hipocrisia. Pela democracia, pelo desenvolvimento do Brasil, contra os fascistas, os golpistas, os oportunistas e os picaretas desta Câmara, eu sou contra este golpe moderno que querem fazer e atingir a nossa Presidenta Dilma. Covardia daqueles que votam a favor desse impeachment. Bando de covardes!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (268, 'JOSE JUSCELINO DOS SANTOS REZENDE FILHO', 'Juscelino Filho', 'DEM', 'MA', 'Sim', 'M', 'Sr. Presidente, pela minha família, pelos meus amigos e colegas médicos, pelo povo do meu querido Estado, o Maranhão, que me deu a oportunidade de representá-lo hoje neste momento histórico, em especial pela minha querida Santa Inês e por Vitorino Freire, por um futuro melhor para o nosso Brasil, meu voto é sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (269, 'JOSÉ LEONARDO COSTA MONTEIRO', 'Leonardo Monteiro', 'PT', 'MG', 'Não', 'M', 'Quero cumprimentar o Brasil, sobretudo os vales de Minas Gerais. Quero dizer também que não reconheço legitimidade nesse processo. Nós temos um Presidente na Câmara que não tem legitimidade para conduzir um processo como esse, em que não há crime. Se não há crime, é golpe! Mesmo assim, quero saudar todos os trabalhadores e trabalhadoras, do campo e da cidade. Quero cumprimentar, sobretudo os beneficiados da educação. Lá na nossa região, no Estado de Minas Gerais, implantamos universidades federais, institutos federais, escola integral. Quero cumprimentar o Brasil também pela implantação do Programa Minha Casa, Minha Vida e de programas importantes. Aqui, há um circo armado. Isso aqui éuma falseta organizada pelo Presidente Cunha e pelo Michel Temer, para golpear o nosso País, sobretudo os trabalhadores e trabalhadoras mais pobres. Por isso, o meu voto é contra o golpe! Meu voto é contra!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (270, 'JOSÉ LEÔNIDAS DE MENEZES CRISTINO', 'Leônidas Cristino', 'PDT', 'CE', 'Não', 'M', 'Parlamentares aqui presentes, minhas irmãs e meus irmãos cearenses e brasileiros, vejam o que está acontecendo: um réu no Supremo Tribunal Federal está presidindo a sessão para fazer o impeachment de uma Presidente honrada e inocente. Isso não pode acontecer. O Brasil não merece isso. O meu voto é não!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (271, 'JOSE LUIZ STÉDILE', 'José Stédile', 'PSB', 'RS', 'Sim', 'M', 'Os dois lados da rua pedem ética na política. Hoje, nós vamos dar o primeiro passo. Em nome dos desempregados e da indústria nacional, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (272, 'JOSE MARIA MACEDO JUNIOR', 'Macedo', 'PP', 'CE', 'Não', 'M', 'Sr. Presidente, na adversidade é que se conhece o caráter dos homens. Em respeito à minha família e à Constituição e por uma democracia plena no nosso País, eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (273, 'JOSÉ MENDONÇA BEZERRA FILHO', 'Mendonça Filho', 'DEM', 'PE', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, venho de um Estado de tradições libertárias e democráticas. A Presidente Dilma cometeu crime de responsabilidade. A regra constitucional é clara. O povo coloca o Presidente no poder. Quando o Presidente da República comete crime de responsabilidade, cabe aos representantes do povo — portanto, nós — tirar aquele que cometeu o crime do cargo que ocupa como Presidente da República. E nós estamos cumprindo com a nossa obrigação perante a Constituição. Vamos votar sim pelo impeachment da Presidente Dilma Rousseff.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (274, 'JOSÉ MENTOR GUILHERME DE MELLO NETTO', 'José Mentor', 'PT', 'SP', 'Não', 'M', 'Em respeito à Constituição e à soberania popular, em defesa da democracia, da justiça e por ser impossível um impeachment sem crime de responsabilidade; o meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (275, 'JOSÉ NOBRE GUIMARÃES', 'José Guimarães', 'PT', 'CE', 'Não', 'M', 'Sras. e Srs. Parlamentares, brasileiros e brasileiras, povo cearense, é importante, neste momento, em nome dos milhões de pessoas que estão nas ruas — como mostra aquela foto —, em nome dos milhares de cearenses que estão em Fortaleza, em nome dos 54 milhões de votos da Presidenta Dilma, em nome dos mais humildes e daqueles que estão nos assistindo, nós todos dizemos não ao golpe, pela democracia. Meu voto é não, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (276, 'JOSÉ NUNES SOARES', 'José Nunes', 'PSD', 'BA', 'Não', 'M', 'Sr. Presidente, Srs. Deputados e Sras. Deputadas, em homenagem ao povo da minha Bahia, em respeito às instituições democráticas, em respeito à Constituição Federal e para manter a minha coerência política, o meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (277, 'JOSÉ OLIMPIO SILVEIRA MORAES', 'Missionário José Olímpio', 'DEM', 'SP', 'Sim', 'M', 'Sr. Presidente, eu quero, em primeiro lugar, agradecer a Deus, àminha família, aos meus amigos, à minha querida cidade de Itu, à região, ao Estado de São Paulo e ao Brasil. Voto sim, Sr. Presidente!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (278, 'JOSE ORCIRIO MIRANDA DOS SANTOS', 'Zeca do PT', 'PT', 'MS', 'Não', 'M', 'Sr. Presidente, em 1964, quando esta Casa resolveu tomar o mandato do legitimamente eleito João Goulart, Tancredo Neves a este microfone se referiu aos golpistas como traidores, canalhas, canalhas e canalhas! Portanto, em homenagem a Tancredo Neves, absolutamente em defesa da democracia, do Estado de Direito dos mais pobres, dos povos indígenas, dos quilombolas e pela reforma agrária, eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (279, 'JOSÉ OTÁVIO GERMANO', 'José Otávio Germano', 'PP', 'RS', 'Sim', 'M', 'Em homenagem à minha família, aos meus amigos, aos gaúchos e às gaúchas e, especialmente, ao povo de Cachoeira do Sul, o voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (280, 'José Reinaldo Carneiro Tavares', 'José Reinaldo', 'PSB', 'MA', 'Sim', 'M', 'Sr. Presidente, quero pedir desculpas ao meu querido amigo e grande Governador Flávio Dino, pois eu não posso passar por cima da cassação estranhíssima e injusta do Governador Jackson Lago, a quem presto homenagem neste momento. Não posso passar por cima das perseguições e injustiças contra mim. Não posso passar por cima do bloqueio do Governo Federal ao meu Governo. Assim, Governador, a quem admiro e respeito, desculpe, mas o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (281, 'JOSÉ RICARDO ALVARENGA TRIPOLI', 'Ricardo Tripoli', 'PSDB', 'SP', 'Sim', 'M', 'Sr. Presidente, pelos eleitores de São Paulo, do meu Estado, da cidade de São Paulo, lembrando o que me disseram Giovana, Bianca e Bruno, quero agradecer muito os eleitores que aqui me trouxeram e que me recomendaram votar sim pelo impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (282, 'JOSÉ ROBERTO OLIVEIRA FARO', 'Beto Faro', 'PT', 'PA', 'Não', 'M', 'Sr. Presidente, em defesa da nossa democracia, em defesa da Constituição, em defesa do povo pobre do Brasil, que teve no Governo Lula e no Governo da Presidenta Dilma esperança e investimentos, o meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (283, 'JOSÉ SARAIVA FELIPE', 'Saraiva Felipe', 'PMDB', 'MG', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, a decisão unânime do PMDB de Minas Gerais levou em conta, primeiro, o relatório do Deputado Jovair Arantes. Em segundo lugar, pesou muito o descalabro econômico que nós estamos vivendo. Nós temos o recuo das políticas sociais, nós temos a saúde que está em frangalhos, temos uma educação que está cada vez mais com dificuldades, os recursos do FIES estão sendo reduzidos. Então, a pergunta que perpassou a nossa decisão unânime foi: o Brasil dá conta de esperar até 2018? Portanto, o meu voto, que é o voto unânime do PMDB de Minas Gerais, é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (284, 'JOSÉ SARNEY FILHO', 'Sarney Filho', 'PV', 'MA', 'Sim', 'M', 'Sr. Presidente, o PV, há muito tempo, decidiu por unanimidade votar sim ao impeachment. E é desta forma que eu coloco o meu voto. Sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (285, 'JOSÉ SILVA SOARES', 'Zé Silva', 'SD', 'MG', 'Sim', 'M', 'Pela agricultura familiar, pela extensão rural brasileira, pela minha família, por Minas Gerais e pelo Brasil. O outro nome de Minas é liberdade. Eu voto sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (286, 'JOSÉ WELLINGTON ROBERTO', 'Wellington Roberto', 'PR', 'PB', 'Não', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, pela democracia, pelo Brasil, pela nossa Paraíba e sabendo que o impeachment não vai resolver os problemas do nosso País, apesar de defender novas eleições, eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (287, 'JOSÉ WILSON SANTIAGO FILHO', 'Wilson Filho', 'PTB', 'PB', 'Sim', 'M', 'Sr. Presidente, Deputados e Deputadas do Brasil, eu tenho orgulho de neste momento histórico representar o meu Estado, a Paraíba. Em nome de João Pessoa, em nome de todo o Estado eu decidi olhar para o futuro, apostar na boa política e na renovação da esperança do brasileiro. As pedaladas fiscais aconteceram, e nós estamos no momento certo para mudar o Brasil. Está na hora de mudar o Brasil! Eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (288, 'JOSINIANE BRAGA NUNES', 'Josi Nunes', 'PMDB', 'TO', 'Sim', 'F', 'Sr. Presidente, pela legalidade, moralidade e retomada do crescimento para todos, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (289, 'JOSUÉ BENGTSON', 'Josué Bengston', 'PTB', 'PA', 'Sim', 'M', 'Sr. Presidente, por minha família, pela família quadrangular e evangélica em todo o Brasil, pelo Pará, eu voto sim. E feliz é a Nação cujo Deus é o Senhor. Que Deus continue abençoando o povo brasileiro!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (290, 'JOVAIR DE OLIVEIRA ARANTES', 'Jovair Arantes', 'PTB', 'GO', 'Sim', 'M', 'Sr. Presidente, eu disse no meu relatório que o povo do meu Estado de Goiás, que o povo brasileiro, que a juventude brasileira merece uma nova chance. Esta é a nova chance! E peço ao povo brasileiro que, através de seu trabalho, respeite, a partir de agora, um Parlamento que sempre defendeu o povo, que é a Câmara dos Deputados da República Federativa do Brasil. Um abraço! Meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (291, 'JOZIANE ARAUJO NASCIMENTO', 'Jozi Araújo', 'PTN', 'AP', 'Não', 'F', 'Pelo Amapá, pela democracia e pelos votos válidos, eu voto não, senhores.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (292, 'JULIA MARIA GODINHO DA CRUZ MARINHO', 'Júlia Marinho', 'PSC', 'PA', 'Sim', 'F', 'Sr. Presidente, em respeito à minha família, pelo povo brasileiro e do meu querido Pará, e em consideração e respeito à nação evangélica, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (293, 'JÚLIO CÉSAR DE CARVALHO LIMA', 'Júlio César', 'PSD', 'PI', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, contra o desajuste das contas públicas, contra o aumento da inflação, contra o desemprego e a favor dos 10 milhões de desempregados do nosso País, em defesa dos 5.570 Municípios e para restabelecer a esperança do povo do Piauí e do povo brasileiro, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (294, 'JÚLIO CÉSAR DELGADO', 'Júlio Delgado', 'PSB', 'MG', 'Sim', 'M', 'Eu não acho legítimo que um suspeito presida uma sessão como esta. É suspeito pelas irregularidades cometidas aqui nesta Casa. Eu só quero dizer: Cunha, a sua hora vai chegar! Não é por você nem pelos seus golpes que eu vou deixar de votar sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (295, 'JULIO LUIZ BAPTISTA LOPES', 'Julio Lopes', 'PP', 'RJ', 'Sim', 'M', 'Sras. e Srs. Deputados, queria primeiro dar um afetuoso abraço em cada cidadão e em cada cidadã do Estado do Rio de Janeiro pelo difícil momento que atravessa a economia do nosso Estado. Sr. Presidente, Deputado Eduardo Cunha, Sras. e Srs. Deputados, quero aqui me dirigir ao Deputado José Guimarães que me perguntou se eu tinha raiva da Presidente Dilma Rousseff. Eu quero dizer que respeito muito os meus contrários, Deputado Glauber Braga, mas, quanto à Presidente Dilma, eu tenho pena por ela não ter entendido que vale muito mais a pena insistir no empreendedorismo do Brasil, insistir que cada cidadão e que cada cidadã construa sua própria vida do que fazer esse assistencialismo e esse populismo que assaltou o Brasil. Por isso, Sr. Presidente, pelo povo do meu Estado, pelo povo do Rio de janeiro e pelo Brasil, eu voto sim ao impeachment!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (296, 'JUTAHY MAGALHÃES JÚNIOR', 'Jutahy Junior', 'PSDB', 'BA', 'Sim', 'M', 'Em respeito à Constituição, de cuja promulgação eu tive o privilégio de participar, assinando-a, constituinte que fui em 1988, sei que lá estão definidos os crimes de responsabilidade — a Presidente Dilma, através do parecer do Relator Jovair Arantes, muito bem definiu esses crimes. Na certeza de que, sem o movimento popular das ruas do Brasil, nós não teríamos a votação que nós tivemos hoje, em respeito a todos que foram às ruas, aos milhares de brasileiros, em defesa da Bahia e em defesa do Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (297, 'KAIO CESAR DE MOURA MANICOBA NOVAES FERRAZ', 'Kaio Maniçoba', 'PMDB', 'PE', 'Sim', 'M', 'Sr. Presidente, chego ao alto desta tribuna para proferir aqui uma célebre frase do nosso querido Ulysses Guimarães, que disse: A Nação quer mudar. A Nação deve mudar. A Nação vai mudar. Chego aqui com orgulho de ser pernambucano, de ser sertanejo e de ser florestano, para dizer que voto sim ao impeachment!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (298, 'LAERCIO JOSÉ DE OLIVEIRA', 'Laércio Oliveira', 'SD', 'SE', 'Sim', 'M', 'Sr. Presidente, nobres Deputados e Deputadas, a partir daqui nós temos um compromisso muito grande de resgatar o nosso País para o desenvolvimento. Este momento é um ponto de partida. Em nome do Brasil, em nome do meu povo de Sergipe, do meu povo de Aracaju, e na esperança de dias melhores, meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (299, 'LAERTE RODRIGUES DE BESSA', 'Laerte Bessa', 'PR', 'DF', 'Sim', 'M', 'Sr. Presidente, desculpe-me o PR, meu partido, mas, pela minha mãezinha, Melanie, pelas minhas três filhas, pelo meu neto e pelo Brasil, eu voto sim. E que o Brasil esteja comprometido com a segurança pública! Fora, PT!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (300, 'LAUDIVIO ALVARENGA CARVALHO', 'Laudivio Carvalho', 'SD', 'MG', 'Sim', 'M', 'Sr. Presidente, chega de roubalheira no Brasil! Chega de safadeza! Chega de tanta corrupção! Lugar de bandido é na cadeia, não é no Palácio do Governo. Por isso, Sr. Presidente, eu voto sim. Eu voto sim porque não há golpe, há impeachment! Presidente, tchau, querida!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (301, 'LÁZARO BOTELHO MARTINS', 'Lázaro Botelho', 'PP', 'TO', 'Sim', 'M', 'Pelo respeito ao meu Partido Progressista, pelo respeito ao meu Estado do Tocantins e ao Brasil e pela recuperação da economia brasileira, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (302, 'ANTONIO LAZARO SILVA', 'Irmão Lázaro', 'PSC', 'BA', 'Sim', 'M', 'Existe uma frase que se encaixa bem a este momento: A soberba precede à queda. Isso deve servir de exemplo para cada um de nós, porque não há ninguém que aja com soberba que, um dia, não experimente a queda. É muita soberba abrir rombos bilionários nos cofres públicos e achar que não vai acontecer nada. É muita soberba, mas que isso sirva de exemplo para cada um de nós, tanto para os Deputados que compõem a base do Governo quanto para os Deputados da Oposição. A soberba precede à queda. Meu voto é sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (303, 'LEANDRE DAL PONTE', 'Leandre', 'PV', 'PR', 'Sim', 'F', 'Hoje é o dia em que o Brasil vai se reencontrar com a esperança. Pelo povo do Paraná, pelos brasileiros e pelo Brasil que queremos, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (304, 'LEONARDO CARNEIRO MONTEIRO PICCIANI', 'Leonardo Picciani', 'PMDB', 'RJ', 'Não', 'M', 'Sr. Presidente, eu quero fazer dois registros. O Deputado Aníbal Gomes foi submetido a uma grave cirurgia e se encontra na UTI. Por essa razão, não esteve presente a esta sessão. Também quero registrar o nome de cinco Deputados do PMDB do Rio de Janeiro que, em razão do retorno dos titulares esta semana — e só por esta razão —, não estão votando. São eles: Deputado Marquinho Mendes, Deputado Celso Jacob, Deputada Laura Carneiro, Deputado Zé Augusto Nalin e Deputado Wilson Beserra. Sr. Presidente, nos quatro mandatos que tomei posse nesta Casa, jurei cumprir a Constituição. Eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (305, 'LEONARDO LEMOS BARROS QUINTÃO', 'Leonardo Quintão', 'PMDB', 'MG', 'Sim', 'M', 'Sr. Presidente, tranquilamente eu voto por Minas Gerais, pela minha família, pela recuperação do Brasil. Respeitando todos aqui e clamando a benção de Deus para o nosso País, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (306, 'LEOPOLDO COSTA MEYER', 'Leopoldo Meyer', 'PSB', 'PR', 'Sim', 'M', 'O povo do Paraná pede o voto sim. Espero que nós continuemos a ouvir a voz das ruas. O meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (307, 'LINCOLN DINIZ PORTELA', 'Lincoln Portela', 'PRB', 'MG', 'Sim', 'M', 'Sr. Presidente, estou aqui vendo o Deputado Maurício Quintella Lessa ao meu lado. O Partido Republicano Brasileiro, o PRB, em sua bancada, disse que os 22 Parlamentares votariam coerentemente pela admissibilidade da denúncia. Não estamos julgando aqui se a Presidenta da República é honesta ou não. Estamos julgando a admissibilidade de uma denúncia. O Senado o fará. Não há golpe, porque este ato é legítimo, convalidado pelo Supremo Tribunal Federal, ou então eu estou equivocado. Sr. Presidente, eu voto sim, pela admissibilidade.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (308, 'LINDOMAR BARBOSA ALVES', 'Lindomar Garçon', 'PRB', 'RO', 'Sim', 'M', 'Sr. Presidente, em homenagem ao Presidente do meu partido, Mauro Pereira, e em homenagem a Porto Velho, Candeias do Jamari, Itapuã do Oeste, Jorge Teixeira, Alto Paraíso e todo o Estado de Rondônia, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (309, 'LUCAS DE CASTRO SANTOS', 'Lucas Vergilio', 'SD', 'GO', 'Sim', 'M', 'Pela minha filha Manuela que vai nascer, pela minha sobrinha Helena, pelo futuro de todas as crianças e jovens do nosso País, por todos os corretores de seguros do Brasil, em especial por todo o povo goiano, eu voto sim! Viva o Brasil!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (310, 'RODRIGO BATISTA DE CASTRO', 'Rodrigo de Castro', 'PSDB', 'MG', 'Sim', 'M', 'Por um futuro melhor para a nossa gente, precisamos reconstruir o País, arrasado pela quadrilha do PT. Precisamos dar novamente esperança às famílias brasileiras. Pelos exemplos e ensinamentos de meu pai, por meus filhos, por minha família, em memória de Juscelino e Tancredo, em honra à bandeira de Minas Gerais, sim ao impedimento da Presidente da República!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (312, 'LUCIANO DUCCI', 'Luciano Ducci', 'PSB', 'PR', 'Sim', 'M', 'Pelos paranaenses e pelos curitibanos, por um País justo e decente, não vamos desistir do Brasil. Impeachment sim! Viva o Brasil! Viva Curitiba!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (313, 'LUCIO ANTONIO MOSQUINI', 'Lucio Mosquini', 'PMDB', 'RO', 'Sim', 'M', 'Sr. Presidente, pelo povo destemido e pioneiro do Estado de Rondônia, que desbravou aquela região, pelo Brasil, pela minha família e em respeito aos meus eleitores, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (314, 'LÚCIO DUTRA VALE', 'Lúcio Vale', 'PR', 'PA', 'Não', 'M', 'Sr. Presidente, penso que as acusações feitas à Presidente da República são eivadas de falhas e de vícios. Penso, também, que este processo de impeachment é um processo equivocado. Por isso, eu voto contra o impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (315, 'LÚCIO QUADROS VIEIRA LIMA', 'Lucio Vieira Lima', 'PMDB', 'BA', 'Sim', 'M', 'O meu voto é sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (316, 'LUIS ANTONIO FRANCISCATTO COVATTI', 'Covatti Filho', 'PP', 'RS', 'Sim', 'M', 'O meu sangue farroupilha é motivo de orgulho, orgulho porque o nosso Estado defendeu suas ideologias e por elas sempre lutou. Como diz o hino rio-grandense, um povo sem virtude acaba por ser escravo. Honrando esse Rio Grande do Sul, eu voto sim ao impeachment, Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (317, 'LUIZ CARLOS GHIORZZI BUSATO', 'Luis Carlos Busato', 'PTB', 'RS', 'Sim', 'M', 'Sr. Presidente, este é o momento de reescrever a ética e a democracia brasileira. Pela minha família, pela minha Canoas, pelos gaúchos e pelo Brasil, voto sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (319, 'LUIZ ALBUQUERQUE COUTO', 'Luiz Couto', 'PT', 'PB', 'Não', 'M', 'Este ato é um ato ilegítimo, é um golpe, é um estupro contra a democracia. Esse aí é quem deveria sair daí, esse que está presidindo! Nós vamos reagir! A população vai reagir contra esse golpe, contra os traíras, os golpistas, como esses daqui. Parece que a turma que está em torno da corrupção está aumentando. Por isso, em nome da democracia, em nome dos trabalhadores que estão nas ruas, nós vamos reagir e votamos não!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (320, 'LUIS CARLOS HEINZE', 'Luiz Carlos Heinze', 'PP', 'RS', 'Sim', 'M', 'Pelos gaúchos que represento; para combater o projeto de poder e de corrupção do Lula e do PT; e pelos agricultores brasileiros, voto sim pelo impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (321, 'LUIZ CARLOS JORGE HAULY', 'Luiz Carlos Hauly', 'PSDB', 'PR', 'Sim', 'M', 'Em nome do povo brasileiro, por amor a este País, aos paranaenses de Curitiba, Londrina, Cambé, Rolândia, Arapongas e Ibiporã, que é a minha base eleitoral, e sabendo que este Governo não tem maioria no Parlamento — em qualquer Parlamento do mundo, quando não se tem maioria, só se tem um terço, não tem como governar. Na Europa, troca-se o Governo que não tem maioria. Pelos crimes de responsabilidade e de lesa-pátria cometidos pela Presidente Dilma, voto sim, pelo Brasil.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (322, 'LUIZ CARLOS RAMOS', 'Luiz Carlos Ramos', 'PTN', 'RJ', 'Sim', 'M', 'Pelos moradores de rua, que dormem na rua, que nascem na rua e morrem na rua, sem programa social de governo; pela Zona Oeste; Rio da Prata; Bangu, onde morei 33 anos; Campo Grande, em que moro há 31 anos; pelos vendedores, que, quando não têm uma profissão detalhada, é uma opção de mercado, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (323, 'LUIZ CARLOS CAETANO', 'Caetano', 'PT', 'BA', 'Não', 'M', 'Sr. Presidente, povo brasileiro, neste instante, quero dizer ao Brasil que não sou covarde. Quero dizer ao Brasil que voto pelos filhos do povo, pelos filhos do povo da invasão, das ocupações, dos sem-terra, dos sem-teto, pelos trabalhadores de Camaçari, da Bahia, do sertão, do Nordeste, do Brasil. Senhoras e senhores, olhem esta Mesa que está aí: Mesa de traidores, de conspiradores. Eu voto contra; eu voto não; eu voto pelo Brasil; eu voto pelo povo brasileiro; eu voto não, contra V.Exa., Sr. Presidente; eu voto não contra Michel Temer; eu voto a favor do Brasil. Não! Não! Não, contra vocês.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (324, 'LUIZ CLAUDIO PEREIRA ALVES', 'Luiz Cláudio', 'PR', 'RO', 'Sim', 'M', 'Eu gostaria de, hoje, domingo, quando a população brasileira estános assistindo, votar projetos para tirar o Brasil da lama. Agora, em respeito à minha família de Rondônia, aos meus eleitores de Rolim de Moura, de todas as cidades e do campo do Estado de Rondônia, eu vou votar sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (325, 'LUIZ FELIPE BALEIA TENUTO ROSSI', 'Baleia Rossi', 'PMDB', 'SP', 'Sim', 'M', 'Hoje é o dia de devolvermos a esperança ao povo brasileiro. Em respeito à Constituição Federal, em respeito aos mais de 208 mil eleitores que me deram a oportunidade de estar aqui, por São Paulo e pelo Brasil, voto sim. Que Deus abençoe o nosso País!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (326, 'LUIZ FERNANDO RAMOS FARIA', 'Luiz Fernando Faria', 'PP', 'MG', 'Sim', 'M', 'Sr. Presidente, pelos meus familiares, pela minha mulher, pelos meus filhos, pelos meus liderados, que me pediram que votasse, pelos mineiros e pelos demais brasileiros, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (327, 'LUIZ GIONILSON PINHEIRO BORGES', 'Cabuçu Borges', 'PMDB', 'AP', 'Sim', 'M', 'Sr. Presidente, pelo Brasil e pelo meu querido Estado do Amapá, pela minha família, pelos meus amigos, pelos meus eleitores que votaram em mim de forma espontânea, por esse povo maravilhoso do Estado do Amapá, pela paz, pela harmonia e pela concórdia, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (328, 'LUIZ GONZAGA PATRIOTA', 'Gonzaga Patriota', 'PSB', 'PE', 'Sim', 'M', 'Colegas Deputados e Deputadas, o Brasil inteiro assistiu agora a pouco ao impeachment da Presidente Dilma, porque o Brasil está desmantelado. Eu venho defendendo já há algum tempo eleições gerais, para não vermos o Parlamento brasileiro, os Governadores, os Prefeitos sendo olhados pelo povo brasileiro de uma maneira muito ruim. Eu quero dizer aqui que, em nome de Eduardo Campos, não vamos desistir do Brasil. O Brasil é bom. E eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (329, 'LUIZ GONZAGA RIBEIRO', 'Subtenente Gonzaga', 'PDT', 'MG', 'Sim', 'M', 'Sr. Presidente, sou policial militar em Minas Gerais, uma categoria de trabalhadores que vive e morre todos os dias neste Brasil, com mais de 60 mil vítimas anualmente, em consequência da impunidade. Para mim, se a impunidade tem uma mãe, tem uma origem, tem um DNA, essa é a corrupção. Por acreditar em poder contribuir com o combate à corrupção, com o combate à impunidade, em todos os níveis de governo, inclusive, nesta Casa, da direita e da esquerda, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (330, 'LUIZ HENRIQUE MANDETTA', 'Madetta', 'DEM', 'MS', 'Sim', 'M', 'Porque nós temos um País para construir, por causa das famílias, por causa de Campo Grande, a morena mais linda do Brasil, pelo meu Mato Grosso do Sul e pelo Brasil, o voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (331, 'LUIZ HILOSHI NISHIMORI', 'Luiz Nishimori', 'PR', 'PR', 'Sim', 'M', 'Sou um Deputado municipalista e do agronegócio. Portanto, em nome do Paraná, pelo desenvolvimento e progresso do Brasil, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (332, 'LUIZ LAURO FERREIRA FILHO', 'Luiz Lauro Filho', 'PSB', 'SP', 'Sim', 'M', 'Sr. Presidente, deixo claro que o meu posicionamento desde o início era para que houvesse novas eleições, para que o povo, sim, pudesse escolher o futuro do nosso País. Mas, com o sentimento de dever e obrigação, representando os mais de 105 mil eleitores que me colocaram aqui neste meu primeiro mandato, honrando esta confiança que recebi; em nome da minha família — meus pais, que estão aqui em Brasília me acompanhando; minha mãe, Iara; meu pai, Luiz Lauro; minha esposa, Larissa, e o meu filho, que traz também o meu nome, Luiz Lauro Neto —, Sr. Presidente, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (333, 'LUIZ ODORICO MONTEIRO DE ANDRADE', 'Odorico Monteiro', 'PROS', 'CE', 'Não', 'M', 'Sr. Presidente, pela minha geração, que foi às ruas enfrentar a ditadura e o golpe, para que os meus filhos, Daniel e Sofia, só conheçam o golpe pelos livros, em defesa do Sistema Único de Saúde e dos avanços que nós tivemos nos Governos Lula e Dilma, eu sou contra o impeachment sem crime de responsabilidade. Não ao golpe! Não ao impeachment!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (334, 'LUIZ PAULO TEIXEIRA FERREIRA', 'Paulo Teixeira', 'PT', 'SP', 'Não', 'M', 'Ao povo brasileiro, não ao golpe daqueles que querem tirar uma pessoa que teve 54 milhões de votos nas urnas. Esses que querem tirá-la, não têm votos. Contra o ataque à Constituição e à democracia que está sendo feito aqui nesta tarde em Brasília; e contra a conspiração dirigida pelo réu por corrupção no Supremo Tribunal Federal, Eduardo Cunha, junto com Michel Temer e Aécio Neves, com o objetivo de tirar direitos do povo brasileiro, muitos deles conquistados nos Governos Lula e Dilma, voto não, pelo Brasil!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (335, 'LUIZ SÉRGIO NÓBREGA DE OLIVEIRA', 'Luiz Sérgio', 'PT', 'RJ', 'Não', 'M', 'Sr. Presidente, primeiro, quero deixar registrado que nunca em minha vida, em um espaço tão curto, eu ouvi tantas vezes o nome de Deus ser usado em vão, como se fosse um panfleto. Em segundo lugar, em respeito ao voto popular, em respeito à democracia, eu voto não, Sr. Presidente. Golpe não!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (336, 'LUIZA ERUNDINA DE SOUSA', 'Luiza Erundina', 'PSOL', 'SP', 'Não', 'F', 'Pelos que deram a vida pela democracia no Brasil e pelo empoderamento das mulheres, meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (337, 'LUIZIANNE DE OLIVEIRA LINS', 'Luizianne Lins', 'PT', 'CE', 'Não', 'F', 'Pela juventude deste País, pelas mulheres guerreiras, pela população LGBT, pelos quilombolas, pelos trabalhadores do campo e da cidade, pelas pessoas com deficiência, por todos aqueles e aquelas que ousam sonhar, pelo fim da corrupção e para que em nosso País a esperança continue vencendo o medo, meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (338, 'MAGDA MOFATTO HON', 'Magda Mofatto', 'PR', 'GO', 'Sim', 'F', 'Por uma Nação livre, pelos trabalhadores do Brasil, pelo meu querido Estado de Goiás, pela minha querida Caldas Novas, por todos aqueles que confiaram em mim, pelos 120 mil eleitores que puseram o meu nome na urna, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (339, 'MANOEL ALVES DA SILVA JUNIOR', 'Manoel Junior', 'PMDB', 'PB', 'Sim', 'M', 'Sr. Presidente, ecoa nesta Casa o clamor das ruas. A Nação exige mudança. A Nação terá mudança. Contra a corrupção, por mais qualidade na saúde, na educação, na segurança do nosso País; pela honra dos meus eleitores da Paraíba, pelos meus companheiros médicos e da área da Saúde, pelos meus conterrâneos de Pedras de Fogo, pela minha querida João Pessoa, pela Paraíba e pelo Brasil, sim ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (340, 'MARA CRISTINA GABRILLI', 'Mara Gabrilli', 'PSDB', 'SP', 'Sim', 'F', 'Por São Paulo, pelo fim dessa quadrilha que assaltou o País, pelo meu pai, que tanto sofreu na mão do PT, por mais dignidade às pessoas com deficiência, pelo meu povo brasileiro, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (341, 'MARCELO BELINATI MARTINS', 'Marcelo Belinati', 'PP', 'PR', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, povo brasileiro, defendo eleições gerais para a renovação da política do Brasil. De preferência, que nossa população mande de volta para casa todas — sem exceção — essas velhas raposas que estão aí. Voto sim ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (342, 'MARCELO COSTA E CASTRO', 'Marcelo Castro', 'PMDB', 'PI', 'Não', 'M', 'ras. e Srs. Deputados, isso aqui hoje não é uma eleição indireta, é um processo de impeachment de um Presidente da República num País presidencialista. Para isso, pressupõe-se que haja um crime de responsabilidade. A Presidenta Dilma não matou, não roubou, não tem contas no exterior, não descumpriu nenhuma lei do País. Ela é uma pessoa digna, honesta e honrada. Não há crime de responsabilidade, não hácrime nenhum. Todo esse processo é artificial, é forjado, é falso. Por isso, eu voto contra.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (343, 'MARCELO GUILHERME DE ARO FERREIRA', 'Marcelo Aro', 'PHS', 'MG', 'Sim', 'M', 'Sr. Presidente, é importante que as pessoas saibam que eu vim das Minas Gerais, e ninguém vem das Minas Gerais à toa. O Brasil caminha para onde o povo mineiro aponta. Por isso, Sr. Presidente, o meu voto é sim, é sim e é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (344, 'MARCELO HENRIQUE TEIXEIRA DIAS', 'Marcelo Álvaro Antônio', 'PR', 'MG', 'Sim', 'M', 'Sr. Presidente, pela minhas filhas Amanda Dias e Ana Clara, pela minha esposa Janaína, pela minha mãe, pelas famílias de cada um dos brasileiros — quero fazer uma menção especial à minha região do Barreiro, à nossa querida Belo Horizonte —, levando em consideração também a legitimidade dos protestos, as vozes das ruas, a legalidade do processo e a governabilidade do nosso País, eu voto sim, Sr. Presidente. Que Deus abençoe o nosso Brasil! Sr. Presidente, só para corrigir aqui uma situação. Eu quero mandar um abraço. Eu não mencionei o meu filho, Paulo Henrique.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (345, 'MARCELO SQUASSONI', 'Marcelo Squassoni', 'PRB', 'SP', 'Sim', 'M', 'Por você, meu pai, e pela sua história;pelo meu filho de 18 anos e pela juventude do Brasil; pelo Guarujá, minha cidade querida, e pela Baixada Santista, é claro que o meu voto é sim, Sr. Presidente!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (346, 'MARCELO VIVIANI GONÇALVES', 'Marcelo Matos', 'PHS', 'RJ', 'Sim', 'M', 'Sr. Presidente, em nome da minha família, do meu Estado, do meu Município, São João de Meriti, daquele povo, que acredita no Governo Federal, que acredita no nosso mandato; em nome da Baixada Fluminense, eu quero dizer que voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (347, 'MÁRCIO CARLOS MARINHO', 'Márcio Marinho', 'PRB', 'BA', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, em nome do Brasil, em nome da Bahia, em nome de todos os evangélicos, de todos os cristãos do nosso País, em nome do nosso partido, pela coerência do nosso partido e pelo nosso Presidente Marcos Pereira, nós votamos sim, pelo Brasil.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (348, 'MARCIO LUIZ ALVINO DE SOUZA', 'Marcio Alvino', 'PR', 'SP', 'Sim', 'M', 'Sr. Presidente, por todos que acreditaram em mim; pelo Estado de São Paulo; pela minha mãe, ex-Prefeita em Guararema; por Mogi das Cruzes e por toda a região, o meu voto é sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (350, 'MARCO ANTÔNIO FELICIANO', 'Pastor Marcos Feliciano', 'PSC', 'SP', 'Sim', 'M', 'Com a ajuda de Deus, pela minha família, pelo povo brasileiro, pelos evangélicos da Nação toda, pelos meninos do MBL, pelo Vem Pra Rua Brasil — dizendo que o Olavo tem razão, Sr. Presidente, dizendo tchau para essa querida e para o PT, Partido das Trevas —, eu voto sim ao impeachment, Sr. Presidente!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (351, 'MARCO ANTONIO NEVES CABRAL', 'Marco Antônio Cabral', 'PMDB', 'RJ', 'Sim', 'M', 'Sr. Presidente, pelo Brasil, pelo meu Estado do Rio de Janeiro, pela democracia, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (352, 'MARCO ANTONIO TEBALDI', 'Marco Tebaldi', 'PSDB', 'SC', 'Sim', 'M', 'Em nome de Joinville, de Santa Catarina, pelo meu neto Pedro, que nasceu há10 dias, pelo futuro dele e por todas as famílias de bem do Brasil, meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (353, 'MARCO AURÉLIO SPALL MAIA', 'Marco Maia', 'PT', 'RS', 'Não', 'M', 'Pelos trabalhadores e trabalhadoras do nosso Brasil, pela democraciae pelo respeito ao voto soberano do povo brasileiro, que elegeu a Presidenta Dilma com 54 milhões votos, o meu voto é não a esse golpe.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (354, 'MARCOS ABRAO RORIZ SOARES DE CARVALHO', 'Marcos Abrão', 'PPS', 'GO', 'Sim', 'M', 'Por todos aqueles que não têm onde morar, por todos os brasileiros que tiveram os seus sonhos frustrados e por todas as famílias do meu Estado de Goiás — minha filha, você vai crescer num País melhor do aquele onde o seu pai cresceu —, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (355, 'MARCOS BEZERRA RIBEIRO SOARES', 'Marcos Soares', 'DEM', 'RJ', 'Sim', 'M', 'Sr. Presidente, pelo Rio de Janeiro, contra este Governo, que sempre tentou destruir a família. Agora, temos a oportunidade de votar pelo sim. do Brasil. Sim. pelo impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (356, 'MARCOS JOSE REATEGUI SOUZA', 'Marcos Reategui', 'PSD', 'AP', 'Sim', 'M', 'Sr. Presidente, Srs. Parlamentares, o momento é histórico e de um Brasil passado a limpo. Considerando essa situação e a minha origem de delegado da Polícia Federal, eu quero dizer que, no meu Amapá, Justiça e Ministério Público retiveram, sem recolher, mais de 350 milhões de reais devidos ao Fisco. Se nós queremos consertar o Brasil, nós precisamos ir até ao final. Eu estou propondo aqui uma CPI para que nós possamos realmente passar o Brasil a limpo. O voto é sim à CPI e sim também ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (357, 'MARCOS MONTES CORDEIRO', 'Marcos Montes', 'PSD', 'MG', 'Sim', 'M', 'Por você, João Marcos, por você, Felipe, meus queridos netos, esperando um Brasil melhor, por você, Marília, por você, mamãe, pela família uberabense, o meu voto é sim. Pela Frente Parlamentar da Agropecuária, que representa a produção e o emprego neste País, pelo Brasil, por Minas Gerais e pela querida Uberaba e região, o meu voto, Presidente, é sim, com muita responsabilidade.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (358, 'MARCOS ROGÉRIO DA SILVA BRITO', 'Marcos Rogério', 'DEM', 'RO', 'Sim', 'M', 'Sr. Presidente, demonstrada a existência de crime de responsabilidade, contra a corrupção estrutural e sistêmica, que ofende a probidade da administração, contra o desgoverno, a favor dos rondonienses e dos brasileiros, eu voto sim ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (359, 'MARCOS SERGIO ROTTA', 'Marcos Rotta', 'PMDB', 'AM', 'Sim', 'M', 'Sr. Presidente, para que Manaus, o Amazonas e o Brasil voltem a acreditar em um País justo, decente, ordeiro, honesto e, acima de tudo, democrático, voto, com muita satisfação e orgulho, sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (360, 'MARCUS ANTÔNIO VICENTE', 'Marcus Vicente', 'PP', 'ES', 'Sim', 'M', 'Sr. Presidente, em respeito às famílias brasileiras, em respeito ao meu querido Espírito Santo, em respeito à minha cidade natal, Ibiraçu, pelos meus amigos e por toda a minha família, eu voto sim ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (361, 'MARCUS VINÍCIUS CAETANO PESTANA DA SILVA', 'Marcus Pestana', 'PSDB', 'MG', 'Sim', 'M', 'Pelas tradições libertárias das Minas de Tancredo, Itamar, JK e Tiradentes, eu digo não à corrupção, eu digo não à fraude fiscal, eu digo não ao estelionato eleitoral, eu digo não à obstrução da Justiça. Cinquenta milhões de votos não é biombo que autorize o assalto do País. Com a Constituição na mão, com o Brasil no coração, eu digo sim ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (362, 'MARIA AUXILIADORA SEABRA REZENDE', 'Professora Dorinha Seabra Rezende', 'DEM', 'TO', 'Sim', 'F', 'Pela democracia, com tranquilidade, consciência plena, eu voto sim, pelo Tocantins, pelo Brasil e pela minha família.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (363, 'MARIA DA CONCEICAO SAMPAIO MOURA', 'Conceição Sampaio', 'PP', 'AM', 'Sim', 'F', 'Sr. Presidente, gostaria, em primeiro lugar, de agradecer a Deus por este momento e pedir-lhe misericórdia para nossa Nação. Não acho que, no final desta noite, teremos vencidos nem vencedores, mas quero lembrar aqui uma frase de Platão: O juiz não é nomeado para fazer favores com a justiça, mas para julgar segundo as leis. Pelo Amazonas, pelo Brasil, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (364, 'MARIA DO ROSÁRIO NUNES', 'Maria do Rosário', 'PT', 'RS', 'Não', 'M', 'Pela democracia, pela soberania do voto popular, pela dignidade humana, por todos os que lutaram contra a ditadura militar, pelo Deputado Rubens Paiva, cassado e morto pela ditadura, pelas novas gerações, eu voto nãoa este golpe, e sim ao Brasil.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (366, 'MARIA GORETE PEREIRA', 'Gorete Pereira', 'PR', 'CE', 'Abstenção', 'F', 'Pela Constituição brasileira, contra a corrupção do meu País e também respeitando a diminuição das desigualdades sociais, querendo uma eleição nova para este Brasil, eu tenho que me abster, porque não posso acreditar nem em uma chapa nem na outra. Eu me abstenho.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (367, 'MARIA HELENA VERONESE RODRIGUES', 'Maria Helena', 'PSB', 'RR', 'Sim', 'F', 'Por Roraima e pelo povo brasileiro que foi às ruas pedindo mudanças e um Brasil melhor; não podemos desistir do Brasil. Eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (368, 'MARIA MARGARIDA MARTINS SALOMÃO', 'Margarida Salomão', 'PT', 'MG', 'Não', 'F', 'Sras. Parlamentares, Srs. Parlamentares, ouvindo com atenção os oradores que me precederam, eu observei, com espanto, que a maioria dos Deputados que apoiam o impeachmento fazem invocando os seus familiares, os aniversários, a situação das estradas, as coisas mais diversas, inclusive o nome de Deus. Não aludem ao crime de responsabilidade, que seria a causa constitucional para o impeachment, porque esse crime não existe. A Presidenta Dilma não cometeu crime nenhum. Ela é uma mulher decente, íntegra, honesta, que está sendo vítima de uma grande injustiça. Peço aos senhores que me ouçam como eu os ouvi. Eu os ouvi mencionando todas as razões. Eu quero falar, em nome da democracia, em homenagem a todos os que estão nas redes sociais e nas ruas, lutando pela democracia e contra o golpe, que voto contra o golpe, contra os golpistas, contra Eduardo Cunha, contra Michel Temer.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (369, 'MARIANA FONSECA RIBEIRO CARVALHO DE MORAES', 'Mariana Carvalho', 'PSDB', 'RO', 'Sim', 'F', 'Pelo resgate da dignidade,pelos sonhos, pelo resgate da autoestima do povo brasileiro, pela juventude, pelo futuro das novas gerações, pelo povo de Rondônia, pelo Brasil, por Porto Velho, eu digo sim ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (370, 'MARINALDO ROSENDO DE ALBUQUERQUE', 'Marinaldo Rosendo', 'PSB', 'PE', 'Sim', 'M', 'Sr. Presidente, quero dizer a todos os meus amigos Deputados e Deputadas que é um prazer estar aqui, neste primeiro mandato pelo PSB de Pernambuco, para poder ajudar o Brasil a melhorar. O nosso Brasil está sendo visto pelo povo brasileiro como um Brasil que está quebrado, que não tem credibilidade nem aqui nem fora do País. Eu queria mandar um grande abraço aos timbaubenses, da cidade de Timbaúba, do Estado de Pernambuco. Pelo nosso Estado, pelos nossos pernambucanos, pelo nosso Brasil, pelo meu grande amigo de sempre Eduardo Campos, por tudo isso, não vamos desistir do Brasil. Meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (371, 'MARINHA CÉLIA ROCHA RAUPP DE MATOS', 'Marinha Raupp', 'PMDB', 'RO', 'Sim', 'F', 'Pela minha história, pela minha consciência, pela querida e amada população da BR-429 — é o momento também de aqui externar gratidão à Ministra Dilma Rousseff, que tirou aquela população do sofrimento —, pelo meu partido e pela unificação das famílias, dos partidos, da política, do povo de Rondônia, da juventude e das mulheres, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (372, 'MÁRIO LÚCIO HERINGER', 'Mário Heringer', 'PDT', 'MG', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, eu não tenho mais idade para ser rebelde, mas ainda tenho idade para ter esperança. E, neste momento, nós precisamos disso, precisamos de mudança. Por isso, eu vou votar sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (373, 'MARIO SILVIO MENDES NEGROMONTE JUNIOR', 'Mário Negromonte Jr.', 'PP', 'BA', 'Abstenção', 'M', 'Sr. Presidente, infelizmente, não vou poder votar como o meu coração manda. Meu voto é para os meus eleitores da Bahia, em especial, para os de Paulo Afonso, minha cidade natal, e de Glória. Mas, como não posso descumprir uma determinação do meu Partido Progressista, eu me abstenho de votar.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (374, 'MARX BELTRÃO LIMA SIQUEIRA', 'Marx Beltrão', 'PMDB', 'AL', 'Sim', 'M', 'Diante da impossibilidade de novas eleições, em favor dos 10 milhões de desempregados no nosso País; em favor de melhorar a economia do nosso País; em favor da minha querida cidade de Coruripe; em homenagem aos jovens do nosso País, à minha geração, à geração dos meus filhos, à geração que clama por esperança e por dias melhores; pelo bem da Nação e, acima de tudo, pelo bem do meu Estado de Alagoas, eu voto sim pelo impeachment da Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (375, 'MAURÍCIO QUINTELLA MALTA LESSA', 'Maurício Quintella Lessa', 'PR', 'AL', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, meus queridos amigos do Estado de Alagoas, do litoral ao sertão do meu Estado, quero dizer que, quando este processo começou, eu não tinha opinião formada. Eu fui para a Comissão Especial, lá sentei ao lado do Deputado Jovair Arantes, do Deputado Rogério Rosso, do Deputado Carlos Sampaio, de todos aqueles companheiros. Ouvi, com toda a atenção, os denunciantes, dissequei a defesa e fiz meu convencimento. Quero aqui dizer, companheiros, que a Presidente da República não cometeu um crime de responsabilidade: ela cometeu um rol de crimes de responsabilidade. Ela atentou contra o Orçamento do País, ela atentou contra a competência do Congresso Nacional, ela atentou contra a própria democracia. Mesmo tendo o meu partido orientação diferente, eu não podia liderar a maioria da minha bancada, pelo menos, para o precipício. Renunciei à liderança da minha bancada, para chegar a este momento aqui em paz com o meu travesseiro, que é o melhor amigo da minha consciência. Voto sim pelo impeachment! Voto sim pelo afastamento da Presidenta!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (376, 'MAURO MARIANI', 'Mauro Mariani', 'PMDB', 'SC', 'Sim', 'M', 'Para traduzir um sentimento majoritário do povo e da gente de Santa Catarina,e na esperança de que este momento inaugure um novo tempo na política brasileira, o meu voto é sim');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (377, 'MAURO PEREIRA', 'Mauro Pereira', 'PMDB', 'RS', 'Sim', 'M', 'Pelo povo da minha Caxias do Sul, da Serra Gaúcha, do Rio Grande do Sul, do Brasil, pela dignidade e pela esperança do povo brasileiro, eu voto sim. Viva o Brasil! Viva o Sérgio Moro!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (378, 'MAURO RIBEIRO LOPES', 'Mauro Lopes', 'PMDB', 'MG', 'Sim', 'M', 'Prezado Presidente Eduardo Cunha, ocupei o cargo de Ministro de Estado do atual Governo e guardarei a gratidão comigo. Mas, honrando o nosso PMDB com lealdade, na condição de Secretário-Geral do PMDB, junto com o nosso honrado Presidente do partido, Michel Temer, acompanhando também a nossa bancada de Minas Gerais dos Deputados Federais, acompanhando também a bancada do PMDBda Assembleia Legislativa de Minas Gerais, pelo povo de Minas Gerais e também esperando o crescimento do transporte deste País, que está exatamente em uma situação agonizante, eu quero aqui dizer, Sr. Presidente, do fundo da minha alma, pensando na minha família, na minha esposa, nos meus filhos, nos meus netos e nos meus conterrâneos da minha querida Caratinga, que eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (379, 'MAX FREITAS MAURO FILHO', 'Max Filho', 'PSDB', 'ES', 'Sim', 'M', 'Sr. Presidente, Srs. Deputados, em homenagem aos princípios da Constituição Federal, da legalidade, da impessoalidade e, sobretudo, da moralidade, da publicidade e da eficiência, meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (380, 'MIGUEL CORRÊA DA SILVA JÚNIOR', 'Miguel Corrêa', 'PT', 'MG', 'Não', 'M', 'Senhoras e senhores, cidadãos e cidadãs do Brasil, população brasileira que foi às ruas pelas manifestações contrárias à corrupção, especialmente a favor da democracia, saibam que o mais sagrado de um Estado Democrático de Direito é a sua Constituição, e cumprir a sua regra significa votar não ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (381, 'MIGUEL LOMBARDI', 'Miguel Lombardi', 'PR', 'SP', 'Sim', 'M', 'Sr. Presidente, eu vou votar pelas famílias que estão desesperadas, sem emprego, sem seu sustento, pelas famílias que estão com medo de perder o seu emprego, pelo desenvolvimento, pela geração de emprego, pela Nação brasileira, pelo Estado de São Paulo, pela minha consciência, pela minha mãe, pela memória do meu pai, pela minha cidade de Limeira, por ter acreditado em mim, eu voto sim, Sr. Presidente!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (382, 'MIGUEL MOUBADDA HADDAD', 'Miguel Haddad', 'PSDB', 'SP', 'Sim', 'M', 'Sr. Presidente, em respeito aos milhões de brasileiros que foram às ruas pedir o impeachment da Presidente Dilma, representando São Paulo, representando Jundiaí e região, o meu voto é sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (383, 'MILTON ANTONIO CASQUEL MONTI', 'Milton Monti', 'PR', 'SP', 'Sim', 'M', 'Voto pela juventude brasileira, pela esperança de um futuro melhor. Voto sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (384, 'MIRO TEIXEIRA', 'Miro Teixeira', 'Rede', 'RJ', 'Sim', 'M', 'Sr. Presidente, meu voto é sim, porém peço licença aos companheiros para apresentar uma divergência em relação a fundamentações. Nós não queremos confrontos de nenhuma espécie, mas não vamos nos intimidar com as ameaças que ouvimos ao longo das semanas. Saberemos enfrentá-las. Não as desejamos, mas saberemos enfrentá-las e vencê-las. Hoje, aqui, vou decidir sobre um processo. A pauta cuida de um processo, o processo de admissibilidade do impeachment de Dilma Rousseff. Não adianta tergiversar, e, repito, pedindo licença para divergir, reconhecendo o direito de todos falarem o que bem quiser. Hoje o processo aqui é Dilma Rousseff. Aqui temos que julgar o mensalão, inclusive, e começar a voltar no tempo para ver como isso começou. Quanto maior forem as provocações, mais voltaremos no tempo. Vamos ver o petrolão, porque esse processo contra Dilma Rousseff permitirá fortalecer a Lava-Jato e aprofundar as investigações do petrolão. Ninguém aqui tem medo de ninguém.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (385, 'MISAEL ARTUR FERREIRA VARELLA', 'Misael Varella', 'DEM', 'MG', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Parlamentares, em respeito aos eleitores mineiros que confiaram o seu voto a mim para suceder o meu pai, Lael Varella, pela Fundação Cristiano Varella, que é o hospital de câncer, pela transparência contra a corrupção, pelo resgate de novas esperanças e pelo resgate da credibilidade política e social, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (386, 'MOEMA ISABEL PASSOS GRAMACHO', 'Moema Gramacho', 'PT', 'BA', 'Não', 'F', 'Meu Deus! Quanta hipocrisia! Não é Dilma que tem que sair do Palácio, porque Dilma não cometeu crime algum. Quem não tinha que estar nessa cadeira é você, Cunha, você e seus cúmplices, tramando contra Dilma. Vocês não deviam estar mais nessa cadeira! Esses valentes que gritam Fora, Dilma!, não gritam Fora, Cunha!. Por que será? Querem tirar Dilma para salvar Cunha, para se salvarem. Tramam como Temer, no esgoto do Jaburu, tramou para dar rasteira em Dilma. Vocês são uns covardes! Covardes! Pelas trabalhadoras que elegeram a primeira mulher Presidenta da República, pelo povo na rua... Quando os covardes pararem, eu falo. Se eles não pararem, eu vou ficar aqui. Covardes! Covardes! Covardes! Meu voto, pela democracia — fica, Dilma! —, é não. Não ao golpe! Covardes! Covardes');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (387, 'MORONI BING TORGAN', 'Moroni Torgan', 'DEM', 'CE', 'Sim', 'M', 'Contra o golpe, pelo Estado Democrático de Direito, pela Constituição, pela Pátria, pelos maravilhosos cearenses que confiam em mim, eu voto sim, Sr. Presidente!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (388, 'MOSES HAENDEL MELO RODRIGUES', 'Moses Rodrigues', 'PMDB', 'CE', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, diante de falsas promessas, como a refinaria do Estado do Ceará, pelo fim dos coronéis do meu Estado, sustentados pelo Governo da Presidente Dilma, pela dignidade de todos os cearenses e também pelo povo de Sobral, eu sou pela mudança. Eu voto sim, Sr. Presidente!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (389, 'NELSON MARCHEZAN JÚNIOR', 'Nelson Marchezan Junior', 'PSDB', 'RS', 'Sim', 'M', 'Para corresponder, Sr. Presidente, às expectativas dos meus eleitores, dos brasileiros, às expectativas de um Brasil melhor, para combater a corrupção, para possamos olhar para a frente, para um Brasil diferente e separar o que é legal do que é ilegal e pela nossa Constituição, o meu voto só poderia ser, e será, sim ao impeachment da Presidente Dilma.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (390, 'NELSON MARQUEZELLI', 'Nelson Marquezelli', 'PTB', 'SP', 'Sim', 'M', 'Sr. Presidente, o relatório apresentado pelo Líder do PTB, Deputado Jovair Arantes, foi conciso e atendeu aos seus objetivos, tanto é que a Comissão o aprovou. Eu consultei o PTB da minha cidade de Pirassununga, consultei o Estado de São Paulo, o nosso regional, e consultei o nosso Partido Nacional, que fechou questão conosco: por unanimidade de todo o PTB brasileiro, o voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (391, 'NELSON MEURER', 'Nelson Meurer', 'PP', 'PR', 'Sim', 'M', 'Sr. Presidente, pela minha família, pelo meu querido Município de Francisco Beltrão, pelo meu querido sudoeste do Paraná, pelos meus eleitores do meu Estado, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (392, 'NELSON PADOVANI', 'Nelson Padovani', 'PSDB', 'PR', 'Sim', 'M', 'Sr. Presidente, pelo meu Estado, o Paraná, e pelo Brasil, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (393, 'NEWTON CARDOSO JUNIOR', 'Newton Cardoso Jr', 'PMDB', 'MG', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Parlamentares, dirijo-me a toda a população brasileira neste momento, em especial a todas as famílias e a nossa juventude mineira. Quero dizer que, pela retomada do crescimento no nosso País, por um País verdadeiramente melhor e fiscalmente responsável, pela renovação das esperanças nos corações de todos os brasileiros, por Minas Gerais e pelo Brasil, eu, Newton Cardoso Jr, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (394, 'NILSON APARECIDO LEITÃO', 'Nilson Leitão', 'PSDB', 'MT', 'Sim', 'M', 'Meus colegas Deputados e Deputadas de todos os partidos, o Brasil chegou hoje ao juízo final de uma luta de quase 1 ano dizendo que o impeachment tinha que ocorrer. Que Deus ilumine este País! Pela nossa Pátria unida, não a do Brasil de nós e eles, porque o Brasil é um só — ninguém vai nos dividir —, em nome da minha família, do meu Mato Grosso, da minha cidade de Sinop, da nossa Pátria, eu voto sim, pelo Brasil.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (395, 'NILSON PINTO DE OLIVEIRA', 'Nilson Pinto', 'PSDB', 'PA', 'Sim', 'M', 'Sr. Presidente, em respeito à minha mulher, aos meus filhos e aos meus netos, pelo povo do meu querido Estado do Pará, por um futuro melhor para o Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (396, 'NILTO IGNACIO TATTO', 'Nilto Tato', 'PT', 'SP', 'Não', 'M', 'Sr. Presidente, em defesa dos direitos indígenas e dos quilombolas, em defesa da reforma agrária, em defesa da agricultura familiar, em defesa do Minha Casa, Minha Vida, em defesa da Constituição e da democracia, eu sou contra esse golpe tramado pelo Presidente desta Casa e pelo Vice-Presidente Michel Temer.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (397, 'NILTON BALBINO', 'Nilton Capixaba', 'PTB', 'RO', 'Sim', 'M', 'Pelos meus eleitores, pela minha cidade de Cacoal, pela minha família,pelos evangélicos, por meu Estado de Rondônia e pelo futuro do Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (398, 'ODELMO LEÃO CARNEIRO SOBRINHO', 'Odelmo Leão', 'PP', 'MG', 'Sim', 'M', 'Sr. Presidente, o meu desejo é que, amanhã, renasça um novo dia neste País, nos lares de milhares e milhares de brasileiros, mineiros da minha região do Triângulo Mineiro e na minha querida Uberlândia. Que Deus nos abençoe! Amanhã é um novo dia de esperança. Meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (399, 'OLAVO BILAC PINTO NETO', 'Bilac Pinto', 'PR', 'MG', 'Sim', 'M', 'Sras. e Srs. Deputados, o meu voto épelo Brasil, pelos mineiros e, acima de tudo, pelo reencontro da esperança deste País. Eu voto sim, pelo afastamento da Sra. Presidente da República.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (400, 'ONYX DORNELLES LORENZONI', 'Onyx Lorenzoni', 'DEM', 'RS', 'Sim', 'M', 'Diz o hino rio-grandense: Sirvam nossas façanhas de modelo a toda terra. Estamos legitimados pelo povo brasileiro para dizer um basta para a roubalheira. É sim, de esperança num novo futuro para o nosso Brasil.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (402, 'OSMAR GASPARINI TERRA', 'Osmar Terra', 'PMDB', 'RS', 'Sim', 'M', 'Pela minha família, minha esposa, meus filhos, pelas famílias brasileiras, pelas crianças do Brasil, pela minha Santa Rosa, meu povo do Rio Grande, pelo Brasil, é sim, Sr. Presidente!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (403, 'OSMAR JOSÉ SERRAGLIO', 'Osmar Serraglio', 'PMDB', 'PR', 'Sim', 'M', 'Sr. Presidente, pelo País sério que todos nós sonhamos e queremos, por um País sem mensalão, sem petrolão, pelo que nós queremos para o nosso querido Paraná, do Sérgio Moro, para a minha querida Umuarama, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (404, 'OTAVIO SANTOS SILVA LEITE', 'Otavio Leite', 'PSDB', 'RJ', 'Sim', 'M', 'Em primeiro lugar, eu queria deixar claro que, assim como o grande brasileiro Juscelino Kubitschek de Oliveira, eu creio na vitória final inexorável do Brasil como nação. Em nome daqueles que estão nas ruas no Rio de Janeiro, em nome do povo do Rio de Janeiro, em defesa do futuro e da esperança das novas gerações, eu voto sim ao impedimento de Dilma Lana Rousseff.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (405, 'PATRUS ANANIAS DE SOUSA', 'Patrus Ananias', 'PT', 'MG', 'Não', 'M', 'Colegas Deputadas e Deputados, na minha sexagenária caminhada de militante político e social cristão, jamais vi e ouvi tantas afrontas ao segundo, ao quarto e ao sétimo mandamentos da lei de Deus. Quero dizer também, colegas Deputadas e Deputados, que reassumi nesta Casa o mandato que o povo de Minas Gerais me confiou nas urnas, para lutar contra três golpes que as forças mais conservadoras querem impor ao País. Estamos aqui para impedir um golpe, para lutar contra um golpe contra a Presidenta Dilma. Estamos aqui para lutar contra um golpe contra a democracia brasileira. Por último, quero denunciar que a população brasileira vai ser testemunha do golpe contra as políticas sociais. O golpe contra o Bolsa Família. O golpe contra o Minha Casa, Minha Vida. O golpe contra os pobres. Portanto, o nosso voto é não. Não, não passarão, não passarão.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (406, 'PAUDERNEY TOMAZ AVELINO', 'Pauderney Avelino', 'DEM', 'AM', 'Sim', 'M', 'Por uma nova chance para o Brasil, reconhecendo a legitimidade do processo constitucional que estamos movendo, pelo meu Amazonas, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (407, 'PAULO ABI-ACKEL', 'Paulo Abi-Ackel', 'PSDB', 'MG', 'Sim', 'M', 'Sr. Presidente, não há absolutamente nenhum golpe em curso neste País. Há absoluto respeito à Constituição Federal. Por isso, com muita convicção, certo de que estou cumprindo com o meu dever cívico, patriótico, voto sim ao impedimento da Presidente da República.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (408, 'PAULO EDUARDO LIMA MARTINS', 'Paulo Martins', 'PSDB', 'PR', 'Sim', 'M', 'Sr. Presidente, também em nome dos Deputados Edmar Arruda e Valdir Rossoni, pelo povo que foi às ruas do Brasil de verde e amarelo, por um Brasil livre do PT, pelo Paraná, pela República de Curitiba, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (409, 'PAULO FERNANDO DOS SANTOS', 'Paulão', 'PT', 'AL', 'Não', 'M', 'Companheiros e companheiras Parlamentares, povo brasileiro, eu queria discutir um dos princípios fundamentais da Constituição de 1988: o poder emana do povo. E a referência era o velho Ulysses Guimarães, um verdadeiro brasileiro que recepcionou na sua Carta Magna a soberania nacional, a defesa pelo Brasil. Agora vejam a vergonha! A Casa está sendo presidida... pelo sacripanta Deputado Eduardo Cunha, que não tem amor ao Brasil, até porque suas contas não são no Brasil, são na Suíça e no Panamá. É por isso que, independente desse resultado, honrando o povo brasileiro, no sangue e na luta do Zumbi, e agradecendo aos movimentos sociais, a luta continua! Não ao golpe! Não ao golpe!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (410, 'PAULO FERNANDO FEIJO TORRES', 'Paulo Feijó', 'PR', 'RJ', 'Sim', 'M', 'Sr. Presidente, em respeito ao norte e noroeste do Estado do Rio, em respeito àminha querida Campos dos Goytacazes, que está em festa, São Francisco do Itabapoana, Bom Jesus do Itabapoana, Santa Maria Madalena, à minha família, aos ferroviários do Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (411, 'PAULO ROBERTO FOLETTO', 'Paulo Foletto', 'PSB', 'ES', 'Sim', 'M', 'Sr. Presidente, em nome da minha querida cidade de Colatina, de todos os filhos do solo capixaba, do noroeste capixaba, de todo o Espírito Santo; em nome da Nação brasileira; em nome dos meus filhos Sara, Estevão, Giordano e Donato; em nome do meu sobrinho, de toda a minha família e de todos os jovens deste País; em nome de todos esses e em nome do Brasil, com a esperança de que nós possamos construir, com o novo Governo, um diálogo com a política, com a economia e recuperar esta Nação, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (412, 'PAULO ROBERTO FREIRE DA COSTA', 'Paulo Freire', 'PR', 'SP', 'Sim', 'M', 'Sr. Presidente, que Deus tenha misericórdia do nosso País! Por São Paulo, pelo Brasil — e também, Sr. Presidente, em respeito e fidelidade ao meu povo assembleiano, que me trouxe para este Parlamento —, o meu voto é sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (413, 'PAULO ROBERTO GOMES MANSUR', 'Beto Mansur', 'PRB', 'SP', 'Sim', 'M', 'Sr. Presidente, não existe nada mais democrático do que o que estamos fazendo aqui. Eu, pela segunda vez, estou votando o impeachment de um Presidente, e a Presidente Dilma Rousseff vai receber o impeachment desta Casa porque é incompetente administrativamente e porque não tem relação política com o Congresso Nacional. Nós precisamos recuperar o Brasil, e eu tenho certeza de que com o Michel Temer vamos fazer isso. O meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (414, 'PAULO ROBERTO SEVERO PIMENTA', 'Paulo Pimenta', 'PT', 'RS', 'Não', 'M', 'Eu não reconheço legitimidade de Eduardo Cunha para presidir este processo. Não reconheço legitimidade de o conspirador Michel Temer para presidir este País. Não acredito em político demagogo, que fala em combater a corrupção e se alia com corruptos — aquilo que de pior o Brasil tem. Contra o golpe, contra os golpistas, eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (415, 'PAULO SALIM MALUF', 'Paulo Maluf', 'PP', 'SP', 'Sim', 'M', 'Sr. Presidente, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (416, 'PAULO SÉRGIO PARANHOS DE MAGALHÃES', 'Paulo Magalhães', 'PSD', 'BA', 'Não', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, o meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (417, 'PAULO VELLOSO DANTAS AZI', 'Paulo Azi', 'DEM', 'BA', 'Sim', 'M', 'Sr. Presidente, o meu voto homenageia a minha família. O meu voto respeita a vontade dos meus eleitores. Pela minha querida Alagoinhas; pela minha Bahia, que tem sofrido tanto nos últimos anos; pelos brasileiros que trabalham, que produzem e que constroem este País, um País de tanta corrupção — o meu voto também éuma homenagem a um exemplo de administrador público, perseguido pelo Governo Federal e Estadual e que, ainda assim, é considerado o melhor Prefeito do Brasil, o Prefeito de Salvador, ACM Neto —; e pelo futuro do Brasil; é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (418, 'PEDRO FERNANDES RIBEIRO', 'Pedro Fernandes', 'PTB', 'MA', 'Não', 'M', 'Sem demagogias, Sr. Presidente, voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (419, 'PEDRO FRANCISCO UCZAI', 'Pedro Uczai', 'PT', 'SC', 'Não', 'M', 'Sr. Presidente, voto com Lula, o melhor Presidente deste País; com Dilma, mulher valente e honesta; pelo Brasil e pela democracia. Não ao golpe! Fora, golpistas! O voto é não, pelo Brasil.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (420, 'PEDRO OLIVEIRA CUNHA LIMA', 'Pedro Cunha Lima', 'PSDB', 'PB', 'Sim', 'M', 'Na exigência do respeito, que carrego por efeito da confiança em mim, voto pela mudança, no compasso da esperança. Vamos em frente com a força. Voto sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (421, 'PEDRO PAULO CARVALHO TEIXEIRA', 'Pedro Paulo', 'PMDB', 'RJ', 'Sim', 'M', 'Senhoras e senhores de todo o Brasil, com a esperança de ver o País sair da crise, é preciso mudar. Pelo meu Rio de Janeiro, pelo Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (422, 'PEDRO PINHEIRO CHAVES', 'Pedro Chaves', 'PMDB', 'GO', 'Sim', 'M', 'Sr. Presidente, pela bela fundamentação jurídica do relatório do Deputado Jovair Arantes, na expectativa e na confiança do crescimento socioeconômico do nosso País, voto sim, pela admissibilidade.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (423, 'PEDRO TORRES BRANDÃO VILELA', 'Pedro Vilela', 'PSDB', 'AL', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, em respeito ao povo de Alagoas, em defesa da Constituição Federal, em nome da memória do meu avô, minha maior referência, o saudoso Menestrel das Alagoas, Teotônio Vilela (palmas), para renovar a esperança do povo brasileiro, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (424, 'RAFAEL HUETE DA MOTTA', 'Rafael Motta', 'PSB', 'RN', 'SIm', 'M', 'Sr. Presidente, caros colegas, Deputadas e Deputados, a frustração de uma nação é o maior peso que um Parlamentar pode levar na sua consciência. Pelos jovens do Brasil, pelas futuras gerações, pelo Estado do Rio Grande do Norte, por Natal e pelo meu País, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (425, 'RAIMUNDO ANGELIM VASCONCELOS', 'Angelim', 'PT', 'AC', 'Não', 'M', 'Em respeito à Constituição, em defesa da democracia e pelo povo brasileiro, voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (426, 'REGINALDO LÁZARO DE OLIVEIRA LOPES', 'Reginaldo Lopes', 'PT', 'MG', 'Não', 'M', 'Quero convidar o povo brasileiro para permanecer nas ruas, porque esta Legislatura já tem um lugar na história, na história de ser a Legislatura revanchista à Constituição de 1988. Vários que estão aqui acham que a Constituição de 1988 não cabe no capitalismo brasileiro. Por isso, querem rasgar a Constituição. O Presidente Eduardo Cunha transforma esta Legislatura num tribunal de exceção. Querem caçar uma Presidente honesta, querem caçar uma Presidente que não cometeu nenhum crime. Querem, em nome da corrupção seletiva, enriquecer alguns, como o Presidente Eduardo Cunha, que é o beneficiário da corrupção, e a Presidenta Dilma é a vítima desse processo. Não ao golpe! Vamos à luta! Vamos contra os golpistas! Vamos à luta!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (427, 'REJANE RIBEIRO SOUSA DIAS', 'Rejane Dias', 'PT', 'PI', 'Não', 'F', 'Sr. Presidente, demais Deputados e Deputadas aqui presentes, em primeiro lugar, eu oro para que Deus abençoe a nossa querida Nação, o Brasil. Em segundo lugar, pelo desenvolvimento do meu querido Estado do Piauí, pela democracia, pelo Estado Democrático de Direito e em respeito aos milhares e milhares de brasileiros e brasileiras que votaram em Dilma, elegendo-a a primeira Presidenta mulher, eu voto não!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (428, 'REMIDIO MONAI MONTESSI', 'Remídio Monai', 'PR', 'RR', 'Sim', 'M', 'Com a minha consciência, pela minha família, por Roraima e pelo Brasil, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (429, 'RENATA HELLMEISTER DE ABREU', 'Renata Abreu', 'PTN', 'SP', 'Sim', 'F', 'Sr. Presidente, hoje passaremos a limpo o Brasil. A voz do povo ecoará na Casa do Povo. Por São Paulo, pela minha família, pelo meu marido, Gabriel, e pelos meus dois filhos, Felipe e Rafael — eu sou Renata Abreu —, digo sim ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (430, 'RENATO DELMAR MOLLING', 'Renato Molling', 'PP', 'RS', 'Sim', 'M', 'Existe espaço também na política para quem é sério e fala a verdade. Vamos crescer pelo trabalho. O exemplo tem que vir de cima. Pelo Rio Grande, pelo Brasil, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (431, 'RENZO DO AMARAL BRAZ', 'Renzo Braz', 'PP', 'MG', 'Sim', 'M', 'Sr. Presidente, pelo futuro de nossas crianças, pelos meus filhos, Marina, Gilberto Braz e Renzo Braz, por minha mulher, por minha Minas e a favor do Brasil, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (432, 'RICARDO IZAR JUNIOR', 'Ricardo Izar', 'PP', 'SP', 'Sim', 'M', 'Sr. Presidente, por amor ao Brasil, pela memória de meu pai, Ricardo Izar, poramor à minha filha e às futuras gerações, e por todos aqueles que exercem a cidadania na Avenida Paulista, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (433, 'RICARDO JOSÉ MAGALHÃES BARROS', 'Ricardo Barros', 'PP', 'PR', 'Sim', 'M', 'Sr. Presidente, pela unidade do Partido Progressista, que fechou questão em relação ao impeachment, pelos progressistas da minha família: Maria Victoria, Cida Borghetti, Silvio Barros, pelos paranaenses que represento e pela minha Maringá, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (434, 'RICARDO TEOBALDO CAVALCANTI', 'Ricardo Teobaldo', 'PTN', 'PE', 'Não', 'M', 'Sr. Presidente, político que não tem lado, que não tem posição não merece o respeito das pessoas. O meu voto não modifica muita coisa. O placar já mostra o resultado impeachmando a Presidente da República. Mas eu venho de um Estado de grande liderança e de história no País. Desde que começou o processo de impeachment, eu tenho uma posição formada nesta Casa. Vi muitos companheiros mudarem de lado de um dia para o outro, de uma manhã para uma noite. E acho que o povo vai julgar esses que mudaram, que transferiram votos. Seria muito cômodo agora, com um placar desses, eu dizer que votaria "sim". Mas é uma injustiça votar "sim" a este processo contra uma mulher séria, honrada, como a Presidente Dilma. E o meu voto é por uma cidade que tem uma tradição, em Pernambuco, de homens sérios, a minha querida Limoeiro. Já passaram por aqui grandes homens públicos, como Maurílio Ferreira Lima, Carlos Alberto Oliveira, Heráclito do Rêgo. Por Limoeiro, por Pernambuco, pela seriedade, eu voto não ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (435, 'ROBERTO DA SILVA SALES', 'Roberto Sales', 'PRB', 'RJ', 'Sim', 'M', 'Sr. Presidente, pela unidade do PRB, pela família e para nenhum governo se levantar contra a Nação de Israel, por São Gonçalo, pelo leste fluminense, pelo Rio de Janeiro, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (436, 'ROBERTO EGÍDIO BALESTRA', 'Roberto Balestra', 'PP', 'GO', 'Sim', 'M', 'Sr. Presidente, pela coerência de oito mandatos aqui nesta Casa, votando pela segunda vez pelo impeachment; pela minha mãe, que está em casa, já com 93 anos; infelizmente, na ausência do meu pai; pela minha família, esposa, filhos, netos e o primeiro bisneto, eu voto pelo impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (437, 'ROBERTO PEREIRA DE BRITTO', 'Roberto Britto', 'PP', 'BA', 'Não', 'M', 'Pela Bahia e pela democracia, não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (438, 'ROBERTO SALAME FILHO', 'Beto Salame', 'PP', 'PA', 'Abstenção', 'M', 'Sr. Presidente, todos no Pará sabem da minha lealdade e da minha posição contrária ao impeachment. No entanto, em respeito à posição do PP, do meu partido, eu me abstenho.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (439, 'RODRIGO FELINTO IBARRA EPITÁCIO MAIA', 'Rodrigo Maia', 'DEM', 'RJ', 'Sim', 'M', 'Sr. Presidente, V.Exa. entra para a história hoje. Pela minha família, mas principalmente pelo meu pai, Cesar Maia, que quando Prefeito do Rio, foi atropelado pelo Governo do PT — o PT rasga a Constituição no Rio de Janeiro e rasga a Constituição aqui —, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (440, 'RODRIGO GARCIA', 'Rodrigo Garcia', 'DEM', 'SP', 'Sim', 'M', 'Sr. Presidente, ao lado aqui do meu suplente, Deputado Marcelo Aguiar, que comigo representa São Paulo; pelo futuro dos meus filhos, pelo futuro dos filhos de todos os brasileiros, pelo nosso querido Estado de São Paulo e pelo Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (441, 'RODRIGO OTAVIO SOARES PACHECO', 'Rodrigo Pacheco', 'PMDB', 'MG', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Parlamentares, neste processo jurídico e político, a minha decisão se baseia no entendimento de uma parte considerável da comunidade jurídica brasileira, inclusive da respeitadíssima Ordem dos Advogados do Brasil. Em respeito ao meu glorioso e histórico PMDB de Minas Gerais, sobretudo em razão da vontade, uma vontade manifesta, clara e sincera do povo de Minas, terra de Tiradentes e de todos os brasileiros, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (442, 'RODRIGO RODRIGUES DE SOUZA MARTINS', 'Rodrigo Martins', 'PSB', 'PI', 'Sim', 'M', 'Pelo meu Estado do Piauí; pelo futuro do povo do Brasil, em especial, dos piauienses e da cidade de Teresina; em respeito a todos os homens e mulheres de bem que estão lutando, estão nas ruas por um Brasil melhor; pelo futuro das minhas filhas Maria Luísa e Ana Maria; eu voto sim ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (443, 'ROGÉRIO MENDONÇA', 'Rogério Peninha Mendonça', 'PMDB', 'SC', 'Sim', 'M', 'Pelo Brasil; pela cidade de Ituporanga, que me adotou; por Nova Trento, onde eu nasci; por toda Santa Catarina; pela mudança do Estatuto do Desarmamento; pelos nossos agricultores e pelo fim da corrupção no Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (444, 'ROGÉRIO SCHUMANN ROSSO', 'Rogério Rosso', 'PSD', 'DF', 'Sim', 'M', 'Sr. Presidente, meus pais, desde cedo, ensinaram a mim, ao meu irmão e à minha irmã que ninguém nessa vida é melhor do que ninguém. Em homenagem ao ordenamento jurídico brasileiro, que permite a ampla defesa; em homenagem ao Estado Democrático de Direito, que nos permite estar aqui democraticamente; em homenagem à harmonia e à independência entre os Poderes, pilar fundamental do nosso sistema; em homenagem ao povo do Distrito Federal, que recebe todos, sempre, de braços abertos; e à minha família, o meu voto é sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (445, 'ROGÉRIO SIMONETTI MARINHO', 'Rogério Marinho', 'PSDB', 'RN', 'Sim', 'M', 'Pela coerência com os meus eleitores e respeito à minha família, aos meus pais, que me deixaram um legado, e aos meus filhos, a quem eu quero transmitir o legado de respeito ao meu País, contra um partido que aparelhou o nosso País, que se utiliza da política externa nacional para financiar ditaduras bolivarianas sanguinárias com recursos do País, contra aqueles que se utilizam da educação para doutrinar e assediar as nossas crianças, por melhores dias para o nosso País,livre dessa quadrilha que se entranhou em nosso seio, com todo o coração voto sim. Fora, Dilma!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (446, 'RÔMULO JOSÉ DE GOUVEIA', 'Rômulo Gouveia', 'PSD', 'PB', 'Sim', 'M', 'Sr. Presidente, para chegar a esta Casa pedimos confiança à população. No meu Estado da Paraíba, na minha querida Campina Grande, a população inteira pede mudança. A população não aceita este modelo administrativo implantado no País. A minha população, através dos vários segmentos da sociedade, fez com que se decidisse neste instante o futuro do Brasil, que precisa de todos. Precisamos nos unir num Governo de coalizão, para o futuro deste País. Sr. Presidente, em 1952 o jovem Vereador Félix Araújo, em Campina Grande, foi assassinado porque combatia a corrupção. E o que nós estamos votando hoje é o combate à corrupção, é o fim da corrupção. Por isso, Sr. Presidente, em nome da Paraíba, em nome de Campina Grande, voto sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (447, 'RONALDO AUGUSTO LESSA SANTOS', 'Ronaldo Lessa', 'PDT', 'AL', 'Não', 'M', 'Sr. Presidente, Srs. Deputados, chegamos a uma época da vida que achamos que não vai passar por nada mais difícil do que passou. Melhorou, porque a decisão já está tomada, é menos ruim. Ou talvez fosse melhor, se eu tivesse que decidir. Mas falo por dois motivos, Sr. Presidente, rapidamente. Primeiro, pelo clamor da sociedade, que é pela mudança. Por isso, tentei ver se o PDT abria a questão, mas o PDT já tinha fechado, sob a alegação irrefutável, que eu não podia discutir, de que não havia crime de responsabilidade para se tirar a Presidente da República. Isso é inquestionável, principalmente para quem já foi Chefe do Executivo. Portanto, voto não. E voto não também em nome dos nordestinos, do povo do Brasil!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (448, 'RONALDO CARLETTO', 'Ronaldo Carletto', 'PP', 'BA', 'Não', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, apesar de não concordar com a forma como este Governo conduz o País, entendo que, na democracia, se chega ao poder através do voto. Eleição já! Impeachment não!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (449, 'RONALDO FONSECA DE SOUZA', 'Ronaldo Fonseca', 'PROS', 'DF', 'Sim', 'M', 'Sr. Presidente, sem medo de ter esperança e com a convicção de que a Constituição Federal ampara esta sessão; pelo povo brasileiro; pelo Distrito Federal; pela nação evangélica e cristã e pela paz de Jerusalém, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (450, 'RONALDO JOSÉ BENEDET', 'Ronaldo Benedet', 'PMDB', 'SC', 'Sim', 'M', 'Pela minha cidade Criciúma, pela maioria do povo do meu Estado e por uma esperança para o Brasil, voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (451, 'RONALDO MANCHADO MARTINS', 'Ronaldo Martins', 'PRB', 'CE', 'Sim', 'M', 'Pelo povo de Deus, em defesa da família, tão atacada por este Governo, em nome do povo do meu Estado do Ceará, tão enganado por este Governo, pelo meu partido, pelo PRB, que segue unido, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (452, 'RONALDO NOGUEIRA DE OLIVEIRA', 'Ronaldo Nogueira', 'PTB', 'RS', 'Sim', 'M', 'Pelos fundamentos do cristianismo, em defesa dos princípios da administração pública, porque ninguém está acima da lei, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (453, 'RÔNEY TANIOS NEMER', 'Rôney Nemer', 'PP', 'DF', 'Sim', 'M', 'Para que as famílias do Distrito Federal, do entorno e do Brasil voltem a sonhar, voltem a acreditar na geração de emprego, voltem a acreditar que o País tem jeito, eu voto sim, pelo impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (454, 'ROSANGELA DE SOUZA GOMES', 'Rosangela Gomes', 'PRB', 'RJ', 'Sim', 'F', 'Sr. Presidente, pela retomada da economia brasileira, por todos os funcionários públicos e aposentados do Estado do Rio de Janeiro, pelo fim da violência na Baixada Fluminense, pela minha cidade Nova Iguaçu, contra uma saúde ruim, contra um desenvolvimento horrível, voto sim, pelo Brasil, pelo Partido Republicano Brasileiro. Muito obrigada.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (455, 'RUBENS BUENO', 'Rubens Bueno', 'PPS', 'PR', 'Sim', 'M', 'Sr. Presidente, primeiro, quero fazer uma homenagem à bancada do PPS,que na sua totalidade está votando pelo impeachment. Dois Parlamentares não puderam estar aqui para votar pela suplência: o Deputado Raul Jungmann e o nosso Presidente, Deputado Roberto Freire, de São Paulo. Então, deixo aqui a nossa homenagem. Eu disse, em 1992, que eu estava aqui votando em nome do povo de Campo Mourão, que me acolheu, que me recebeu. Repito, Campo Mourão me acolheu e continuo muito grato por isso. Sr. Presidente, temos agora o compromisso maior de fazer com que a Operação Lava-Jato, o Ministério Público, a Polícia Federal e o Juiz Sérgio Moro possam dar a sua contribuição. Por isso, pelo Paraná, pelo Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (456, 'RUBENS OTONI GOMIDE', 'Rubens Otoni', 'PT', 'GO', 'Não', 'M', 'Pelos trabalhadores e trabalhadoras, em defesa da democracia e da justiça social, o meu voto é consciente: é não, contra o golpe.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (458, 'SÁGUAS MORAES SOUSA', 'Ságuas Moraes', 'PT', 'MT', 'Não', 'M', 'Sr. Presidente, pelo Brasil e por Mato Grosso, por tudo o que Lula e Dilma fizeram por este País, pela democracia e contra o golpe, meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (459, 'SAMUEL MOREIRA DA SILVA JUNIOR', 'Samuel Moreira', 'PSDB', 'SP', 'Sim', 'M', 'Sr. Presidente, por absoluto respeito à Constituição, por reformas, pela esperança de um futuro melhor para o nosso País, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (460, 'SANDRO ALEX CRUZ DE OLIVEIRA', 'Sandro Alex', 'PSD', 'PR', 'Sim', 'M', 'Em respeito à minha família e à Constituição brasileira e representando a cidade de Ponta Grossa, a região dos Campos Gerais e o Estado do Paraná, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (461, 'SEBASTIÃO SIBÁ MACHADO OLIVEIRA', 'Sibá Machado', 'PT', 'AC', 'Não', 'M', 'Sr. Presidente, falar aqui em democracia para tentar aplicar um golpe de Estado no nosso Brasil é no mínimo uma hipocrisia. Por aquela trabalhadora que conseguiu ter uma carteira assinada; por aquele trabalhador que conseguiu colocar seu filho numa universidade; por aquele cidadão que conseguiu colocar seu filho em Harvard ou no MIT; pelo trabalhador rural que recebeu energia elétrica na sua casa; pelo fim da hipocrisia, meu voto é não, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (462, 'SEBASTIAO IGNACIO DE OLIVEIRA JUNIOR', 'Sebastião Oliveira', 'PR', 'PE', 'Abstenção', 'M', 'Sr. Presidente, sou um Deputado do Sertão de Pernambuco. Os sertanejos, diferente da região metropolitana, não comungam com a saída da crise através do impeachment. Mas também o povo pernambucano sabe que, em 2014, eu procurei outra opção para o Brasil, que foi acompanhar Marina Silva e Eduardo Campos. Hoje, em respeito ao meu partido, vou me abster do voto.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (463, 'SERGIO BAVINI', 'Sérgio Reis', 'PRB', 'SP', 'Sim', 'M', 'Sr. Presidente, todos aqui presentes, eu acho que neste plenário não tem ninguém que conheça o Brasil como eu. São 58 anos de carreira, em todas as cidades e todos Estados fui recebido com muito carinho. Vi muitas coisas tristes. Perguntaram a mim: O que você vai fazer lá em Brasília?. Eu disse: Vou dedicar 4 anos da minha vida ao meu povo. Hoje é um momento importante para mim, porque nós precisamos mudar esse estágio. Não é possível que nós tenhamos 10 milhões de desempregados, o povo morrendo nos hospitais e ninguém faz nada. Meu voto é sim, pelo PRB.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (464, 'SERGIO DE SOUZA', 'Sergio Souza', 'PMDB', 'PR', 'Sim', 'M', 'Sr. Presidente, em nome do meu partido, o PMDB, da minha cidade de Curitiba, no Paraná, e em nome do povo brasileiro, eu voto sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (465, 'SÉRGIO IVAN MORAES', 'Sérgio Moraes', 'PTB', 'RS', 'Sim', 'M', 'Sr. Presidente, em respeito ao suor e à mão calejada dos meus fumicultores e dos trabalhadores da indústria fumageira do meu Estado, Rio Grande do Sul, eu voto sim. Feliz aniversário, Ana, minha neta!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (466, 'SERGIO LUCIO DE ALMEIDA', 'Tenente Lúcio', 'PSB', 'MG', 'Sim', 'M', 'Por Minas e pelo Brasil, pelo Exército Brasileiro, quero aqui, Sr. Presidente, em nome do grupão de amigos de Uberlândia e região, Alexandre Andrade, em nome da memória de Eduardo Campos, que me trouxe para o PSB, Valentina, dizer que o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (467, 'SÉRGIO LUÍS LACERDA BRITO', 'Sérgio Brito', 'PSD', 'BA', 'Não', 'M', 'Sr. Presidente, o meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (468, 'SERGIO OLIMPIO GOMES', 'Major Olímpio', 'SD', 'SP', 'Sim', 'M', 'Fui dentro do covil dos bandidos, na faixa da posse do Lula, para safá-lo das mãos do Juiz Moro, dizer o que estava entalado na garganta de milhões e milhões de brasileiros! E agora eu vou repetir: Dilma, você é uma vergonha, vergonha, vergonha! Sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (469, 'SERGIO ZVEITER', 'Sergio Zveiter', 'PMDB', 'RJ', 'Sim', 'M', 'Pela responsabilidade que tenho de votar hoje aqui, como advogado, duas vezes Presidente da OAB, e representando o Estado do Rio de Janeiro, eu estudei esse processo e posso afirmar, sem medo de errar, que o parecer do Relator está de acordo com a Constituição, com as leis que regulam o impeachment e com o Regimento Interno desta Casa. Por isso, eu voto sim ao prosseguimento do processo.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (470, 'SHÉRIDAN ESTÉRFANY OLIVEIRA DE ANCHIETA', 'Shéridan', 'PSDB', 'RR', 'Sim', 'F', 'Pelo resgate da esperança que foi roubada do povo brasileiro, por esta geração, pelas próximas gerações, pelo meu Estado de Roraima, eu voto sim. Eu voto pelo Brasil.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (471, 'SILAS CÂMARA', 'Silas Câmara', 'PRB', 'AM', 'Sim', 'M', 'Sr. Presidente, pela reconstrução da unidade de uma Nação que tentaram dividir, por amor e carinho ao povo do Amazonas, pela minha família e, acima de tudo, por amor a Deus, o meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (472, 'SILVIO FRANÇA TORRES', 'Silvio Torres', 'PSDB', 'SP', 'Sim', 'M', 'Sr. Presidente, pelo meu Estado de São Paulo, governado há 20 anos por políticos honestos do meu partido, pela minha região, São José do Rio Pardo, minha terra natal, pelos milhões de brasileiros que foram às ruas clamar por decência na vida pública, por uma nova história do Brasil que vai varrer os corruptos que estão no Governo e onde vão viver os meus filhos e os meus netos. Sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (474, 'SIMÃO SESSIM', 'Simão Sessim', 'PP', 'RJ', 'Sim', 'M', 'Sr. Presidente, saudando as pessoas da Capital e das demais cidades do Estado do Rio de Janeiro que foram para as ruas apoiar esse movimento; para que haja paz, tranquilidade, no Brasil e crescimento da Nação brasileira; por aqueles que me deram dez mandatos consecutivos nesta Casa, fazendo história; pela minha família, pelos meus filhos, Sérgio e Marcelo, pelas minhas noras, pelos meus netos; pela minha querida Baixada Fluminense; pela minha querida cidade de Nilópolis, a velha Nilópolis; por tudo isso voto sim, pelo impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (475, 'SIMONE MARIA MORGADO FERREIRA', 'Simone Morgado', 'PMDB', 'PA', 'Não', 'F', 'Pela defesa da democracia, por considerar que a Presidente Dilma não cometeu nenhum crime, diferentemente de tantos outros denunciados que o cometeram e que envergonham esta Casa, por toda a história do Brasil e pelo povo do Pará, eu digo não ao impeachment!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (476, 'SINVAL MALHEIROS PINTO JÚNIOR', 'Dr. Sinval Malheiros', 'PTN', 'SP', 'Sim', 'M', 'Meu querido Presidente, colegas Deputados, eu sou um dos representantes do Estado de São Paulo. Estou solidário à nossa população brasileira e paulista, pelas suas grandes dificuldades. Na saúde, as Santas Casas estão fechando, endividadas, com médicos e enfermeiros passando por dificuldades. Em relação aos estudantes, o FIES está muito comprometido, e muitos bons estudantes não conseguem estudar. Quanto ao desemprego, o setor sucroalcooleiro da nossa região, que é um setor que sempre gerou emprego, hoje está em grande dificuldade. Em decorrência de todos esses fatores, eu acho que temos que dar ânimo e uma nova esperança para o povo brasileiro. Votamos sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (477, 'SORAYA ALENCAR DOS SANTOS', 'Soraya Santos', 'PMDB', 'RJ', 'Sim', 'F', 'Sr. Presidente, por acreditar que a política, a ética e a responsabilidade devem caminhar juntas; por andar por este Brasil afora e ver milhões de brasileiros nas ruas vestindo verde-amarelo, de forma pacífica, ordeira, mostrando que os seus filhos não fogem à luta — não precisam invadir casa de ninguém, não precisam quebrar ônibus nenhum —, pedindo união e pregando esperança por um Brasil melhor; por minha família, e neste momento eu me faço representar pelos meus netos, Arthur, Sofia e a mais nova, a caçula, que está chegando; eu quero dizer aos brasileiros, à minha cidade de Niterói e ao meu Estado do Rio de Janeiro que voto sim. Impeachment já!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (478, 'SOSTENES SILVA CAVALCANTE', 'Sóstenes Cavalcante', 'DEM', 'RJ', 'Sim', 'M', 'A negociata da velha política impediu um suplente do meu partido, o DEM, Marcelo Delaroli, de votar aqui hoje. Por você, Marcelo; pelos evangélicos do meu Estado; pelos movimentos sociais, Movimento Brasil Livre —MBL, Vem Pra Rua, Revoltados Online e todos os outros que ocuparam as ruas deste País; por um País mais altivo, livre da corrupção de todos; por Lula e Dilma na cadeia; voto sim ao impeachment, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (479, 'STEFANO AGUIAR DOS SANTOS', 'Stefano Aguiar', 'PSD', 'MG', 'Sim', 'M', 'Liliene, meu amor, esse voto é pelo Lorenzo, nosso filho, pelas futuras gerações, pelas crianças do Brasil. Quero homenagear aqui os 3 milhões de guerreiros e guerreiras da família, da nação quadrangular, que há 65 anos contribui e ora por um Brasil melhor. Existe um provérbio que diz: Minas Gerais é a terra do ouro e do diamante e o mineiro em silêncio segue o passo de um gigante. Mas este mineiro aqui não vai ficar em silêncio. Não há golpe! Impeachment! Meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (480, 'TANIA RAQUEL DE QUEIROZ MUNIZ', 'Raquel Muniz', 'PSD', 'MG', 'Sim', 'F', 'Sr. Presidente, o meu voto é em homenagem às vítimas da BR-251. O meu voto é para dizer que o Brasil tem jeito, e o Prefeito de Montes Claros mostra isso para todos nós com a sua gestão. O meu voto é por Tiago, David, Gabriel, Mateus, minha neta Júlia, minha mãe, Elza. Meu voto é pelo norte de Minas, é por Montes Claros, é por Minas Gerais, é pelo Brasil. Sim, sim, sim!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (481, 'TEREZA CRISTINA CORREA DA COSTA DIAS', 'Tereza Cristina', 'PSB', 'MS', 'Sim', 'F', 'Pelo meu Mato Grosso do Sul, por todos que foram às ruas pedir este momento, pelos agricultores, pela minha família e por uma nova esperança para o Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (482, 'THIAGO MELLO PEIXOTO DA SILVEIRA', 'Thiago Peixoto', 'PSD', 'GO', 'Sim', 'M', 'Sr. Presidente, impeachment não é golpe. Toda democracia sólida possibilita o impeachment. O impeachment seráo primeiro passo para o resgate do nosso País, para o fim do retrocesso. Pelo meu Estado, Goiás, pela minha esposa, Paula, eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (483, 'ULDURICO ALENCAR PINTO', 'Uldurico Junior', 'PV', 'BA', 'Sim', 'M', 'Sr. Presidente, nada como a consciência limpa; consciência limpa por saber e ter a certeza de que houve, sim, crime de responsabilidade; consciência limpa de quem sabe e tem guardado no coração a certeza de que não se vendeu por cargos nem por dinheiro nenhum; consciência de quem sabe e acredita que nós podemos, sim, mudar o nosso País. Em nome do extremo sul da Bahia e em nome do Brasil, o meu voto é a favor da Bahia, a favor do meu País, sim ao impeachment da Presidenta Dilma.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (484, 'VALDIR COLATTO', 'Valdir Colatto', 'PMDB', 'SC', 'Sim', 'M', 'Por todos aqueles brasileiros que foram enganados por este Governo, por Santa Catarina, pelo meu oeste catarinense, por Xanxerê, por Chapecó, por todos aqueles que produzem e trabalham neste Brasil, pelos nossos agricultores que nos alimentam todos os dias, porque se o agricultor não planta, ninguém almoça, nem janta, voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (485, 'VALMIR CARLOS DA ASSUNÇÃO', 'Valmir Assunção', 'PT', 'BA', 'Não', 'M', 'Povo brasileiro, no dia 17 de abril de 1996, 21 sem-terra foram assassinados no Pará. Quem era Presidente do Brasil? Fernando Henrique Cardoso, do PSDB. Quem era Governador do Pará? Almir Gabriel, do PSDB. E 20 anos se passaram, ficando impunes aqueles que assassinaram nossos companheiros. Por isso, Sr. Presidente, em homenagem àqueles que lutaram, deram a sua vida pela reforma agrária; em homenagem ao Movimento dos Trabalhadores Rurais Sem Terra, ao Movimento dos Trabalhadores Sem Teto e à população negra deste País; em homenagem a quem vive nas favelas; em homenagem àqueles e àquelas que não vão ficar com a marca de golpistas na cara, que nem óleo de peroba vai tirar; em homenagem ao Capitão Lamarca; em homenagem a Marighella, a Zumbi e a Dandara; em homenagem ao povo brasileiro, à minha Bahia, a Itamaraju, ao PT, a Lula e a Dilma; é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (486, 'VALMIR PRASCIDELLI', 'Valmir Prascidelli', 'PT', 'SP', 'Não', 'M', 'Sr. Presidente, por direitos iguais a todos os brasileiros, com a minha consciência absolutamente tranquila, diferentemente de muitos aqui, mas com o coração cheio de esperança de que a luta pelo Brasil melhor vai continuar. Pela democracia, contra o golpe, eu voto não!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (487, 'VALTENIR LUIZ PEREIRA', 'Valtenir Pereira', 'PMDB', 'MT', 'Não', 'M', 'Sr. Presidente, nobres pares, eu vou votar de acordo com a ordem jurídica. Se chegar a este Plenário processo de impeachment contra o Vice-Presidente Michel Temer com o mesmo fundamento deste processo de impeachment, eu já adianto: vou votar contra. Então, Sr. Presidente e nobres pares, meu voto é não ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (488, 'VANDER LUIZ DOS SANTOS LOUBET', 'Vander Loubet', 'PT', 'MS', 'Não', 'M', 'Sr. Presidente, impeachment sem crime é golpe, e a Presidente Dilma não cometeu crime. Pela democracia e pela liberdade, eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (489, 'VANDERLEI MACRIS', 'Vanderlei Macris', 'PSDB', 'SP', 'Sim', 'M', 'Sr. Presidente, meus caros colegas, meu voto é contra o Governo, que institucionalizou a corrupção neste País. Quero fazer homenagem aqui aos brasileiros de bem, àqueles milhões que foram às ruas para reivindicar mudanças, e mudança é o impeachment. Pela Constituição, pelo Brasil, pelo meu Estado de São Paulo, voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (490, 'VENEZIANO VITAL DO RÊGO SEGUNDO NETO', 'Veneziano Vital do Rêgo', 'PMDB', 'PB', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Parlamentares, com equilíbrio, com moderação, como este momento paroxístico para o País e para todos nós nos exige, com responsabilidade jurídica e com consciência política dos nossos votos, a minha posição é favorável ao prosseguimento do processo de impedimento da Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (491, 'VICENTE ALVES DE OLIVEIRA JUNIOR', 'Vicentinho Júnior', 'PR', 'TO', 'Não', 'M', 'Sr. Presidente, falar em moralidade, falar em boa política é tão fácil neste microfone! Gosto de ver a prática no meu Estado do Tocantins. Venho de um Estado em que, por duas vezes na sua história, tivemos a tristeza de ver dois governos cerceados no meio de um mandato. O primeiro, por sinal, é um Governador do PMDB cassado, que, não satisfeito com isso, novamente reincidena Suprema Corte, em Brasília. Na segunda vez... Não temo gritaria. Respeito a todos. E outro, pela segunda vez, afastado. Eu sei, na prática, o que é isso para o povo, para as instituições. Em respeito ao voto popular, em respeito à segurança das instituições, eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (492, 'VICENTE CÂNDIDO DA SILVA', 'Vicente Candido', 'PT', 'SP', 'Não', 'M', 'Sr. Presidente, em respeito à Constituição brasileira; em homenagem aos jovens trabalhadores que ocuparam as ruas do Brasil, nos últimos dias, pregando a decência, contra o ódio e contra o rancor; em homenagem aos trabalhadores que um dia decidiram construir a história com as suas próprias mãos e fizeram do seu instrumento de luta política o trabalho, que é o Partido dos Trabalhadores, eu voto contra o impeachment da Presidente Dilma.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (493, 'VICENTE FERREIRA DE ARRUDA COELHO', 'Vicente Arruda', 'PDT', 'CE', 'Não', 'M', 'Voto não ao impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (494, 'VICENTE PAULO DA SILVA', 'Vicentinho', 'PT', 'SP', 'Não', 'M', 'Eu não sou corrupto, não faço conchavo, não sou traidor da classe trabalhadora, nem oportunista. Em nome da família da classe operária, do meu povo negro, dos quilombolas, dos trabalhadores do campo e da cidade, em nome da dignidade da Presidenta Dilma, eu não aceito este conluio espúrio. Por isso, voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (495, 'VICTORIO GALLI FILHO', 'Professor Victório Galli', 'PSC', 'MT', 'Sim', 'M', 'Sr. Presidente, em nome da minha família, em nome do meu Estado de Mato Grosso, em nome do Brasil, em nome do povo cristão, que detesta a corrupção, o meu voto é sim. Já dizia Olavo de Carvalho, na década de 90, que o PT daria no Brasil PT — Perda Total.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (496, 'VINICIUS DE AZEVEDO GURGEL', 'Vinicius Gurgel', 'PR', 'AP', 'Abstenção', 'M', 'Por novas eleições, porque trocar seis por meia dúzia não resolve, eu me abstenho.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (497, 'VINICIUS RAPOZO DE CARVALHO', 'Vinicius Carvalho', 'PRB', 'SP', 'Sim', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, povo brasileiro, este Governo errou ao apostar na desordem. Este Governo errou ao não acreditar no progresso deste País. E nós nesta Casa, com mais de 95 milhões de votos dos Deputados, temos, sim, Sr. Presidente, legitimidade para poder dar fim a este Governo. Em nome do povo brasileiro, em nome do Estado de São Paulo, que me acolheu muito bem, em nome do PRB, meu partido, especialmente da minha esposa e da minha filha, que tem 20 anos, as minhas principais eleitoras, que sabem da dignidade do meu trabalho, o nosso voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (498, 'VITOR LIPPI', 'Vitor Lippi', 'PSDB', 'SP', 'Sim', 'M', 'Sr. Presidente, em nome de todos os moradores de Sorocaba e da nossa querida região, em nome da minha família, em nome daquelas pessoas que estão esperando atendimento nas portas dos hospitais deste País, contra a corrupção, contra a mentira, contra o populismo, a favor da volta dos empregos neste País e pelo Brasil, voto simpelo afastamento da Presidente Dilma.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (499, 'VITOR PEREIRA VALIM', 'Vitor Valim', 'PMDB', 'CE', 'Sim', 'M', 'Para que a esperança possa vencer o medo, em nome de mais de 10 milhões de brasileiros desempregados, em nome do meu Estado do Ceará e dos verdadeiros golpistas que não cumpriram a promessa da refinaria nem do metrô, em nome do meu povo de Fortaleza — um abraço a todos vocês que estão na Praça Portugal —, meu voto é sim. Fora, PT!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (500, 'WADIH NEMER DAMOUS FILHO', 'Wadih Damous', 'PT', 'RJ', 'Não', 'M', 'Sr. Presidente, em homenagem aos milhares de advogados e juristas deste País que apontaram que esse relatório é uma farsa, é um instrumento de golpe; em homenagem ao povo trabalhador que hoje está nas ruas resistindo contra o golpe; e dando razão a V.Exa. quando pediu a Deus que tenha misericórdia deste País, e tem que ter mesmo, porque V.Exa. é o chefe da corrupção neste País, presidindo um tribunal que vai julgar uma mulher honrada — não se iluda, Sr. Presidente, com esse seu sorrisinho, V.Exa. é a bola da vez no Supremo Tribunal Federal —; meu voto é não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (501, 'WALDENOR ALVES PEREIRA FILHO', 'Waldenor Pereira', 'PT', 'BA', 'Não', 'M', 'Brasileiros, povo da Bahia, esta sessão será reconhecida no futuro como a maior farsa da história política brasileira. Uma Presidenta honesta, honrada, eleita democraticamente pelo voto popular está sendo julgada por um tribunal de exceção, um tribunal cujo Presidente e mais de 50% dos seus membros são indiciados pelo Ministério Público ou são réus do Supremo Tribunal Federal. Em homenagem aos homens e mulheres do Brasil que lutaram e lutam pela democracia, eu voto não. Eu voto não, contra o golpe!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (502, 'WALDIR MARANHÃO CARDOSO', 'Waldir Maranhão', 'PP', 'MA', 'Não', 'M', 'Sr. Presidente, Sras. e Srs. Deputados, todos nós estamos emocionados. Todos nós estamos atentos, julgando a nós próprios, respeitando consciências. Eu quero dizer que o Partido Progressista firmou questão. Mas, em desacordo com a posição do meu partido, eu quero dizer, meu Presidente querido, que continuarei sendo leal à sua pessoa, como Presidente desta Casa. E quero dizer ao meu Governador, do Estado do Maranhão, Flávio Dino, que sonhou com a mudança, que vai transformar o nosso Estado... Em defesa da Constituição, em defesa da democracia, voto não ao golpe, contra o impeachment.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (503, 'WALDIR SOARES DE OLIVEIRA', 'Delegado Waldir', 'PR', 'GO', 'Sim', 'M', 'Pátria amada, Pátria amada, seu filho Delegado Waldir não foge à luta. Por ti, Goiânia querida, por ti, Goiás, pelo meu País, por Deus, por minha família, pelas famílias e pelas pessoas de bem, o meu voto é sim. Fora Dilma! Fora Lula! Fora PT!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (504, 'WALTER PEREIRA ALVES', 'Walter Alves', 'PMDB', 'RN', 'Sim', 'M', 'Sr. Presidente, na esperança de dias melhores, em nome do povo do meu Estadodo Rio Grande do Norte, que me concedeu 191 mil votos, em nome do povo do Brasil, eu voto sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (505, 'WANDERLEY ALVES DE OLIVEIRA', 'Deley', 'PTB', 'RJ', 'Sim', 'M', 'Sr. Presidente, observando, nesses meus quatro mandatos, a intolerância, o ódio e, às vezes, até a falta de respeito que vi nesta Casa, lembrei-me de uma frase da época que ainda jogava futebol se não me engano, de Tancredo Neves: Briguem as ideias, não briguem os homens. Que o nosso dia possa terminar bem! Eu voto sim, Sr. Presidente.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (506, 'WASHINGTON REIS DE OLIVEIRA', 'Washington Reis', 'PMDB', 'RJ', 'Sim', 'M', 'Sr. Presidente, que a partir de amanhã, segunda-feira, Deus possa derramar muitas bênçãos sobre o nosso Brasil e sobre o povo brasileiro. Sr. Presidente, voto a favor.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (507, 'WELINGTON COIMBRA', 'Lelo Coimbra', 'PMDB', 'ES', 'Sim', 'M', 'Presidente, em homenagem e em resposta aos milhares de brasileiros que, pelas ruas do País, do meu Estado do Espírito Santo, das cidades do meu Estado, em especial a minha querida Vitória, que nos cobram, nos exigem e nos chamam para responder afirmativamente contra a corrupção, que nos chamam para responder afirmativamente pelo restauro da economia e que nos chamam para responsabilizar aquela que, ao longo desses anos, inviabilizou a economia, nós precisamos responsabilizar a Presidente neste momento, com o ato que aqui estamos praticando. E esse ato tem uma resposta. A resposta é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (508, 'WELITON FERNANDES PRADO', 'Weliton Prado', 'PMB', 'MG', 'Sim', 'M', 'Repudio e sou contra a negociação de cargos feita à vista pelo Governo, repudio e sou contra a negociação de cargos feita a prazo pelo Temer. Se há crime de responsabilidade para a Dilma, há crime, sim, de responsabilidade para o Temer. Cartão vermelho para Dilma! Cartão vermelho para Temer! Cartão vermelho para Cunha! Para pacificar o País, Constituinte exclusiva! Para pacificar o País, novas eleições e uma reforma política já! Meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (509, 'WEVERTON ROCHA MARQUES DE SOUSA', 'Weverton Rocha', 'PDT', 'MA', 'Não', 'M', 'Sr. Presidente, eu cheguei a esta Casa e estou no meu segundo mandato. Agradeço a brava militância do meu único partido, da militância da juventude socialista do PDT. Percorri meu caminho, lutei ao lado do Dr. Jackson e sei que, se ele estivesse aqui, assim como qualquer defensor da Constituição, não ousaria em dizer não a esta tentativa de golpe que está sendo dada aqui.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (510, 'WHERLES FERNANDES DA ROCHA', 'Rocha', 'PSDB', 'AC', 'Sim', 'M', 'Sr. Presidente, ao contrário do que muitos querem fazer crer, uma eleição não dá o direito de saquear o Brasil. Por um País melhor; por mais esperança para a nossa população; por um novo futuro para as novas gerações, eu voto em favor da minha família, do meu Estado, o Acre, e do Brasil. É sim, pelo impeachment! Fora, PT! Fora, roubalheira!.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (511, 'WLADIMIR AFONSO DA COSTA RABELO', 'Wladimir Costa', 'SD', 'PA', 'Sim', 'M', 'Presidente, um colega nosso aqui da Câmara , cujo nome não vou citar, disse que, se nós cassarmos a Presidente Dilma hoje, ele vai se mudar do Brasil. Eu já comprei a passagem dele, sem volta. Saia daqui, porque nós vamos cassar o Brasil, em nome do Pará . Minha mãe negra Lucimar, meu sul e sudeste do Pará, meu Tapajós amado, meu querido nordeste do Pará, toda a área metropolitana, nós encaminhamos, em nome do Brasil, minha mãezinha, dos meus filhos, dos meus amigos do Solidariedade, desse povo querido que vota sim, nós votamos sim! E quem vota sim coloca a mão para cima! Coloca a mão para cima!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (512, 'WOLNEY QUEIROZ MACIEL', 'Wolney Queiroz', 'PDT', 'PE', 'Não', 'M', 'Sr. Presidente, a política não é lugar para os fracos, e o povo sabe dar valor ao homem público que tem posição. Então, em nome da democracia, nãoa Eduardo Cunha, não ao golpe, não a um Presidente eleito sem votos. Não ao golpe! Não ao golpe! Não ao golpe!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (513, 'ZENAIDE MAIA CALADO PEREIRA DOS SANTOS', 'Zenaide Maia', 'PR', 'RN', 'Não', 'F', 'Sr. Presidente, pela democracia, pelos avanços sociais, pelas famílias brasileiras e por saber que o Deputado Eduardo Cunha e Michel Temer não são solução para o nosso País, eu voto não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (67, 'BENEVENUTO DACIOLO FONSECA DOS SANTOS', 'Cabo Daciolo', 'PT do B', 'RJ', 'Sim', 'M', 'Glória a Deus! Sr. Presidente, todos aqui ouviram eu falar Fora, Dilma!, Fora, Michel Temer!, Fora, Eduardo Cunha!, Fora, Rede Globo, mentirosa, que fica difamando pessoas. Vocês podem ser grandes aos olhos do homem, mas, para Deus, vocês são pequenininhos. Em nome do Senhor Jesus, eu profetizo a queda dos senhores a partir de hoje. E venho dizer aqui, pelos militares das Forças Armadas que estão sendo sucateados há anos, pelos militares da segurança pública que estão morrendo todos os dias, pelos militares que estão agora, inativos e pensionistas, sem salário, Fora, Pezão!, Fora, Dornelles!. Chega de corrupção! O meu voto é sim. Glória a Deus! Feliz a nação cujo Deus é o Senhor.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (318, 'LUIS HENRIQUE DE OLIVEIRA RESENDE', 'Luis Tibé', 'PT do B', 'MG', 'Sim', 'M', 'Meu voto não écontra um partido nem a favor de outro. Meu voto representa a vontade do povo da minha querida Belo Horizonte, das Minas Gerais e do Brasil. Meu voto é sim.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (473, 'SÍLVIO SERAFIM COSTA', 'Silvio Costa', 'PT do B', 'PE', 'Não', 'M', 'A maior herança que um homem pode deixar para os seus filhos, netos e bisnetos é a sua honra, a sua integridade, o seu caráter. Hoje, pela segunda vez, uma parcela da elite brasileira... Uma parcela da elite brasileira está torturando a Presidente Dilma. Hoje é um dia que não deveria existir. No futuro, sobre este dia, a história vai contar que a alegria é triste e que o não é a palavra mais bonita da língua portuguesa. Meu voto é não!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (24, 'ALICE MAZZUCO PORTUGAL', 'Alice Portugal', 'PC do B', 'BA', 'Não', 'F', 'O Brasil já tem a prova de que não há crime. Se não hácrime de responsabilidade, não há justificativa para impeachment e é golpe. O tribunal de exceção foi montado, como o inquisitor à frente, Sr. Eduardo Cunha! Na Bahia se diz: com tiranos não combinam brasileiros, corações. O Brasil não engolirá a sua tirania nem o cretinismo parlamentar. É não ao golpe! Não ao golpe.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (108, 'DANIEL GOMES DE ALMEIDA', 'Daniel Almeida', 'PC do B', 'BA', 'Não', 'M', 'Esse processo está se constituindo numa farsa e é ilegal, imoral, ilegítimo. O povo brasileiro está acompanhando essa farsa. Por isso, está na rua, reclamando, com a consciência democrática deste País, que não aceita golpe. Eu voto não ao golpe.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (114, 'DAVIDSON DE MAGALHÃES SANTOS', 'Davidson Magalhães', 'PC do B', 'BA', 'Não', 'M', 'Em defesa da democracia, que aqui, hoje, está vivendo uma farsa, dirigida por um réu de processo de corrupção no STF, em defesa do povo brasileiro, em nome da Bahia, em nome da minha região sul da Bahia, em nome da minha cidade Itabuna, em defesa da CEPLAC e do pré-sal, não ao golpe. Não passará!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (177, 'FRANCISCO LOPES DA SILVA', 'Chico Lopes', 'PC do B', 'CE', 'Não', 'M', 'Calma, gente! Eu estou emocionado. Eu pensei que vinha para uma reunião política, mas vim para o encontro de bons maridos e bons pais. Não fala ninguém que é desonesto, mas quando olho a cara, vejo bem uns cabras que já estiveram na Veja, estiveram na Lava-Jato, estiveram na imprensa, só não estiveram nas páginas sociais. Portanto, em nome de 54 milhões de brasileiros, eu voto "não" contra a ditadura desses cabras.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (217, 'JANDIRA FEGHALI', 'Jandira Feghali', 'PC do B', 'RJ', 'Não', 'F', 'Primeiro quero registrar a minha indignação, Deputado Eduardo Cunha, por ainda vê-lo sentado nessa cadeira sem reunir condições morais para isso. Segundo, registro a minha indignação de vê-lo abraçado com um traidor da democracia, que tenta usurpar a cadeira da Presidente, que é o Sr. Michel Temer, que não tem voto para tal. Terceiro, quero expressar aqui que ficou muito claro para a sociedade brasileira qual é a aliança pelo impeachmentque reúne corruptos, torturadores, como Jair Bolsonaro, e traidores da Pátria. Em respeito aos trabalhadores que usam o vermelho da luta e a bandeira do Brasil, trabalhadores do campo e da cidade que estão nas ruas com muito sacrifício, com convicção vou dizer: a luta apenas começou! Em respeito à democracia, à Constituição, não há crime. A Presidente Dilma é honesta. Não há crime! Portanto, é golpe! E eu voto não. Contra o golpe!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (311, 'LUCIANA BARBOSA DE OLIVEIRA SANTOS', 'Luciana Santos', 'PC do B', 'PE', 'Não', 'F', 'Há uma canção em Pernambuco que diz: (...) a injustiça dói. Nós somos madeira de lei que cupim não rói. Aqui, o PCdoB vota por aqueles que tombaram pela democracia no nosso País. O PCdoB vota pelos brasileiros e brasileiras que estão nas ruas indignados, no dia de hoje e nos dias que se passaram, porque, além de todas as injustiças desse processo, há um réu aqui comandando o procedimento de impedimento da Presidenta Dilma, uma mulher séria. É por isso que o PCdoB diz em alto e bom som: vai ter luta! Não ao golpe!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (349, 'MARCIVANIA DO SOCORRO DA ROCHA FLEXA', 'Professora Marcivania', 'PC do B', 'AP', 'Não', 'F', 'Eu acho que eu nunca vi tanta hipocrisia junta por metro quadrado. Dizer que vai votar contra a corrupção, colocando Michel Temer e Eduardo Cunha, como Presidente e Vice-Presidente, é de uma hipocrisia que não tem tamanho. E o povo sabe e vai enxergar isso. Então, pela democracia, contra o golpe de Estado e institucional que está sendo feito por esta Casa, eu voto, tranquilamente, com a minha consciência não.');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (365, 'MARIA DO SOCORRO JÔ MORAES', 'Jô Moraes', 'PC do B', 'MG', 'Não', 'F', 'Sr. Presidente, em homenagem àqueles que deram a vida para que a democracia florescesse neste País, para que os filhos das famílias brasileiras não vivam o que eu vivi na ditadura, eu digo não a esta vergonhosa fraude jurídica e farsa política de Michel Temer e Eduardo Cunha. Lembro aqui as palavras de Tancredo Neves: O primeiro compromisso de Minas é com a liberdade. A luta apenas começou! Eu digo não a esta fraude!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (401, 'ORLANDO SILVA DE JESUS JUNIOR', 'Orlando Silva', 'PC do B', 'SP', 'Não', 'M', 'Saudando os trabalhadores e trabalhadoras do Brasil que foram às ruas em defesa da democracia, como os que neste momento ocupam o Vale do Anhangabaú, em São Paulo; em defesa da Constituição Federal, que, quando tomei posse, jurei defender e fazer cumprir; em defesa da democracia, o meu voto e odo meu partido, o PCdoB — um partido que tem história, princípio, coerência, lado e coragem —, o voto do PCdoB é não. Não ao golpe!');
INSERT INTO votacao (id, nome_completo, nome, sigla_partido, sigla_uf, voto, sexo, discurso) VALUES (457, 'RUBENS PEREIRA E SILVA JUNIOR', 'Rubens Pereira Júnior', 'PC do B', 'MA', 'Não', 'M', 'Conseguiu, hein, Presidente? Governo mal avaliado deve ser julgado nas urnas. Aqui deve ser apreciado se pedalada e decreto são ou não crime de responsabilidade. Segundo a Constituição, não é. Defender a Constituição em momentos contra majoritários é para quem tem coragem. Por isso, voto não!');
